'use client';

import * as React from 'react';
import {useEffect, useState} from 'react';
import {CoreDetailsStep} from "@/app/manage/organization/[organization_id]/event/_components/CoreDetailsStep";
import {Progress} from "@/components/ui/progress";
import {CreateEventRequest} from "@/types/event";
import {toast} from "sonner";
import {FormProvider, useForm} from "react-hook-form";
import {Button} from "@/components/ui/button";
import {TiersStep} from "@/app/manage/organization/[organization_id]/event/_components/TierStep";
import {zodResolver} from "@hookform/resolvers/zod";
import {coreDetailsSchema, tiersSchema} from "@/lib/validators/event";
import {WizardSidebar} from "../_components/WizardSideBar";
import {useSidebar} from "@/components/ui/sidebar";

// --- Main Wizard Parent Component ---
export default function CreateEventPage() {
    const [step, setStep] = useState(1);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const totalSteps = 5;
    const {setOpen} = useSidebar();

    const steps = [
        {number: 1, title: "Core Details", description: "Name, category, and location"},
        {number: 2, title: "Tiers & Pricing", description: "Set your ticket prices"},
        {number: 3, title: "Scheduling", description: "Add dates and times"},
        {number: 4, title: "Seating", description: "Configure seating maps"},
        {number: 5, title: "Review & Publish", description: "Final check before submission"},
    ];

    // Collapse the main sidebar when component mounts
    useEffect(() => {
        setOpen(false);
    }, [setOpen]);

    // ✅ The single form instance for the entire wizard is created here.
    const methods = useForm<CreateEventRequest>({
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            organizationId: '', // Should be set from context/params
            categoryId: '',
            isOnline: false,
            tiers: [],
            sessions: [],
        },
        mode: 'onSubmit', // Only validate when explicitly triggered
    });

    // Update the validation schema based on the current step
    useEffect(() => {
        if (step === 1) {
            methods.clearErrors();
        } else if (step === 2) {
            methods.clearErrors();
        }
    }, [step, methods]);

    const validateCoreDetails = async () => {
        try {
            // Check for cover photos
            if (coverFiles.length === 0) {
                toast.error("Please upload at least one cover photo.");
                return false;
            }

            // Get form values for core details
            const values = methods.getValues();
            const coreData = {
                title: values.title,
                description: values.description || "",
                overview: values.overview || "",
                categoryId: values.categoryId || "",
                isOnline: values.isOnline || false,
                venueId: values.venueId,
                onlineLink: values.onlineLink,
                locationDescription: values.locationDescription,
            };

            // Validate with Zod schema
            const result = coreDetailsSchema.safeParse(coreData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.path.join('.')}: ${err.message}`);
                });

                // Also set form errors for field highlighting
                result.error.errors.forEach(err => {
                    const path = err.path.join('.') as keyof CreateEventRequest;
                    methods.setError(path, { 
                        type: 'manual', 
                        message: err.message 
                    });
                });

                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Validation error:", error);
            toast.error("Please check all required fields");
            return false;
        }
    };

    const validateTiers = async () => {
        try {
            // Get tiers data
            const values = methods.getValues();
            const tiersData = { tiers: values.tiers || [] };

            // Validate with Zod schema
            const result = tiersSchema.safeParse(tiersData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.message}`);
                });
                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Tier validation error:", error);
            toast.error("Please add at least one tier with valid information");
            return false;
        }
    };

    const onNext = async () => {
        let isValid = false;

        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        } else {
            // For future steps that don't have validation yet
            isValid = true;
        }

        if (isValid) {
            setStep(s => Math.min(totalSteps, s + 1));
        }
    };

    const onPrev = () => setStep(s => Math.max(1, s - 1));

    const onSubmit = async (data: CreateEventRequest) => {
        // Perform final validation before submission
        let isValid = true;

        // Validate the current step first
        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        }

        if (isValid) {
            console.log("Final Assembled Form Data:", data);
            console.log("Final Cover Files:", coverFiles);
            // Here you would call your createEvent API action
            toast.success("Event submitted for approval!");
        }
    };

    const renderStep = () => {
        switch (step) {
            case 1:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
            case 2:
                return <TiersStep/>;
            // Add cases for other steps here
            default:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
        }
    };

    return (
        <div className="flex min-h-screen bg-muted/40">
            <WizardSidebar currentStep={step} steps={steps}/>
            <main className="flex-1 p-4 md:p-8 overflow-y-auto ml-72">
                <div className="max-w-4xl mx-auto">
                    <div className="mb-8">
                        <p className="text-sm text-muted-foreground">Step {step} of {totalSteps}</p>
                        <Progress value={(step / totalSteps) * 100} className="mt-2"/>
                    </div>

                    {/* ✅ The FormProvider broadcasts the form state to all children */}
                    <FormProvider {...methods}>
                        <form onSubmit={methods.handleSubmit(onSubmit)}>
                            {renderStep()}

                            <div className="flex justify-between mt-8">
                                <Button type="button" variant="outline" onClick={onPrev} disabled={step === 1}>
                                    Previous
                                </Button>
                                {step < totalSteps ? (
                                    <Button type="button" onClick={onNext}>
                                        Next
                                    </Button>
                                ) : (
                                    <Button type="submit">
                                        Submit for Approval
                                    </Button>
                                )}
                            </div>
                        </form>
                    </FormProvider>
                </div>
            </main>
        </div>
    );
}

'use client';

import React, {createContext, useContext, useState, useEffect, ReactNode, useMemo} from 'react';
import {useAuth} from '@/providers/AuthProvider';
import {getAppConfiguration} from '@/lib/actions/configActions';
import {AppConfig, TierLimitDetails, TierName} from '@/types/config';

// Define the shape of the context
interface LimitContextType {
    config: AppConfig | null;
    isLoading: boolean;
    error: string | null;
    currentUserTier: TierName;
    currentUserTierLimits: TierLimitDetails | null;
    getLimitsForTier: (tier: TierName) => TierLimitDetails | null;
}

const LimitContext = createContext<LimitContextType | undefined>(undefined);

export const LimitProvider = ({children}: { children: ReactNode }) => {
    const {keycloak} = useAuth();
    const [config, setConfig] = useState<AppConfig | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Fetch the entire configuration on initial application load
    useEffect(() => {
        const fetchConfig = async () => {
            try {
                const appConfig = await getAppConfiguration();
                setConfig(appConfig);
            } catch (err) {
                setError('Failed to load application configuration. Some features may be unavailable.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };
        fetchConfig();
    }, []);

    // A memoized helper to get the current user's highest tier from their JWT
    const currentUserTier = useMemo((): TierName => {
        if (!keycloak?.tokenParsed?.user_groups || !Array.isArray(keycloak.tokenParsed.user_groups)) return 'FREE';

        const userGroups: string[] = keycloak.tokenParsed.user_groups;
        let highestTier: TierName = 'FREE';
        let maxLevel = 0;

        const tierLevels: Record<TierName, number> = {FREE: 0, PRO: 1, ENTERPRISE: 2};

        userGroups.forEach(group => {
            if (group.startsWith('/Tiers/')) {
                const tierName = group.substring('/Tiers/'.length).toUpperCase() as TierName;
                if (tierLevels[tierName] !== undefined && tierLevels[tierName] > maxLevel) {
                    maxLevel = tierLevels[tierName];
                    highestTier = tierName;
                }
            }
        });
        return highestTier;
    }, [keycloak]);

    // A memoized value for the current user's specific limits, derived from the full config
    const currentUserTierLimits = useMemo(() => {
        if (!config) return null;
        return config.tierLimits[currentUserTier] || config.tierLimits.FREE;
    }, [config, currentUserTier]);

    // A function to get limits for any specified tier (useful for upgrade pages)
    const getLimitsForTier = (tier: TierName): TierLimitDetails | null => {
        if (!config) return null;
        return config.tierLimits[tier] || null;
    };

    const value = {config, isLoading, error, currentUserTier, currentUserTierLimits, getLimitsForTier};

    return (
        <LimitContext.Provider value={value}>
            {children}
        </LimitContext.Provider>
    );
};

// The custom hook to easily access the limits from any component
export const useLimits = (): LimitContextType => {
    const context = useContext(LimitContext);
    if (context === undefined) {
        throw new Error('useLimits must be used within a LimitProvider');
    }
    return context;
};

'use client';

