import {SeatDTO, SeatingBlockDTO} from "@/types/event";
import {Button} from "@/components/ui/button";
import {Badge} from "@/components/ui/badge";
import {cn} from "@/lib/utils";
import {ReadModelSeatStatus} from "@/lib/validators/enums";
import {TooltipContent, TooltipTrigger, Tooltip} from "@/components/ui/tooltip";

import {SeatDTO, SeatingBlockDTO} from "@/types/event";
import {Button} from "@/components/ui/button";
import {Badge} from "@/components/ui/badge";
import {cn} from "@/lib/utils";
import {ReadModelSeatStatus} from "@/lib/validators/enums";
import {TooltipContent, TooltipTrigger, Tooltip} from "@/components/ui/tooltip";
import {Lock, X, Clock} from "lucide-react";

export const SeatedGridBlock = ({block, selectedSeats, onSeatSelect}: {
    block: SeatingBlockDTO;
    selectedSeats?: string[];
    onSeatSelect?: (seat: SeatDTO, blockName: string) => void;
}) => (
    <>
        <div className="text-sm font-medium mb-2 text-center text-foreground">{block.name}</div>
        <div
            className="grid gap-1.5"
            style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, minmax(0, 1fr))`}}
        >
            {block.rows?.map(row =>
                row.seats.map(seat => {
                    const seatStatus = seat.status || ReadModelSeatStatus.AVAILABLE;
                    const isDisabled = seatStatus !== ReadModelSeatStatus.AVAILABLE;
                    const isSelected = !!selectedSeats?.some(s => s === seat.id);

                    // Get status-specific styling
                    const getStatusStyles = () => {
                        switch(seatStatus) {
                            case ReadModelSeatStatus.AVAILABLE:
                                return "cursor-pointer hover:brightness-105";
                            case ReadModelSeatStatus.LOCKED:
                                return "bg-amber-200 text-amber-900 cursor-not-allowed border-amber-500 border";
                            case ReadModelSeatStatus.RESERVED:
                                return "bg-red-200 text-red-900 cursor-not-allowed border-red-500 border";
                            case ReadModelSeatStatus.BOOKED:
                                return "bg-gray-300 text-gray-700 cursor-not-allowed";
                            default:
                                return "opacity-30 cursor-not-allowed";
                        }
                    };

                    // Get status icon
                    const StatusIcon = () => {
                        switch(seatStatus) {
                            case ReadModelSeatStatus.LOCKED:
                                return <Lock className="absolute top-0 right-0 h-2.5 w-2.5 text-amber-600" />;
                            case ReadModelSeatStatus.RESERVED:
                                return <X className="absolute top-0 right-0 h-2.5 w-2.5 text-red-600" />;
                            case ReadModelSeatStatus.BOOKED:
                                return <Clock className="absolute top-0 right-0 h-2.5 w-2.5 text-gray-600" />;
                            default:
                                return null;
                        }
                    };

                    return (
                        <Tooltip key={seat.id}>
                            <TooltipTrigger asChild>
                                <div className="relative">
                                    <Button
                                        type="button"
                                        variant="ghost"
                                        className={cn(
                                            "h-6 w-6 p-0 rounded-full text-xs font-mono transition-transform duration-200 hover:scale-110",
                                            getStatusStyles(),
                                            isSelected && "ring-2 ring-offset-2 ring-offset-background ring-primary"
                                        )}
                                        style={{
                                            backgroundColor: seatStatus === ReadModelSeatStatus.AVAILABLE && seat.tier 
                                                ? `${seat.tier.color}80` 
                                                : undefined
                                        }}
                                        disabled={isDisabled || !onSeatSelect}
                                        onClick={() => onSeatSelect?.(seat, block.name)}
                                    >
                                        {seat.label}
                                    </Button>
                                    <StatusIcon />
                                </div>
                            </TooltipTrigger>
                            <TooltipContent side="top"
                                            className="p-2 shadow-md border-border bg-popover">
                                <div className="flex items-center gap-2">
                                    <Badge
                                        variant={
                                            seatStatus === ReadModelSeatStatus.LOCKED 
                                                ? 'warning' 
                                                : seatStatus === ReadModelSeatStatus.RESERVED || seatStatus === ReadModelSeatStatus.BOOKED
                                                    ? 'destructive' 
                                                    : 'outline'
                                        }
                                        className="text-xs px-1"
                                    >
                                        {seatStatus === ReadModelSeatStatus.LOCKED 
                                            ? 'PAYMENT PROCESSING'
                                            : seatStatus.replace('_', ' ')}
                                    </Badge>
                                    {seat.tier && (
                                        <>
                                            <div className="flex items-center gap-1">
                                                <div className="h-2 w-2 rounded-full"
                                                     style={{backgroundColor: seat.tier.color}}/>
                                                <span className="text-xs">{seat.tier.name}</span>
                                            </div>
                                            <span
                                                className="text-xs font-medium">{new Intl.NumberFormat('en-LK', {
                                                style: 'currency',
                                                currency: 'LKR'
                                            }).format(seat.tier.price)}</span>
                                        </>
                                    )}
                                </div>
                            </TooltipContent>
                        </Tooltip>
                    );
                })
            )}
        </div>
    </>
);

import * as React from 'react';
import {Tag} from 'lucide-react';
import {Avatar, AvatarFallback, AvatarImage} from "@/components/ui/avatar";
import {EventBasicInfoDTO} from "@/types/event";
import {Skeleton} from "@/components/ui/skeleton";
import EventCarousel from "@/components/EventCarousel";
import TiersSection from "@/app/(home-app)/events/[event_id]/_components/TiersSection";
import {Separator} from "@/components/ui/separator";
import {Card} from "@/components/ui/card";

// A new component for the sticky right-side panel
const TicketActionPanel: React.FC<{ tiers: EventBasicInfoDTO['tiers'] }> = ({tiers}) => {
    return (
        <div className="lg:sticky lg:top-24">
            <Card className="border rounded-2xl shadow-lg">
                <div className="p-6">
                    {tiers && tiers.length > 0 && <TiersSection tiers={tiers}/>}
                </div>
            </Card>
        </div>
    );
};


export const EventHero: React.FC<{ event: EventBasicInfoDTO }> = ({event}) => {
    const {title, description, overview, coverPhotos, organization, category, tiers} = event;

    return (
        // Changed from a single column to a responsive grid layout
        <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8 lg:gap-12 px-4">

            {/* Left Column: Event Details */}
            <div className="lg:col-span-2 space-y-6">
                {coverPhotos && coverPhotos.length > 0 && (
                    <EventCarousel coverPhotos={coverPhotos}/>
                )}

                <div className="flex items-center gap-4">
                    {organization && (
                        <Avatar className="h-14 w-14 border flex-shrink-0">
                            <AvatarImage src={organization.logoUrl || ''} alt={organization.name}/>
                            <AvatarFallback>{organization.name?.charAt(0).toUpperCase()}</AvatarFallback>
                        </Avatar>
                    )}
                    <div className="flex flex-col gap-1 min-w-0">
                        <h1 className="text-3xl md:text-4xl font-extrabold">{title}</h1>
                        <div className="flex flex-col sm:flex-row sm:items-center gap-1 sm:gap-3">
                            {organization && (
                                <div className="flex items-center gap-1 text-sm text-muted-foreground">
                                    <span>by</span>
                                    <span className="font-medium text-foreground">{organization.name}</span>
                                </div>
                            )}
                            {category && (
                                <div className="flex items-center gap-2 text-muted-foreground">
                                    <Tag className="w-4 h-4"/>
                                    <span>{category.name}</span>
                                    {category.parentName &&
                                        <span className="ml-1 text-xs">({category.parentName})</span>}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
                <div className="prose dark:prose-invert max-w-none text-gray-700 dark:text-gray-300">
                    {description && (
                        <div>
                            <h2 className="text-xl font-bold mb-2">About this event</h2>
                            <p>{description}</p>
                        </div>
                    )}
                    {overview && (
                        <div className="mt-4">
                            <h2 className="text-xl font-bold mb-2">Overview</h2>
                            <p>{overview}</p>
                        </div>
                    )}
                </div>
            </div>

            {/* Right Column: Ticket Panel */}
            <div className="lg:col-span-1">
                <TicketActionPanel tiers={tiers}/>
            </div>
        </div>
    );
};

