                                                                }) => {

    // Helper function to get image URL
    const getImageUrl = (file: File | string): string => {
        if (typeof file === 'string') {
            return file; // Assuming it's already a URL
        }
        return URL.createObjectURL(file); // Create a local URL for the file
    };

    return (
        <div className="space-y-6">
            {coverFiles.length > 0 && (
                <div className="w-full rounded-xl overflow-hidden">
                    <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                        <CarouselContent>
                            {coverFiles.map((file, index) => (
                                <CarouselItem key={index}>
                                    <div className="aspect-[21/9] w-full relative">
                                        <Image
                                            src={getImageUrl(file)}
                                            alt={`Cover photo ${index + 1}`}
                                            fill
                                            className="object-cover rounded-lg"
                                        />
                                    </div>
                                </CarouselItem>
                            ))}
                        </CarouselContent>
                        <CarouselPrevious className="absolute left-4"/>
                        <CarouselNext className="absolute right-4"/>
                    </Carousel>
                </div>
            )}

            <div className="space-y-4">
                <h1 className="text-4xl font-bold tracking-tight">{title}</h1>

                <div className="flex flex-wrap items-center gap-4 text-sm">

                    {organization?.name && (
                        <div className="flex items-center gap-1">
                            <Avatar className="h-6 w-6">
                                <AvatarImage src={organization.logoUrl} alt={organization.name}/>
                                <AvatarFallback> {organization.name.charAt(0).toUpperCase()} </AvatarFallback>
                            </Avatar>
                            <span>By {organization.name}</span>
                        </div>
                    )}

                    {categoryName && (
                        <div className="flex items-center gap-1">
                            <Tag className="h-4 w-4 text-muted-foreground"/>
                            <span>{categoryName}</span>
                        </div>
                    )}

                </div>
            </div>
        </div>
    );
};

'use client';

import * as React from 'react';
import Image from 'next/image';
import {Tag} from 'lucide-react';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Autoplay from 'embla-carousel-autoplay';
import {Avatar, AvatarFallback, AvatarImage} from "@/components/ui/avatar";
import {EventBasicInfoDTO} from "@/types/event";

function getImageUrl(url: string) {
    // If the URL is already absolute, use it directly
    if (url.startsWith('http')) return url;
    // Otherwise, treat as relative to public
    return `/${url}`;
}

export const ReviewEventHero: React.FC<{ event: EventBasicInfoDTO }> = ({ event }) => {
    const { title, overview, coverPhotos, organization, category } = event;

    return (
        <div className="space-y-6">
            {coverPhotos && coverPhotos.length > 0 && (
                <div className="w-full rounded-xl overflow-hidden">
                    <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                        <CarouselContent>
                            {coverPhotos.map((url, index) => (
                                <CarouselItem key={index}>
                                    <div className="aspect-[21/9] w-full relative">
                                        <Image
                                            src={getImageUrl(url)}
                                            alt={`Cover photo ${index + 1}`}
                                            fill
                                            className="object-cover rounded-lg"
                                        />
                                    </div>
                                </CarouselItem>
                            ))}
                        </CarouselContent>
                        <CarouselPrevious className="absolute left-4"/>
                        <CarouselNext className="absolute right-4"/>
                    </Carousel>
                </div>
            )}
            <div className="flex items-center gap-4">
                {organization && (
                    <Avatar className="h-12 w-12 border flex-shrink-0">
                        <AvatarImage src={organization.logoUrl || ''} alt={organization.name}/>
                        <AvatarFallback>{organization.name?.charAt(0).toUpperCase()}</AvatarFallback>
                    </Avatar>
                )}
                <div className="flex flex-col gap-1 min-w-0">
                    <h1 className="text-2xl md:text-3xl font-bold truncate">{title}</h1>
                    {category && (
                        <div className="flex items-center gap-2 text-muted-foreground text-sm">
                            <Tag className="w-4 h-4"/>
                            <span>{category.name}</span>
                            {category.parentName && <span className="ml-2 text-xs">({category.parentName})</span>}
                        </div>
                    )}
                </div>
            </div>
            {overview && (
                <div className="prose max-w-2xl text-muted-foreground">
                    {overview}
                </div>
            )}
        </div>
    );
};

import {SeatDTO, SessionSeatingMapDTO} from "@/types/event";
import {SeatingBlock, SeatingBlockPreview} from "@/app/(home-app)/events/[event_id]/_components/SeatingBlock";
