export const SeatingLayout = ({
                                  seatingMap,
                                  selectedSeats,
                                  onSeatSelect,
                              }: {
    seatingMap: SessionSeatingMapDTO;
    selectedSeats?: SelectedSeat[]; // Make optional for reuse
    onSeatSelect?: (seat: SeatDTO, blockName: string, rowLabel: string) => void; // Make optional for reuse
}) => {
    return (
        <div className="border rounded-lg p-4 bg-background/50">
            <h3 className="font-semibold mb-4 text-foreground">Seating Layout: {seatingMap.name}</h3>
            <div
                className="relative flex p-4 rounded-lg"
                style={{
                    backgroundImage: 'radial-gradient(circle at 1px 1px, hsl(var(--muted)) 1px, transparent 0)',
                    backgroundSize: '20px 20px',
                }}
            >
                {seatingMap.layout.blocks.map(block => (
                    <SeatingBlock
                        key={block.id}
                        block={block}
                        selectedSeats={selectedSeats}
                        onSeatSelect={onSeatSelect}
                    />
                ))}
            </div>
        </div>
    );
};

import {SeatDTO, SessionSeatingMapDTO} from "@/types/event";
import {SeatingBlock, SeatingBlockPreview} from "@/app/(home-app)/events/[event_id]/_components/SeatingBlock";
import {SelectedSeat} from "@/app/(home-app)/events/[event_id]/_components/SessionBookingPage";


export const SeatingLayoutPreview = ({seatingMap}: { seatingMap: SessionSeatingMapDTO }) => {
    return (
        <div className="border rounded-lg p-4 bg-card">
            <h3 className="font-semibold mb-4 text-foreground">Seating Layout: {seatingMap.name}</h3>
            <div
                className="relative p-4 rounded-lg overflow-auto"
                // Elegant dot-grid background
                style={{
                    backgroundImage: 'radial-gradient(circle at 1px 1px, hsl(var(--muted)) 1px, transparent 0)',
                    backgroundSize: '20px 20px',
                    height: 'auto',
                    minHeight: '400px'
                }}
            >
                {seatingMap.layout.blocks.map(block => (
                    <SeatingBlockPreview key={block.id} block={block}/>
                ))}
            </div>
        </div>
    );
};

export const SeatingLayout = ({
                                  seatingMap,
                                  selectedSeats,
                                  onSeatSelect,
                              }: {
    seatingMap: SessionSeatingMapDTO;
    selectedSeats?: SelectedSeat[]; // Make optional for reuse
    onSeatSelect?: (seat: SeatDTO, blockName: string, rowLabel: string) => void; // Make optional for reuse
}) => {
    // Find the furthest y coordinate plus the height of the block to determine the container height
    const calculateRequiredHeight = () => {
        let maxBottom = 0;
        seatingMap.layout.blocks.forEach(block => {
            const bottom = block.position.y + (block.height || 0);
            if (bottom > maxBottom) {
                maxBottom = bottom;
            }
        });
        // Add padding to ensure blocks aren't cut off
        return maxBottom + 50;
    };

    const containerHeight = calculateRequiredHeight();

    return (
        <div className="border rounded-lg p-4 bg-background/50">
            <h3 className="font-semibold mb-4 text-foreground">Seating Layout: {seatingMap.name}</h3>
            <div
                className="relative p-4 rounded-lg overflow-x-auto"
                style={{
                    backgroundImage: 'radial-gradient(circle at 1px 1px, hsl(var(--muted)) 1px, transparent 0)',
                    backgroundSize: '20px 20px',
                    height: `${containerHeight}px`,
                    overflowY: 'hidden'
                }}
            >
                {seatingMap.layout.blocks.map(block => (
                    <SeatingBlock
                        key={block.id}
                        block={block}
                        selectedSeats={selectedSeats}
                        onSeatSelect={onSeatSelect}
                    />
                ))}
            </div>
        </div>
    );
};

import {Badge} from "@/components/ui/badge";
import {SessionStatus} from "@/lib/validators/enums";

export const SessionStatusBadge: React.FC<{ status: SessionStatus }> = ({ status }) => {
    const statusBadge: { [key in SessionStatus]: string } = {
        [SessionStatus.ON_SALE]: 'success',
        [SessionStatus.SOLD_OUT]: 'destructive',
        [SessionStatus.CANCELED]: 'secondary',
        [SessionStatus.SCHEDULED]: 'default',
        [SessionStatus.CLOSED]: 'outline',
        [SessionStatus.PENDING]: 'warning',
    };

    return (
        <Badge variant={statusBadge[status] as "success" | "destructive" | "secondary" | "default" | "outline" | "warning"}>
            {status.replace('_', ' ')}
        </Badge>
    );
};

'use client';

import React, { useEffect, useState } from 'react';
import { SessionInfoBasicDTO } from "@/types/event";
import { getEventSessionsInRange } from '@/lib/actions/public/eventActions';
import { CalendarIcon } from "lucide-react";
import { SessionItem } from "@/app/(home-app)/events/[event_id]/_components/SessionItem";

const SessionsNoPagination = ({ eventId }: { eventId: string }) => {
    const [sessions, setSessions] = useState<SessionInfoBasicDTO[] | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchSessions = async () => {
            setIsLoading(true);

            // Get current date and a date one month from now
            const fromDate = new Date().toISOString();
            const toDate = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

            try {
                const data = await getEventSessionsInRange({ eventId, fromDate, toDate });
                setSessions(data);
            } catch (error) {
                console.error("Failed to fetch sessions", error);
            } finally {
                setIsLoading(false);
            }
        };

        fetchSessions();
    }, [eventId]);

    return (
        <div id="sessions-section" className="max-w-7xl mx-auto space-y-4 scroll-mt-16">
            <h2 className="text-2xl font-bold text-foreground flex items-center gap-2">
                <CalendarIcon className="w-6 h-6"/> Available Sessions
            </h2>
            {isLoading ? (
                <div>Loading sessions...</div>
            ) : sessions && sessions.length > 0 ? (
                <div className="space-y-2">
                    {sessions.map(session => (
                        <SessionItem key={session.id} session={session} />
                    ))}
                </div>
            ) : (
                <div>No upcoming sessions available.</div>
            )}
        </div>
    );
};

export default SessionsNoPagination;

import React, {Suspense} from 'react';
import {EventHero, ReviewEventHeroSkeleton} from "@/app/(home-app)/events/[event_id]/_components/EventHero";
import {Separator} from "@/components/ui/separator";
import {getEventSummery} from "@/lib/actions/public/server/eventActions";
import {EventTracker} from "@/app/(home-app)/events/[event_id]/_components/EventTracker";


export default async function Layout({params, children}: {
    params: Promise<{ event_id: string }>
    children: React.ReactNode;
}) {
    const {event_id} = await params;
    const eventSummery = await getEventSummery(event_id);

    return (
        <div>
            <div className="min-h-screen p-4 sm:p-6 md:p-8">
                <div className="max-w-7xl mx-auto space-y-8">
                    <EventTracker event={{
                        title: eventSummery.title,
                        id: eventSummery.id,
                        organization_id: eventSummery.organization?.id || '',
                    }}/>
                    <Suspense fallback={<ReviewEventHeroSkeleton/>}>
                        <EventHero event={eventSummery}/>
                    </Suspense>
                    <Separator/>
                    {children}
                </div>
            </div>
        </div>
    );
}

import React, {Suspense} from 'react';
import {EventHero, ReviewEventHeroSkeleton} from "@/app/(home-app)/events/[event_id]/_components/EventHero";
import {Separator} from "@/components/ui/separator";
import {getEventSummery, getEventViews} from "@/lib/actions/public/server/eventActions";
import {EventTracker} from "@/app/(home-app)/events/[event_id]/_components/EventTracker";


export default async function Layout({params, children}: {
    params: Promise<{ event_id: string }>
    children: React.ReactNode;
}) {
    const {event_id} = await params;
    const eventSummery = await getEventSummery(event_id);
    const viewsData = await getEventViews(event_id);

    return (
        <div>
            <div className="min-h-screen p-4 sm:p-6 md:p-8">
                <div className="max-w-7xl mx-auto space-y-8">
                    <EventTracker event={{
                        title: eventSummery.title,
                        id: eventSummery.id,
                        organization_id: eventSummery.organization?.id || '',
                    }}/>
                    <Suspense fallback={<ReviewEventHeroSkeleton/>}>
                        <EventHero event={eventSummery} viewCount={viewsData.success ? viewsData.viewCount : undefined}/>
                    </Suspense>
                    <Separator/>
                    {children}
                </div>
            </div>
        </div>
    );
}

import * as React from 'react';
import {format, parseISO} from 'date-fns';
import {Calendar, Clock, LinkIcon, MapPin, Tag} from 'lucide-react';
import {Accordion, AccordionContent, AccordionItem, AccordionTrigger,} from "@/components/ui/accordion";
import {Badge} from '@/components/ui/badge';
import {SessionFormData, Tier} from '@/lib/validators/event';
import {SessionType} from "@/lib/validators/enums";
import dynamic from "next/dynamic";

const SeatingInformation = dynamic(
    () => import("./SeatingInformation").then(mod => mod.SeatingInformation),
    {ssr: false}
);

interface ReviewSessionsProps {
    sessions: SessionFormData[];
    tiers: Tier[];
}

export const ReviewSessions: React.FC<ReviewSessionsProps> = ({sessions, tiers}) => {
    if (sessions.length === 0) return null;

    return (
        <div className="space-y-6">
            <h2 className="text-2xl font-semibold">Schedule</h2>
            <Accordion type="single" collapsible className="w-full">
                {sessions.map((session, index) => (
                    <SessionAccordionItem
                        key={`session-${index}`}
                        session={session}
                        index={index}
                        tiers={tiers}
                    />
                ))}
            </Accordion>
        </div>
    );
};

interface SessionAccordionItemProps {
    session: SessionFormData;
    tiers: Tier[];
    index: number;
}

const SessionAccordionItem: React.FC<SessionAccordionItemProps> = ({session, index, tiers}) => {
    const startDate = parseISO(session.startTime);
    const endDate = parseISO(session.endTime);
    const isOnline = session.sessionType === SessionType.ONLINE;
    const {layoutData} = session;

    return (
        <AccordionItem
            value={`item-${index}`}
            className="border rounded-lg mb-4 overflow-hidden"
        >
            <AccordionTrigger className="px-4 py-3 hover:no-underline hover:bg-muted/40">
                <div className="flex items-center gap-3 text-left">
                    <Calendar className="h-4 w-4 text-muted-foreground"/>
                    <div>
                        <div className="font-medium">
                            {format(startDate, "EEEE, MMMM d, yyyy")}
                        </div>
                        <div className="text-sm text-muted-foreground">
                            {format(startDate, "h:mm a")} - {format(endDate, "h:mm a")}
                        </div>
                    </div>
                    <Badge variant={isOnline ? "secondary" : "default"} className="ml-4">
                        {isOnline ? 'Online' : 'Physical'}
                    </Badge>
                            isOnline={isOnline}
                            session={session}
                            tiers={tiers}
                        />
                    )}
                </div>
            </AccordionContent>
        </AccordionItem>
    );
};

interface SessionDetailsProps {
    session: SessionFormData;
}

const SessionDetails: React.FC<SessionDetailsProps> = ({session}) => {
    const startDate = parseISO(session.startTime);
    const endDate = parseISO(session.endTime);
    const isOnline = session.sessionType === SessionType.ONLINE;
    const {venueDetails} = session;

    // Calculate event duration
    const getDuration = (): string => {
        try {
            const durationMs = endDate.getTime() - startDate.getTime();
            const hours = Math.floor(durationMs / (1000 * 60 * 60));
            const minutes = Math.floor((durationMs % (1000 * 60 * 60)) / (1000 * 60));

            return hours > 0
                ? `${hours} hour${hours !== 1 ? 's' : ''}${minutes > 0 ? ` ${minutes} min` : ''}`
                : `${minutes} minutes`;
        } catch (e) {
            console.error("Error calculating duration:", e);
            return "Duration unavailable";
        }
    };

    // Display sales start time
    const getSalesStartTimeDisplay = (): string => {
        if (!session.salesStartTime) {
            return "Sales start time not set";
        }

        try {
            const salesStartDate = parseISO(session.salesStartTime);
            return `Sales start on ${format(salesStartDate, 'MMM d, yyyy h:mm a')}`;
        } catch (e) {
            console.error("Error parsing sales start time:", e);
            return "Invalid sales start time";
        }
    };

    return (
        <div className="space-y-4">
            <div className="flex items-center gap-2">
                <Clock className="h-4 w-4 text-muted-foreground"/>
                <span>Duration: {getDuration()}</span>
            </div>

            <div className="flex items-start gap-2">
                {isOnline ? (
                    <>
                        <LinkIcon className="h-4 w-4 text-muted-foreground mt-0.5"/>
                        <div>
                            <div>Online Event</div>
                            {venueDetails?.onlineLink ? (
                                <div className="text-sm text-muted-foreground break-all">
                                    {venueDetails.onlineLink}
                                </div>
                            ) : (
                                <div className="text-sm text-destructive">
                                    Link not provided
                                </div>
                            )}
                        </div>
                    </>
                ) : (
                    <>
                        <MapPin className="h-4 w-4 text-muted-foreground mt-0.5"/>
                        <div className="w-full">
                            {venueDetails?.name ? (
                                <>
                                    <div>{venueDetails.name}</div>
                                    {venueDetails.address && (
                                        <div className="text-sm text-muted-foreground mb-2">
                                            {venueDetails.address}
                                        </div>
                                    )}
                                </>
                            ) : (
                                <div className="text-destructive">
                                    Venue not specified
                                </div>
                            )}
                        </div>
                    </>
                )}
            </div>

            <div className="flex items-center gap-2">
                <Tag className="h-4 w-4 text-muted-foreground"/>
                <span>{getSalesStartTimeDisplay()}</span>
            </div>
        </div>
    );
};

import * as React from 'react';
import {format, parseISO} from 'date-fns';
import {Calendar, Clock, LinkIcon, MapPin, Tag} from 'lucide-react';
import {Accordion, AccordionContent, AccordionItem, AccordionTrigger,} from "@/components/ui/accordion";
