option.value:
'use client';

import * as React from 'react';
import {useDraggable} from '@dnd-kit/core';
import {Block, Tier} from '@/lib/validators/event';
import {cn} from '@/lib/utils';
import {Button} from '@/components/ui/button';

// --- Interactive Draggable Block for Seated Grids ---
interface DraggableBlockProps {
    block: Block;
    tiers: Tier[];
    onSeatClick: (blockId: string, rowId: string, seatId: string) => void;
    onApplyToAllSeats?: (blockId: string) => void;  // New prop for handling apply to all
}

export function InteractiveDraggableBlock({block, tiers, onSeatClick, onApplyToAllSeats}: DraggableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true, // Dragging is disabled in assignment mode
    });

    const style = {
        // No transform needed as blocks are static in this editor
        left: block.position.x,
        top: block.position.y,
    };

    const getTierColor = (tierId?: string) => {
        if (tierId === 'RESERVED') return 'hsl(var(--muted-foreground))';
        if (!tierId) return undefined;
        const tier = tiers.find(t => t.id === tierId);
        return tier?.color || 'hsl(var(--primary))';
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute p-3 bg-card border rounded-lg">
            <div className="flex flex-col text-center relative">
                <div className="flex items-center justify-between w-full mb-2">
                    <span className="text-sm font-medium">{block.name}</span>
                    {onApplyToAllSeats && (
                        <Button
                            size="sm"
                            variant="outline"
                            className="text-xs py-0 h-6"
                            onClick={(e) => {
                                e.stopPropagation();
                                onApplyToAllSeats(block.id);
                            }}
                        >
                            Apply to All
                        </Button>
                    )}
                </div>
                <div className="grid gap-1.5"
                     style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, 1fr)`}}>
                    {block.rows?.map(row =>
                        row.seats.map(seat => (
                            <button
                                key={seat.id}
                                type="button"
                                onClick={() => onSeatClick(block.id, row.id, seat.id)}
                                className={cn(
                                    "h-6 w-6 rounded-full border text-xs font-mono transition-all hover:scale-110 flex items-center justify-center",
                                    seat.status === 'RESERVED' && "bg-muted-foreground text-primary-foreground line-through border-destructive border-2"
                                )}
                                style={{backgroundColor: seat.tierId && seat.status !== 'RESERVED' ? getTierColor(seat.tierId) : undefined}}
                            >
                                {seat.label}
                            </button>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
}

// --- Interactive Resizable Block for Standing Capacity ---
interface ResizableBlockProps {
    block: Block;
    tiers: Tier[];
    onClick: (blockId: string) => void;
}

export function InteractiveResizableBlock({block, tiers, onClick}: ResizableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true,
    });

    const style = {
        left: block.position.x,
        top: block.position.y,
        width: block.width,
        height: block.height,
    };

    // ✅ Determine the background color based on the tier of the seats inside the block.
    // For simplicity, we'll use the tier of the first seat as the representative color.
    const getBlockTierColor = () => {
        const firstTieredSeat = block.seats?.find(s => s.tierId);
        if (!firstTieredSeat || !firstTieredSeat.tierId) return undefined;
        const tier = tiers.find(t => t.id === firstTieredSeat.tierId);
        return tier?.color;
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute">
            <div
                onClick={() => onClick(block.id)}
                className="p-2 bg-card border rounded-lg shadow-md box-border flex flex-col relative cursor-pointer h-full w-full"
                style={{backgroundColor: getBlockTierColor()}}
            >
                <div className="flex items-center gap-2 flex-grow">
                    <div className="flex flex-col text-center flex-grow">
                        <span className="text-sm font-medium">{block.name}</span>
                        <span className="text-xs text-muted-foreground">{block.capacity} capacity</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useDraggable} from '@dnd-kit/core';
import {Block, Tier} from '@/lib/validators/event';
import {cn} from '@/lib/utils';
import {Button} from '@/components/ui/button';

// --- Interactive Draggable Block for Seated Grids ---
interface DraggableBlockProps {
    block: Block;
    tiers: Tier[];
    onSeatClick: (blockId: string, rowId: string, seatId: string) => void;
    onApplyToAllSeats?: (blockId: string) => void;  // New prop for handling apply to all
}

export function InteractiveDraggableBlock({block, tiers, onSeatClick, onApplyToAllSeats}: DraggableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true, // Dragging is disabled in assignment mode
    });

    const style = {
        // No transform needed as blocks are static in this editor
        left: block.position.x,
        top: block.position.y,
    };

    const getTierColor = (tierId?: string) => {
        if (tierId === 'RESERVED') return 'hsla(var(--muted-foreground), 0.5)'; // Added opacity
        if (!tierId) return undefined;
        const tier = tiers.find(t => t.id === tierId);
        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return 'hsla(var(--primary), 0.5)'; // Added opacity
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute p-3 bg-card border rounded-lg">
            <div className="flex flex-col text-center relative">
                <div className="flex items-center justify-between w-full mb-2">
                    <span className="text-sm font-medium">{block.name}</span>
                    {onApplyToAllSeats && (
                        <Button
                            size="sm"
                            variant="outline"
                            className="text-xs py-0 h-6"
                            onClick={(e) => {
                                e.stopPropagation();
                                onApplyToAllSeats(block.id);
                            }}
                        >
                            Apply to All
                        </Button>
                    )}
                </div>
                <div className="grid gap-1.5"
                     style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, 1fr)`}}>
                    {block.rows?.map(row =>
                        row.seats.map(seat => (
                            <button
                                key={seat.id}
                                type="button"
                                onClick={() => onSeatClick(block.id, row.id, seat.id)}
                                className={cn(
                                    "h-6 w-6 rounded-full border text-xs font-mono transition-all hover:scale-110 flex items-center justify-center",
                                    seat.status === 'RESERVED' && "bg-muted-foreground/50 text-primary-foreground line-through border-destructive border-2"
                                )}
                                style={{backgroundColor: seat.tierId && seat.status !== 'RESERVED' ? getTierColor(seat.tierId) : undefined}}
                            >
                                {seat.label}
                            </button>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
}

// --- Interactive Resizable Block for Standing Capacity ---
interface ResizableBlockProps {
    block: Block;
    tiers: Tier[];
    onClick: (blockId: string) => void;
}

export function InteractiveResizableBlock({block, tiers, onClick}: ResizableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true,
    });

    const style = {
        left: block.position.x,
        top: block.position.y,
        width: block.width,
        height: block.height,
    };

    // Determine the background color based on the tier of the seats inside the block
    // with 50% opacity
    const getBlockTierColor = () => {
        const firstTieredSeat = block.seats?.find(s => s.tierId);
        if (!firstTieredSeat || !firstTieredSeat.tierId) return undefined;
        const tier = tiers.find(t => t.id === firstTieredSeat.tierId);

        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return undefined;
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute">
            <div
                onClick={() => onClick(block.id)}
                className="p-2 bg-card border rounded-lg shadow-md box-border flex flex-col relative cursor-pointer h-full w-full"
                style={{backgroundColor: getBlockTierColor()}}
            >
                <div className="flex items-center gap-2 flex-grow">
                    <div className="flex flex-col text-center flex-grow">
                        <span className="text-sm font-medium">{block.name}</span>
                        <span className="text-xs text-muted-foreground">{block.capacity} capacity</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

// --- Physical Configuration View ---
import {CreateEventFormData, SessionSeatingMapRequest} from "@/lib/validators/event";
import {useFormContext} from "react-hook-form";
import * as React from "react";
import {useEffect, useState} from "react";
import {LayoutData, SeatingLayoutTemplateResponse} from "@/types/seatingLayout";
import {createSeatingLayoutTemplate, getSeatingLayoutTemplatesByOrg} from "@/lib/actions/seatingLayoutTemplateActions";
import {toast} from "sonner";
import {LayoutEditor} from "@/app/manage/organization/[organization_id]/seating/_components/LayoutEditor";
import {Button} from "@/components/ui/button";
import LayoutPreviewCard from "@/app/manage/organization/[organization_id]/seating/_components/LayoutPreviewCard";
import {TierAssignmentEditor} from "@/app/manage/organization/[organization_id]/event/_components/TierAssignmentEditor";

export function PhysicalConfigView({ onSave}: {
    onSave: (layout: SessionSeatingMapRequest) => void;
}) {
    const {watch} = useFormContext<CreateEventFormData>();
    const organizationId = watch('organizationId');
    console.log("Organization ID:", organizationId);
    const [templates, setTemplates] = useState<SeatingLayoutTemplateResponse[]>([]);
    const [mode, setMode] = useState<'select' | 'create' | 'assign'>('select');
    const [selectedLayout, setSelectedLayout] = useState<LayoutData | null>(null);

    useEffect(() => {
        if (organizationId) {
            getSeatingLayoutTemplatesByOrg(organizationId, 0, 100).then(res => setTemplates(res.content));
        }
    }, [organizationId]);

    const handleTierAssignmentSave = (layoutWithTiers: SessionSeatingMapRequest) => {
        onSave(layoutWithTiers);
    };

    const handleSave = async (layoutData: LayoutData) => {
        const request = {
            name: layoutData.name,
            organizationId,
            layoutData,
        };

        toast.promise(createSeatingLayoutTemplate(request), {
            loading: 'Saving new layout...',
            success: (data) => {
                // Redirect to the new edit page on success
                return `Layout "${data.name}" created successfully!`;
            },
            error: (err) => (err.message || 'Failed to create layout.') + 'Moving to assign mode.',
        });
    };


    if (mode === 'create') {
        return <LayoutEditor onSave={async (data) => {
            await handleSave(data);
            setSelectedLayout(data);
            setMode("assign");
        }}/>;
    }

    if (mode === 'assign' && selectedLayout) {
        return <TierAssignmentEditor initialLayout={selectedLayout} onSave={handleTierAssignmentSave}/>;
    }

    return (
        <div className="p-4">
            <h3 className="font-semibold mb-4">Select a Seating Layout</h3>
            <div className="grid grid-cols-5 gap-4 mb-4">
                {templates.map(template => (
                    <div key={template.id} onClick={() => {
                        setSelectedLayout(template.layoutData);
                        setMode('assign');
                    }}>
                        <LayoutPreviewCard layout={template} onDelete={() => {
                        }}/>
                    </div>
                ))}
            </div>
            <Button variant="outline" onClick={() => setMode('create')}>Create From Scratch</Button>
        </div>
    );
}

// --- Physical Configuration View ---
import {CreateEventFormData, SessionSeatingMapRequest, Block, Seat} from "@/lib/validators/event";
import {useFormContext} from "react-hook-form";
import * as React from "react";
import {useEffect, useState} from "react";
import {LayoutData, SeatingLayoutTemplateResponse} from "@/types/seatingLayout";
import {createSeatingLayoutTemplate, getSeatingLayoutTemplatesByOrg} from "@/lib/actions/seatingLayoutTemplateActions";
import {toast} from "sonner";
import {LayoutEditor} from "@/app/manage/organization/[organization_id]/seating/_components/LayoutEditor";
import {Button} from "@/components/ui/button";
import LayoutPreviewCard from "@/app/manage/organization/[organization_id]/seating/_components/LayoutPreviewCard";
import {TierAssignmentEditor} from "@/app/manage/organization/[organization_id]/event/_components/TierAssignmentEditor";

export function PhysicalConfigView({ onSave}: {
    onSave: (layout: SessionSeatingMapRequest) => void;
}) {
    const {watch} = useFormContext<CreateEventFormData>();
    const organizationId = watch('organizationId');
    console.log("Organization ID:", organizationId);
    const [templates, setTemplates] = useState<SeatingLayoutTemplateResponse[]>([]);
    const [mode, setMode] = useState<'select' | 'create' | 'assign'>('select');
    const [selectedLayout, setSelectedLayout] = useState<LayoutData | null>(null);

    useEffect(() => {
        if (organizationId) {
            getSeatingLayoutTemplatesByOrg(organizationId, 0, 100).then(res => setTemplates(res.content));
        }
    }, [organizationId]);

    const handleTierAssignmentSave = (layoutWithTiers: SessionSeatingMapRequest) => {
        // Check if all seats and standing blocks have tier assignments
        let hasUnassignedElements = false;

        for (const block of layoutWithTiers.layout.blocks) {
            // Check seated blocks (rows with seats)
            if (block.type === 'seated_grid' && block.rows) {
                for (const row of block.rows) {
                    for (const seat of row.seats) {
                        // Seat must either have a tierId or be marked as RESERVED
                        if (!seat.tierId && seat.status !== 'RESERVED') {
                            hasUnassignedElements = true;
                            break;
                        }
                    }
                    if (hasUnassignedElements) break;
                }
            }

            // Check standing capacity blocks
            if (block.type === 'standing_capacity' && block.seats) {
                // For standing blocks, check if at least one seat has a tier assignment
                const hasAnyAssignedSeat = block.seats.some(seat => seat.tierId);
                if (!hasAnyAssignedSeat && block.seats.length > 0) {
                    hasUnassignedElements = true;
                    break;
                }
            }

            if (hasUnassignedElements) break;
        }

        if (hasUnassignedElements) {
            toast.error("Please assign all seats to a tier or mark them as reserved. Standing areas must also have tier assignments.");
            return;
        }

        // All elements are properly assigned, proceed with save
        onSave(layoutWithTiers);
    };

    const handleSave = async (layoutData: LayoutData) => {
        const request = {
            name: layoutData.name,
            organizationId,
            layoutData,
        };

        toast.promise(createSeatingLayoutTemplate(request), {
            loading: 'Saving new layout...',
            success: (data) => {
                // Redirect to the new edit page on success
                return `Layout "${data.name}" created successfully!`;
            },
            error: (err) => (err.message || 'Failed to create layout.') + 'Moving to assign mode.',
        });
    };


    if (mode === 'create') {
        return <LayoutEditor onSave={async (data) => {
            await handleSave(data);
            setSelectedLayout(data);
            setMode("assign");
        }}/>;
    }

    if (mode === 'assign' && selectedLayout) {
        return <TierAssignmentEditor initialLayout={selectedLayout} onSave={handleTierAssignmentSave}/>;
    }

    return (
        <div className="p-4">
            <h3 className="font-semibold mb-4">Select a Seating Layout</h3>
            <div className="grid grid-cols-5 gap-4 mb-4">
                {templates.map(template => (
                    <div key={template.id} onClick={() => {
                        setSelectedLayout(template.layoutData);
                        setMode('assign');
                    }}>
                        <LayoutPreviewCard layout={template} onDelete={() => {
                        }}/>
                    </div>
                ))}
            </div>
            <Button variant="outline" onClick={() => setMode('create')}>Create From Scratch</Button>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData} from '@/lib/validators/event';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {
    SessionListItemSeating
} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({ onConfigModeChange }: SeatingStepProps) {
    const { control, formState: { errors }, watch, getValues, setValue } = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const { fields } = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: any) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-4">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div> {/* Empty div for flexbox spacing */}
                </div>

                <Card>
                    <CardHeader>
                        <CardTitle>
                            {currentSession.isOnline
                                ? "Online Capacity Configuration"
                                : "Physical Seating Configuration"}
                        </CardTitle>
                        <CardDescription>
                            {currentSession.isOnline
                                ? "Set the capacity and ticket tier for your online event."
                                : "Choose a layout template or create a new one, then assign your tiers."}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        {currentSession.isOnline ? (
                            <OnlineConfigView onSave={handleSave} />
                        ) : (
                            <PhysicalConfigView onSave={handleSave} />
                        )}
                    </CardContent>
                </Card>

                <div className="flex items-center justify-between border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <Card>
            <CardHeader>
                <CardTitle>Seating & Tier Assignment</CardTitle>
                <CardDescription>
                    Configure the seating layout and assign ticket tiers for each session.
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </CardContent>
        </Card>
    );
}

