                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <div className="space-y-6">
            <div>
                <h2 className="text-xl font-semibold mb-1">Seating & Tier Assignment</h2>
                <p className="text-sm text-muted-foreground">
                    Configure the seating layout and assign ticket tiers for each session.
                </p>
            </div>

            <div className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFormContext} from 'react-hook-form';
import {Block, CreateEventFormData} from '@/lib/validators/event';
import {SessionSeatingMapRequest, Row, Seat} from '@/lib/validators/event';

import {Button} from '@/components/ui/button';
import {TierPalette} from './TierPalette';
import {InteractiveDraggableBlock, InteractiveResizableBlock} from './InteractiveBlocks';
import {toast} from 'sonner';

import {getRowLabel} from "@/app/manage/organization/[organization_id]/seating/create/_lib/getRowLabel";
import {LayoutData} from "@/types/seatingLayout";

interface TierAssignmentEditorProps {
    initialLayout: LayoutData;
    onSave: (layout: SessionSeatingMapRequest) => void;
}

export function TierAssignmentEditor({initialLayout, onSave}: TierAssignmentEditorProps) {
    const {watch} = useFormContext<CreateEventFormData>();
    const tiers = watch('tiers');
    const [layoutData, setLayoutData] = useState<SessionSeatingMapRequest | null>(null);
    const [selectedTierId, setSelectedTierId] = useState<string | null>(tiers[0]?.id || null);

    // ✅ This effect performs the crucial transformation from a structural template to a full seating map.
    useEffect(() => {
        const transformedBlocks = initialLayout.layout.blocks.map((block) => {
            const newBlock: Block = {
                ...block,
                rows: [],
                seats: [],
            };

            if (block.type === 'seated_grid' && block.rows && block.columns) {
                const startRowIndex = block.startRowLabel ? block.startRowLabel.charCodeAt(0) - 'A'.charCodeAt(0) : 0;
                const startCol = block.startColumnLabel || 1;
                const numRows = block.rows;
                const numColumns = block.columns;

                newBlock.rows = Array.from({length: numRows}, (_, rowIndex) => {
                    const newRow: Row = {
                        id: `temp_row_${block.id}_${rowIndex}`,
                        label: `${getRowLabel(startRowIndex + rowIndex)}`,
                        seats: Array.from({length: numColumns}, (_, colIndex) => ({
                            id: `temp_seat_${block.id}_${rowIndex}_${colIndex}`,
                            label: `${startCol + colIndex}${getRowLabel(startRowIndex + rowIndex)}`,
                            status: 'AVAILABLE',
                        })),
                    };
                    return newRow;
                });
            } else if (block.type === 'standing_capacity' && block.capacity) {
                const capacity = block.capacity;
                newBlock.seats = Array.from({length: capacity}, (_, i) => ({
                    id: `temp_seat_${block.id}_${i}`,
                    label: `Slot ${i + 1}`,
                    status: 'AVAILABLE',
                }));
            }
            return newBlock;
        });

        setLayoutData({
            name: initialLayout.name,
            layout: {
                blocks: transformedBlocks,
            },
        });
    }, [initialLayout]);


    const handleSeatClick = (blockId: string, rowId: string, seatId: string) => {
        if (!layoutData) return;
        setLayoutData(prevLayout => {
            // Deep copy to avoid state mutation issues
            const newLayout = JSON.parse(JSON.stringify(prevLayout));
            const block = newLayout.layout.blocks.find((b: Block) => b.id === blockId);
            if (block?.rows) {
                const row = block.rows.find((r: Row) => r.id === rowId);
                if (row?.seats) {
                    const seat = row.seats.find((s: Seat) => s.id === seatId);
                    if (seat) {
                        if (selectedTierId === 'RESERVED') {
                            seat.status = seat.status === 'RESERVED' ? 'AVAILABLE' : 'RESERVED';
                            seat.tierId = undefined;
                        } else {
                            seat.tierId = seat.tierId === selectedTierId ? undefined : selectedTierId;
                            seat.status = 'AVAILABLE';
                        }
                    }
                }
            }
            return newLayout;
        });
    };

    const handleBlockClick = (blockId: string) => {
        if (!layoutData) return;
        setLayoutData(prevLayout => {
            const newLayout = JSON.parse(JSON.stringify(prevLayout));
            const block = newLayout.layout.blocks.find((b: Block) => b.id === blockId);
            if (block) {
                if (selectedTierId === 'RESERVED') {
                    toast.warning("You cannot reserve an entire standing block. To make it unavailable, please edit the layout and change its type to 'non-sellable'.");
                    return prevLayout;
                }
                if (block.seats) {
                    block.seats.forEach((seat: Seat) => {
                        seat.tierId = selectedTierId ?? undefined;
                    });
                }
            }
            return newLayout;
        });
    };

    if (!layoutData) {
        return <div>Loading layout...</div>; // Or a skeleton loader
    }

    return (
        <div className="flex h-[70vh] border rounded-lg">
            <main className="flex-1 relative bg-muted/20 overflow-auto">
                <div className="relative w-full h-full p-4">
                    {layoutData.layout.blocks.map(block => {
                        if (block.type === 'seated_grid') {
                            return <InteractiveDraggableBlock key={block.id} block={block} tiers={tiers}
                                                              onSeatClick={handleSeatClick}/>;
                        }
                        if (block.type === 'standing_capacity') {
                            return <InteractiveResizableBlock key={block.id} block={block} tiers={tiers}
                                                              onClick={handleBlockClick}/>;
                        }
                        return (
                            <div key={block.id} style={{
                                left: block.position.x,
                                top: block.position.y,
                                width: block.width,
                                height: block.height
                            }}
                                 className="absolute p-2 bg-muted border rounded-lg flex items-center justify-center">
                                <p className="text-sm text-muted-foreground">{block.name}</p>
                            </div>
                        );
                    })}
                </div>
            </main>
            <TierPalette tiers={tiers} selectedTierId={selectedTierId} onSelectTier={setSelectedTierId}/>
            <div className="absolute bottom-4 left-4 z-10">
                <Button onClick={() => onSave(layoutData)}>Confirm Tier Assignments</Button>
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFormContext} from 'react-hook-form';
import {Block, CreateEventFormData} from '@/lib/validators/event';
import {SessionSeatingMapRequest, Row, Seat} from '@/lib/validators/event';

import {Button} from '@/components/ui/button';
import {TierPalette} from './TierPalette';
import {InteractiveDraggableBlock, InteractiveResizableBlock} from './InteractiveBlocks';
import {toast} from 'sonner';

import {getRowLabel} from "@/app/manage/organization/[organization_id]/seating/create/_lib/getRowLabel";
import {LayoutData} from "@/types/seatingLayout";

interface TierAssignmentEditorProps {
    initialLayout: LayoutData;
    onSave: (layout: SessionSeatingMapRequest) => void;
}

export function TierAssignmentEditor({initialLayout, onSave}: TierAssignmentEditorProps) {
    const {watch} = useFormContext<CreateEventFormData>();
    const tiers = watch('tiers');
    const [layoutData, setLayoutData] = useState<SessionSeatingMapRequest | null>(null);
    const [selectedTierId, setSelectedTierId] = useState<string | null>(tiers[0]?.id || null);

    // ✅ This effect performs the crucial transformation from a structural template to a full seating map.
    useEffect(() => {
        const transformedBlocks = initialLayout.layout.blocks.map((block) => {
            const newBlock: Block = {
                ...block,
                rows: [],
                seats: [],
            };

            if (block.type === 'seated_grid' && block.rows && block.columns) {
                const startRowIndex = block.startRowLabel ? block.startRowLabel.charCodeAt(0) - 'A'.charCodeAt(0) : 0;
                const startCol = block.startColumnLabel || 1;
                const numRows = block.rows;
                const numColumns = block.columns;

                newBlock.rows = Array.from({length: numRows}, (_, rowIndex) => {
                    const newRow: Row = {
                        id: `temp_row_${block.id}_${rowIndex}`,
                        label: `${getRowLabel(startRowIndex + rowIndex)}`,
                        seats: Array.from({length: numColumns}, (_, colIndex) => ({
                            id: `temp_seat_${block.id}_${rowIndex}_${colIndex}`,
                            label: `${startCol + colIndex}${getRowLabel(startRowIndex + rowIndex)}`,
                            status: 'AVAILABLE',
                        })),
                    };
                    return newRow;
                });
            } else if (block.type === 'standing_capacity' && block.capacity) {
                const capacity = block.capacity;
                newBlock.seats = Array.from({length: capacity}, (_, i) => ({
                    id: `temp_seat_${block.id}_${i}`,
                    label: `Slot ${i + 1}`,
                    status: 'AVAILABLE',
                }));
            }
            return newBlock;
        });

        setLayoutData({
            name: initialLayout.name,
            layout: {
                blocks: transformedBlocks,
            },
        });
    }, [initialLayout]);


    const handleSeatClick = (blockId: string, rowId: string, seatId: string) => {
        if (!layoutData) return;
        setLayoutData(prevLayout => {
            // Deep copy to avoid state mutation issues
            const newLayout = JSON.parse(JSON.stringify(prevLayout));
            const block = newLayout.layout.blocks.find((b: Block) => b.id === blockId);
            if (block?.rows) {
                const row = block.rows.find((r: Row) => r.id === rowId);
                if (row?.seats) {
                    const seat = row.seats.find((s: Seat) => s.id === seatId);
                    if (seat) {
                        if (selectedTierId === 'RESERVED') {
                            seat.status = seat.status === 'RESERVED' ? 'AVAILABLE' : 'RESERVED';
                            seat.tierId = undefined;
                        } else {
                            seat.tierId = seat.tierId === selectedTierId ? undefined : selectedTierId;
                            seat.status = 'AVAILABLE';
                        }
                    }
                }
            }
            return newLayout;
        });
    };

    const handleBlockClick = (blockId: string) => {
        if (!layoutData) return;
        setLayoutData(prevLayout => {
            const newLayout = JSON.parse(JSON.stringify(prevLayout));
            const block = newLayout.layout.blocks.find((b: Block) => b.id === blockId);
            if (block) {
                if (selectedTierId === 'RESERVED') {
                    toast.warning("You cannot reserve an entire standing block. To make it unavailable, please edit the layout and change its type to 'non-sellable'.");
                    return prevLayout;
                }
                if (block.seats) {
                    block.seats.forEach((seat: Seat) => {
                        seat.tierId = selectedTierId ?? undefined;
                    });
                }
            }
            return newLayout;
        });
    };

    // New function to apply selected tier to all seats in a seated block
    const handleApplyToAllSeats = (blockId: string) => {
        if (!layoutData) return;

        setLayoutData(prevLayout => {
            const newLayout = JSON.parse(JSON.stringify(prevLayout));
            const block = newLayout.layout.blocks.find((b: Block) => b.id === blockId);

            if (block?.rows) {
                // Handle 'RESERVED' status specially
                if (selectedTierId === 'RESERVED') {
                    for (const row of block.rows) {
                        for (const seat of row.seats) {
                            seat.status = 'RESERVED';
                            seat.tierId = undefined;
                        }
                    }
                    toast.success(`All seats in ${block.name} have been reserved`);
                } else {
                    // Apply the selected tier to all seats
                    for (const row of block.rows) {
                        for (const seat of row.seats) {
                            if (seat.status !== 'RESERVED') {
                                seat.tierId = selectedTierId;
                                seat.status = 'AVAILABLE';
                            }
                        }
                    }
                    const tierName = tiers.find(t => t.id === selectedTierId)?.name;
                    toast.success(`Applied ${tierName || 'selected tier'} to all available seats in ${block.name}`);
                }
            }

            return newLayout;
        });
    };

    if (!layoutData) {
        return <div>Loading layout...</div>; // Or a skeleton loader
    }

    return (
        <div className="flex h-[70vh] border rounded-lg">
            <main className="flex-1 relative bg-muted/20 overflow-auto">
                <div className="relative w-full h-full p-4">
                    {layoutData.layout.blocks.map(block => {
                        if (block.type === 'seated_grid') {
                            return <InteractiveDraggableBlock 
                                key={block.id} 
                                block={block} 
                                tiers={tiers}
                                onSeatClick={handleSeatClick} 
                                onApplyToAllSeats={handleApplyToAllSeats} 
                            />;
                        }
                        if (block.type === 'standing_capacity') {
                            return <InteractiveResizableBlock key={block.id} block={block} tiers={tiers}
                                                              onClick={handleBlockClick}/>;
                        }
                        return (
                            <div key={block.id} style={{
                                left: block.position.x,
                                top: block.position.y,
                                width: block.width,
                                height: block.height
                            }}
                                 className="absolute p-2 bg-muted border rounded-lg flex items-center justify-center">
                                <p className="text-sm text-muted-foreground">{block.name}</p>
                            </div>
                        );
                    })}
                </div>
            </main>
            <TierPalette tiers={tiers} selectedTierId={selectedTierId} onSelectTier={setSelectedTierId}/>
            <div className="absolute bottom-4 left-4 z-10">
                <Button onClick={() => onSave(layoutData)}>Confirm Tier Assignments</Button>
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect, useRef} from 'react';
import {useForm, FormProvider} from 'react-hook-form';
import {zodResolver} from '@hookform/resolvers/zod';
import {useOrganization} from '@/providers/OrganizationProvider';
import {useLimits} from '@/providers/LimitProvider';
import {CategoryResponse} from '@/types/category';
import {getAllCategories} from '@/lib/actions/categoryActions';
import {coreDetailsSchema, CoreDetailsData} from '@/lib/validators/event';

import {Button} from '@/components/ui/button';
import {Input} from '@/components/ui/input';
import {Textarea} from '@/components/ui/textarea';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {FormControl, FormField, FormItem, FormLabel, FormMessage} from '@/components/ui/form';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
    SelectGroup,
    SelectLabel
} from '@/components/ui/select';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Image from 'next/image';
import {ImageIcon, X, PlusCircle, Upload} from 'lucide-react';
import {toast} from 'sonner';
import Autoplay from 'embla-carousel-autoplay';

// --- Step 1: Core Details Component ---
export function CoreDetailsStep({onNextAction}: { onNextAction: () => void }) {
    const {organization} = useOrganization();
    const {myLimits} = useLimits();
    const [categories, setCategories] = useState<CategoryResponse[]>([]);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const fileInputRef = useRef<HTMLInputElement>(null);

    const maxPhotos = myLimits?.eventLimits.maxCoverPhotos || 1;

    const form = useForm<CoreDetailsData>({
        resolver: zodResolver(coreDetailsSchema),
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            categoryId: '', // Ensure categoryId is present
            isOnline: false,
        },
    });

    useEffect(() => {
        getAllCategories().then(setCategories);
    }, [organization]);

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files) {
            const files = Array.from(e.target.files);
            if ((coverFiles.length + files.length) > maxPhotos) {
                toast.error(`You can only upload a maximum of ${maxPhotos} photos.`);
                return;
            }
            setCoverFiles(prev => [...prev, ...files]);
        }
    };

    const removeImage = (index: number) => {
        setCoverFiles(prev => prev.filter((_, i) => i !== index));
    };

    function onSubmit(data: CoreDetailsData) {
        console.log("Step 1 Data:", data);
        console.log("Cover Files:", coverFiles);
        onNextAction();
    }

    return (
        <FormProvider {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
                {/* Hero Cover Photos Section */}
                <Card>
                    <CardHeader>
                        <CardTitle>Cover Photos</CardTitle>
                        <CardDescription>This is the first thing people will see. Upload up
                            to {maxPhotos} images.</CardDescription>
                    </CardHeader>
                    <CardContent>
                        {coverFiles.length > 0 ? (
                            <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                                <CarouselContent>
                                    {coverFiles.map((file, index) => (
                                        <CarouselItem key={index} className="relative">
                                            <div className="aspect-[21/9] w-full relative">
                                                <Image src={URL.createObjectURL(file)} alt={`Cover photo ${index + 1}`}
                                                       fill className="object-cover rounded-lg"/>
                                                <Button type="button" variant="destructive" size="icon"
                                                        className="absolute top-4 right-4 h-8 w-8 z-10"
                                                        onClick={() => removeImage(index)}><X
                                                    className="h-4 w-4"/></Button>
                                            </div>
                                        </CarouselItem>
                                    ))}
                                </CarouselContent>
                                <CarouselPrevious type="button"
                                                  className="absolute left-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                                <CarouselNext type="button"
                                              className="absolute right-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                            </Carousel>
                        ) : (
                            <div
                                className="flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-12 text-center aspect-[21/9] w-full">
                                <ImageIcon className="h-16 w-16 text-muted-foreground"/>
                                <p className="mt-4 text-lg font-medium">Add cover photos for your event</p>
                                <Button type="button" className="mt-4"
                                        onClick={() => fileInputRef.current?.click()}><Upload className="mr-2 h-4 w-4"/>Upload
                                    Images</Button>
                            </div>
                        )}
                        <div className="mt-4 flex justify-center items-center gap-4">
                            {coverFiles.length > 0 && (<Button type="button" variant="outline"
                                                               onClick={() => fileInputRef.current?.click()}><PlusCircle
                                className="mr-2 h-4 w-4"/>Add More</Button>)}
                            <p className="text-sm text-muted-foreground">{coverFiles.length} of {maxPhotos} photos
                                selected.</p>
                        </div>
                        <Input id="picture" type="file" multiple accept="image/*" onChange={handleFileChange}
                               className="hidden" ref={fileInputRef}/>
                    </CardContent>
                </Card>

                {/* Event Details Card with Category dropdown */}
                <Card>
                    <CardHeader>
                        <CardTitle>Event Details</CardTitle>
                        <CardDescription>Provide the core information about your event.</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                        <FormField control={form.control} name="title" render={({field}) => (
                            <FormItem><FormLabel>Event Title</FormLabel><FormControl><Input
                                placeholder="e.g., Annual Tech Conference 2025" {...field} /></FormControl><FormMessage/></FormItem>)}/>

                        {/* Category Selector as dropdown */}
                        <FormField
                            control={form.control}
                            name="categoryId"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Category</FormLabel>
                                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select a category for your event"/>
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent className="max-h-100">
                                            {categories.map((parentCat) => (
                                                <SelectGroup key={parentCat.id}>
                                                    <SelectLabel>{parentCat.name}</SelectLabel>
                                                    {parentCat.subCategories.map((subCat) => (
                                                        <SelectItem key={subCat.id} value={subCat.id}>
                                                            {subCat.name}
                                                        </SelectItem>
                                                    ))}
                                                </SelectGroup>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <FormField control={form.control} name="description" render={({field}) => (
                            <FormItem><FormLabel>Short Description</FormLabel><FormControl><Textarea
                                placeholder="A brief summary of your event." {...field} /></FormControl><FormMessage/></FormItem>)}/>
                        <FormField control={form.control} name="overview" render={({field}) => (
                            <FormItem><FormLabel>Overview</FormLabel><FormControl><Textarea
                                placeholder="Provide more details like schedule, speakers, etc."
                                className="min-h-32" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    </CardContent>
                </Card>
                <div className="flex justify-end mt-8">
                    <Button type="submit">Next: Tiers & Pricing</Button>
                </div>
            </form>
        </FormProvider>
    )
}

