import {SeatDTO, SeatingBlockDTO} from "@/types/event";
import {Button} from "@/components/ui/button";
import {Badge} from "@/components/ui/badge";
import {cn} from "@/lib/utils";
import {ReadModelSeatStatus} from "@/lib/validators/enums";
import {SelectedSeat} from "./SessionBooking";
import {TooltipContent, TooltipTrigger, Tooltip} from "@/components/ui/tooltip";

export const SeatedGridBlock = ({block, selectedSeats, onSeatSelect}: {
    block: SeatingBlockDTO;
    selectedSeats?: SelectedSeat[];
    onSeatSelect?: (seat: SeatDTO, blockName: string) => void;
}) => (
    <>
        <div className="text-sm font-medium mb-2 text-center text-foreground">{block.name}</div>
        <div
            className="grid gap-1.5"
            style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, minmax(0, 1fr))`}}
        >
            {block.rows?.map(row =>
                row.seats.map(seat => {
                    const seatStatus = seat.status || ReadModelSeatStatus.AVAILABLE;
                    const isDisabled = seatStatus !== ReadModelSeatStatus.AVAILABLE;
                    const isSelected = !!selectedSeats?.some(s => s.id === seat.id);

                    return (
                        <Tooltip key={seat.id}>
                            <TooltipTrigger asChild>
                                <Button
                                    type="button"
                                    variant="ghost"
                                    className={cn(
                                        "h-6 w-6 p-0 rounded-full text-xs font-mono transition-transform duration-200 hover:scale-110",
                                        isDisabled ? "opacity-30 cursor-not-allowed" : "cursor-pointer hover:brightness-105",
                                        isSelected && "ring-2 ring-offset-2 ring-offset-background ring-primary"
                                    )}
                                    style={{backgroundColor: seat.tier ? `${seat.tier.color}80` : undefined}}
                                    disabled={isDisabled || !onSeatSelect}
                                    onClick={() => onSeatSelect?.(seat, block.name)}
                                >
                                    {seat.label}
                                </Button>
                            </TooltipTrigger>
                            <TooltipContent side="top"
                                            className="p-2 shadow-md border-border bg-popover">
                                <div className="flex items-center gap-2">
                                    <Badge
                                        variant={isDisabled ? 'destructive' : 'outline'}
                                        className="text-xs px-1"
                                    >
                                        {seatStatus.replace('_', ' ')}
                                    </Badge>
                                    {seat.tier && (
                                        <>
                                            <div className="flex items-center gap-1">
                                                <div className="h-2 w-2 rounded-full"
                                                     style={{backgroundColor: seat.tier.color}}/>
                                                <span className="text-xs">{seat.tier.name}</span>
                                            </div>
                                            <span
                                                className="text-xs font-medium">{new Intl.NumberFormat('en-LK', {
                                                style: 'currency',
                                                currency: 'LKR'
                                            }).format(seat.tier.price)}</span>
                                        </>
                                    )}
                                </div>
                            </TooltipContent>
                        </Tooltip>
                    );
                })
            )}
        </div>
    </>
);

import {SessionInfoBasicDTO} from '@/types/event';
import {Calendar, Clock, MapPin, ArrowLeft} from 'lucide-react';
import Link from 'next/link';
import {Button} from '@/components/ui/button';

export const SessionDetailsHeader = ({session, eventId}: { session: SessionInfoBasicDTO, eventId?: string }) => {

    const formatDate = (iso: string) => new Date(iso).toLocaleDateString("en-US", {
        weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
    });
    const formatTime = (iso: string) => new Date(iso).toLocaleTimeString("en-US", {
        hour: '2-digit', minute: '2-digit', hour12: true
    });

    return (
        <div>
            {eventId &&
                <div className="flex items-center mb-2">
                    <Link href={`/events/${eventId}`} passHref>
                        <Button variant="ghost" size="sm" className="gap-1 px-2 h-8">
                            <ArrowLeft className="h-4 w-4"/>
                            <span>All Sessions</span>
                        </Button>
                    </Link>
                </div>
            }
            <h1 className="text-2xl sm:text-3xl font-bold tracking-tight text-foreground">{'Session Details'}</h1>
            <div className="mt-2 flex flex-wrap items-center gap-x-6 gap-y-2 text-muted-foreground">
                <div className="flex items-center gap-2"><Calendar
                    className="h-4 w-4"/><span>{formatDate(session.startTime)}</span></div>
                <div className="flex items-center gap-2"><Clock
                    className="h-4 w-4"/><span>{formatTime(session.startTime)} - {formatTime(session.endTime)}</span>
                </div>
                {session.sessionType === 'PHYSICAL' &&
                    <div className="flex items-center gap-2"><MapPin
                        className="h-4 w-4"/><span>{session.venueDetails.name}</span></div>
                }
            </div>
        </div>
    );
};

import {SessionInfoBasicDTO} from '@/types/event';
import {Calendar, Clock, MapPin, ArrowLeft} from 'lucide-react';
import Link from 'next/link';
import {Button} from '@/components/ui/button';
import {SessionMap} from '@/app/(home-app)/events/[event_id]/_components/SessionMap';

export const SessionDetailsHeader = ({session, eventId}: { session: SessionInfoBasicDTO, eventId?: string }) => {

    const formatDate = (iso: string) => new Date(iso).toLocaleDateString("en-US", {
        weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
    });
    const formatTime = (iso: string) => new Date(iso).toLocaleTimeString("en-US", {
        hour: '2-digit', minute: '2-digit', hour12: true
    });

    return (
        <div>
            {eventId &&
                <div className="flex items-center mb-2">
                    <Link href={`/events/${eventId}`} passHref>
                        <Button variant="ghost" size="sm" className="gap-1 px-2 h-8">
                            <ArrowLeft className="h-4 w-4"/>
                            <span>All Sessions</span>
                        </Button>
                    </Link>
                </div>
            }
            <h1 className="text-2xl sm:text-3xl font-bold tracking-tight text-foreground">{'Session Details'}</h1>
            <div className="mt-2 flex flex-wrap items-center gap-x-6 gap-y-2 text-muted-foreground">
                <div className="flex items-center gap-2"><Calendar
                    className="h-4 w-4"/><span>{formatDate(session.startTime)}</span></div>
                <div className="flex items-center gap-2"><Clock
                    className="h-4 w-4"/><span>{formatTime(session.startTime)} - {formatTime(session.endTime)}</span>
                </div>
                {session.sessionType === 'PHYSICAL' &&
                    <div className="flex items-center gap-2"><MapPin
                        className="h-4 w-4"/><span>{session.venueDetails.name}</span></div>
                }
            </div>

            {/* Add map for physical events */}
            {session.sessionType === 'PHYSICAL' && session.venueDetails.location && (
                <div className="mt-4 h-64 rounded-md overflow-hidden border">
                    <SessionMap location={session.venueDetails.location} />
                </div>
            )}
        </div>
    );
};

import {SeatingBlockDTO, SeatDTO} from "@/types/event";
import {Button} from "@/components/ui/button";
import {PlusIcon} from "lucide-react";
import {SelectedSeat} from "./SessionBooking";
import {ReadModelSeatStatus} from "@/lib/validators/enums";

function getAvailabilityPercentage(block: SeatingBlockDTO) {
    if (block.type !== 'standing_capacity' || !block.capacity) return 100;
    const reservedCount = block.seats?.filter(seat =>
        seat.status === 'RESERVED' ||
        seat.status === 'BOOKED' ||
        seat.status === 'LOCKED'
    ).length || 0;
    return Math.max(0, Math.min(100, ((block.capacity - reservedCount) / block.capacity) * 100));
}

function getStandingAreaTierColor(block: SeatingBlockDTO) {
    if (block.type !== 'standing_capacity') return undefined;
    return block.seats?.[0]?.tier?.color;
}

export const StandingCapacityBlock = ({block, selectedSeats, onSeatSelect}: {
    block: SeatingBlockDTO;
    selectedSeats?: SelectedSeat[];
    onSeatSelect?: (seat: SeatDTO, blockName: string) => void;
}) => {
    const availabilityPercentage = getAvailabilityPercentage(block);
    const tierColor = getStandingAreaTierColor(block);
    const availableSeats = block.seats?.filter(seat =>
        (!seat.status || seat.status === ReadModelSeatStatus.AVAILABLE) &&
        !selectedSeats?.some(s => s.id === seat.id)
    ) || [];
    const isDisabled = availableSeats.length === 0 || !onSeatSelect;
    const handleQuickPick = () => {
        if (isDisabled) return;
        const randomIndex = Math.floor(Math.random() * availableSeats.length);
        const randomSeat = availableSeats[randomIndex];
        onSeatSelect?.(randomSeat, block.name);
    };

    return (
        <div className="relative flex flex-col items-center justify-between h-full w-full p-3">
            <div className="absolute top-0 right-0">
                <Button
                    type="button"
                    size="icon"
                    variant="default"
                    className="h-8 w-8 shadow-xl"
                    disabled={isDisabled}
                    onClick={handleQuickPick}
                >
                    <PlusIcon className="h-5 w-5" />
                </Button>
            </div>
            <div className="text-center mt-2">
                <p className="font-medium text-foreground">{block.name}</p>
                <p className="text-sm text-muted-foreground">Capacity: {block.capacity || 0}</p>
            </div>
            <div className="w-full my-3 text-center">
                <div className="w-full bg-muted rounded-full h-2.5">
                    <div
                        className="h-2.5 rounded-full transition-all"
                        style={{
                            width: `${availabilityPercentage}%`,
                            backgroundColor: tierColor || 'hsl(var(--primary))'
                        }}
                    />
                </div>
                <p className="text-xs mt-1 text-muted-foreground">
                    {availabilityPercentage > 0 ? `${availabilityPercentage.toFixed(0)}% Available` : 'Sold Out'}
                </p>
            </div>
        </div>
    );
};

