import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { Skeleton } from "@/components/ui/skeleton";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix default marker icons (Leaflet requires this in React/Next.js setups)
const defaultIcon = new L.Icon({
    iconUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    iconRetinaUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    shadowUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
});
L.Marker.prototype.options.icon = defaultIcon;

export const SessionMap = ({ location }: { location: { coordinates: [number, number] } }) => {
    // Handle SSR - don't render the map during server-side rendering
    const [isMounted, setIsMounted] = React.useState(false);

    React.useEffect(() => {
        setIsMounted(true);
    }, []);

    if (!isMounted) {
        return <Skeleton className="h-64 w-full rounded-md" />;
    }

    // Convert coordinates to Leaflet format [lat, lng]
    // GeoJSON uses [longitude, latitude], but Leaflet uses [latitude, longitude]
    const position: [number, number] = [location.coordinates[1], location.coordinates[0]];

    return (
        <div className="h-full w-full rounded-md overflow-hidden z-0">
            <MapContainer
                center={position}
                zoom={13}
                scrollWheelZoom={false}
                style={{ height: '100%', width: '100%' }}
            >
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <Marker position={position}>
                    <Popup>Event Location</Popup>
                </Marker>
            </MapContainer>
        </div>
    );
};

'use client'

import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { Skeleton } from "@/components/ui/skeleton";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useTheme } from "next-themes";

// Fix default marker icons (Leaflet requires this in React/Next.js setups)
const defaultIcon = new L.Icon({
    iconUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    iconRetinaUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    shadowUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
});
L.Marker.prototype.options.icon = defaultIcon;

export const SessionMap = ({ location }: { location: { coordinates: [number, number] } }) => {
    // Handle SSR - don't render the map during server-side rendering
    const [isMounted, setIsMounted] = React.useState(false);
    const { theme } = useTheme();
    const isDarkMode = theme === "dark";

    React.useEffect(() => {
        setIsMounted(true);
    }, []);

    if (!isMounted) {
        return <Skeleton className="h-64 w-full rounded-md" />;
    }

    // Convert coordinates to Leaflet format [lat, lng]
    // GeoJSON uses [longitude, latitude], but Leaflet uses [latitude, longitude]
    const position: [number, number] = [location.coordinates[1], location.coordinates[0]];

    // Dark mode tile layer from CartoDB
    const darkTileUrl = "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png";
    const darkAttribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>';

    // Light mode tile layer (default OpenStreetMap)
    const lightTileUrl = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";
    const lightAttribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors';

    return (
        <div className="h-full w-full rounded-md overflow-hidden z-0">
            <MapContainer
                center={position}
                zoom={13}
                scrollWheelZoom={false}
                style={{ height: '100%', width: '100%' }}
            >
                <TileLayer
                    attribution={isDarkMode ? darkAttribution : lightAttribution}
                    url={isDarkMode ? darkTileUrl : lightTileUrl}
                />
                <Marker position={position}>
                    <Popup>Event Location</Popup>
                </Marker>
            </MapContainer>
        </div>
    );
};

'use client';

import React, { useEffect, useState } from 'react';
import { SessionInfoBasicDTO } from "@/types/event";
import { getEventSessionsInRange } from '@/lib/actions/public/eventActions';
import { CalendarIcon } from "lucide-react";
import { SessionItem } from "@/app/(home-app)/events/[event_id]/_components/SessionItem";

const SessionsNoPagination = ({ eventId }: { eventId: string }) => {
    const [sessions, setSessions] = useState<SessionInfoBasicDTO[] | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchSessions = async () => {
            setIsLoading(true);

            // Get current date and a date one month from now
            const fromDate = new Date().toISOString();
            const toDate = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

            try {
                const data = await getEventSessionsInRange({ eventId, fromDate, toDate });
                setSessions(data);
            } catch (error) {
                console.error("Failed to fetch sessions", error);
            } finally {
                setIsLoading(false);
            }
        };

        fetchSessions();
    }, [eventId]);

    return (
        <div id="sessions-section" className="max-w-7xl mx-auto space-y-4 scroll-mt-16">
            <h2 className="text-2xl font-bold text-foreground flex items-center gap-2">
                <CalendarIcon className="w-6 h-6"/> Available Sessions
            </h2>
            {isLoading ? (
                <div>Loading sessions...</div>
            ) : sessions && sessions.length > 0 ? (
                <div className="space-y-2">
                    {sessions.map(session => (
                        <SessionItem key={session.id} session={session} />
                    ))}
                </div>
            ) : (
                <div>No upcoming sessions available.</div>
            )}
        </div>
    );
};

export default SessionsNoPagination;

