        </Card>
    );
};

import {useOrganization} from "@/providers/OrganizationProvider";
import {useRef} from "react";
import {toast} from "sonner";
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from "@/components/ui/card";
import {Avatar, AvatarFallback, AvatarImage} from "@/components/ui/avatar";
import {Building, Trash2, Upload} from "lucide-react";
import {Button} from "@/components/ui/button";
import {OrganizationResponse} from "@/types/oraganizations";
import {Input} from "@/components/ui/input";
import {compressImage} from "@/lib/imageUtils";

interface LogoManagementCardProps {
    organization: OrganizationResponse;
    onUpdate: () => void;
}

function LogoManagementCard({organization, onUpdate}: LogoManagementCardProps) {
    const {uploadLogo, removeLogo} = useOrganization();
    const fileInputRef = useRef<HTMLInputElement>(null);

    const handleUploadClick = () => {
        fileInputRef.current?.click();
    };

    const handleFileChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0];
        if (!file) return;

        // Basic file validation
        if (file.size > 5 * 1024 * 1024) { // 5MB limit
            toast.error('File size must be less than 5MB');
            return;
        }

        try {
            // Logo-specific compression options
            const logoCompressionOptions = {
                maxSizeMB: 0.3,           // Lower size limit for logos (300KB)
                maxWidthOrHeight: 512,    // Smaller dimensions suitable for logos
                useWebWorker: true,
                // Preserve PNG format for transparency if the uploaded file is PNG
                fileType: file.type === 'image/png' ? 'image/png' : 'image/jpeg'
            };

            // Compress the image with logo-specific options
            const compressedFile = await compressImage(file, logoCompressionOptions);

            toast.promise(uploadLogo(organization.id, compressedFile), {
                loading: 'Uploading logo...',
                success: ( ) => {
                    onUpdate();
                    return 'Logo uploaded successfully!';
                },
                error: (err) => err.message || 'Failed to upload logo.',
            });
        } finally {
            // Reset file input
            if (fileInputRef.current) {
                fileInputRef.current.value = '';
            }
        }
    };

    const handleRemoveLogo = async () => {
        toast.promise(removeLogo(organization.id), {
            loading: 'Removing logo...',
            success: () => {
                onUpdate();
                return 'Logo removed successfully.';
            },
            error: (err) => err.message || 'Failed to remove logo.',
        });
    };

    const getInitials = (name: string) => {
        return name
            .split(' ')
            .map(word => word.charAt(0))
            .join('')
            .toUpperCase()
            .slice(0, 2);
    };

    return (
        <Card className="w-full max-w-md">
            <CardHeader className="text-center pb-4">
                <CardTitle className="text-lg">Organization Logo</CardTitle>
                <CardDescription>
                    Upload a logo to represent your organization
                </CardDescription>
            </CardHeader>

            <CardContent className="flex flex-col items-center space-y-6">
                <div className="relative group">
                    <Avatar
                        className="h-24 w-24 ring-2 ring-border ring-offset-2 transition-all group-hover:ring-primary/50">
                        <AvatarImage
                            src={organization.logoUrl || undefined}
                            alt={`${organization.name} logo`}
                            className="object-cover"
                        />
                        <AvatarFallback className="bg-muted text-muted-foreground text-lg font-medium">
                            {organization.logoUrl ? (
                                <Building className="h-8 w-8"/>
                            ) : (
                                getInitials(organization.name)
                            )}
                        </AvatarFallback>
                    </Avatar>

                    {/* Optional: Upload overlay on hover */}
                    <div
                        className="absolute inset-0 flex items-center justify-center bg-black/50 rounded-full opacity-0 group-hover:opacity-100 transition-opacity cursor-pointer"
                        onClick={handleUploadClick}>
                        <Upload className="h-6 w-6 text-white"/>
                    </div>
                </div>

                <div className="flex flex-col justify-center sm:flex-row items-center gap-3 w-full">
                    <Input
                        type="file"
                        ref={fileInputRef}
                        onChange={handleFileChange}
                        className="hidden"
                        accept="image/png,image/jpeg,image/jpg,image/webp"
                    />

                    <Button
                        variant="outline"
                        onClick={handleUploadClick}
                        className="w-full sm:w-auto"
                    >
                        <Upload className="mr-2 h-4 w-4"/>
                        {organization.logoUrl ? 'Change Logo' : 'Upload Logo'}
                    </Button>

                    {organization.logoUrl && (
                        <Button
                            variant="ghost"
                            size="sm"
                            className="text-destructive hover:text-destructive hover:bg-destructive/10 w-full sm:w-auto"
                            onClick={handleRemoveLogo}
                        >
                            <Trash2 className="mr-2 h-4 w-4"/>
                            Remove
                        </Button>
                    )}
                </div>

                <p className="text-xs text-muted-foreground text-center">
                    Supports PNG, JPG, JPEG, WebP. Max file size: 5MB (will be compressed to ~1MB)
                </p>
            </CardContent>
        </Card>
    );
}

export {LogoManagementCard};

import {useOrganization} from "@/providers/OrganizationProvider";
import {useRef} from "react";
import {toast} from "sonner";
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from "@/components/ui/card";
import {Avatar, AvatarFallback, AvatarImage} from "@/components/ui/avatar";
import {Building, Trash2, Upload} from "lucide-react";
import {Button} from "@/components/ui/button";
import {OrganizationResponse} from "@/types/oraganizations";
import {Input} from "@/components/ui/input";
import {compressImage} from "@/lib/imageUtils";

interface LogoManagementCardProps {
    organization: OrganizationResponse;
    onUpdate: () => void;
}

function LogoManagementCard({organization, onUpdate}: LogoManagementCardProps) {
    const {uploadLogo, removeLogo} = useOrganization();
    const fileInputRef = useRef<HTMLInputElement>(null);

    const handleUploadClick = () => {
        fileInputRef.current?.click();
    };

    const handleFileChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0];
        if (!file) return;

        // Basic file validation
        if (file.size > 5 * 1024 * 1024) { // 5MB limit
            toast.error('File size must be less than 5MB');
            return;
        }

        try {
            // Logo-specific compression options
            const logoCompressionOptions = {
                maxSizeMB: 0.3,           // Lower size limit for logos (300KB)
                maxWidthOrHeight: 512,    // Smaller dimensions suitable for logos
                useWebWorker: true,
                // Preserve PNG format for transparency if the uploaded file is PNG
                fileType: file.type === 'image/png' ? 'image/png' : 'image/jpeg'
            };

            // Compress the image with logo-specific options
            const compressedFile = await compressImage(file, logoCompressionOptions);

            toast.promise(uploadLogo(organization.id, compressedFile), {
                loading: 'Uploading logo...',
                success: ( ) => {
                    onUpdate();
                    return 'Logo uploaded successfully!';
                },
                error: (err) => err.message || 'Failed to upload logo.',
            });
        } finally {
            // Reset file input
            if (fileInputRef.current) {
                fileInputRef.current.value = '';
            }
        }
    };

    const handleRemoveLogo = async () => {
        toast.promise(removeLogo(organization.id), {
            loading: 'Removing logo...',
            success: () => {
                onUpdate();
                return 'Logo removed successfully.';
            },
            error: (err) => err.message || 'Failed to remove logo.',
        });
    };

    const getInitials = (name: string) => {
        return name
            .split(' ')
            .map(word => word.charAt(0))
            .join('')
            .toUpperCase()
            .slice(0, 2);
    };

    return (
        <Card className="w-full max-w-md">
            <CardHeader className="text-center pb-4">
                <CardTitle className="text-lg">Organization Logo</CardTitle>
                <CardDescription>
                    Upload a logo to represent your organization
                </CardDescription>
            </CardHeader>

            <CardContent className="flex flex-col items-center space-y-6">
                <div className="relative group">
                    <Avatar
                        className="h-24 w-24 ring-2 ring-border ring-offset-2 transition-all group-hover:ring-primary/50">
                        <AvatarImage
                            src={organization.logoUrl || undefined}
                            alt={`${organization.name} logo`}
                            className="object-cover"
                        />
                        <AvatarFallback className="bg-muted text-muted-foreground text-lg font-medium">
                            {organization.logoUrl ? (
                                <Building className="h-8 w-8"/>
                            ) : (
                                getInitials(organization.name)
                            )}
                        </AvatarFallback>
                    </Avatar>

                    {/* Optional: Upload overlay on hover */}
                    <div
                        className="absolute inset-0 flex items-center justify-center bg-black/50 rounded-full opacity-0 group-hover:opacity-100 transition-opacity cursor-pointer"
                        onClick={handleUploadClick}>
                        <Upload className="h-6 w-6 text-white"/>
                    </div>
                </div>

                <div className="flex flex-col justify-center sm:flex-row items-center gap-3 w-full">
                    <Input
                        type="file"
                        ref={fileInputRef}
                        onChange={handleFileChange}
                        className="hidden"
                        accept="image/png,image/jpeg,image/jpg,image/webp"
                    />

                    <Button
                        variant="outline"
                        onClick={handleUploadClick}
                        className="w-full sm:w-auto"
                    >
                        <Upload className="mr-2 h-4 w-4"/>
                        {organization.logoUrl ? 'Change Logo' : 'Upload Logo'}
                    </Button>

                    {organization.logoUrl && (
                        <Button
                            variant="ghost"
                            size="sm"
                            className="text-destructive hover:text-destructive hover:bg-destructive/10 w-full sm:w-auto"
                            onClick={handleRemoveLogo}
                        >
                            <Trash2 className="mr-2 h-4 w-4"/>
                            Remove
                        </Button>
                    )}
                </div>

                <p className="text-xs text-muted-foreground text-center">
                    Supports PNG, JPG, JPEG, WebP. Max file size: 5MB (will be compressed to ~300KB)
                </p>
            </CardContent>
        </Card>
    );
}

export {LogoManagementCard};

'use client';

import * as React from 'react';
import {useState, useEffect, useRef} from 'react';
import {useFormContext} from 'react-hook-form'; // Use context instead of creating new form
import {useOrganization} from '@/providers/OrganizationProvider';
import {useLimits} from '@/providers/LimitProvider';
import {CategoryResponse} from '@/types/category';
import {getAllCategories} from '@/lib/actions/categoryActions';

import {Button} from '@/components/ui/button';
import {Input} from '@/components/ui/input';
import {Textarea} from '@/components/ui/textarea';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {FormControl, FormField, FormItem, FormLabel, FormMessage} from '@/components/ui/form';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
    SelectGroup,
    SelectLabel
} from '@/components/ui/select';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Image from 'next/image';
import {ImageIcon, X, PlusCircle, Upload} from 'lucide-react';
import {toast} from 'sonner';
import Autoplay from 'embla-carousel-autoplay';
import {CreateEventFormData} from "@/lib/validators/event";

interface CoreDetailsStepProps {
    coverFiles: File[];
    setCoverFilesAction: React.Dispatch<React.SetStateAction<File[]>>
}

export function CoreDetailsStep({coverFiles, setCoverFilesAction}: CoreDetailsStepProps) {
    const {organization} = useOrganization();
    const {myLimits} = useLimits();
    const [categories, setCategories] = useState<CategoryResponse[]>([]);
    const fileInputRef = useRef<HTMLInputElement>(null);

    //  Use the form context from parent instead of creating a new form
    const {control, setValue} = useFormContext<CreateEventFormData>();

    const maxPhotos = myLimits?.eventLimits.maxCoverPhotos || 1;

    useEffect(() => {
        getAllCategories().then(setCategories);
    }, [organization]);

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files) {
            const files = Array.from(e.target.files);
            if ((coverFiles.length + files.length) > maxPhotos) {
                toast.error(`You can only upload a maximum of ${maxPhotos} photos.`);
                return;
            }
            setCoverFilesAction(prev => [...prev, ...files]);
        }
    };

    const removeImage = (index: number) => {
        setCoverFilesAction(prev => prev.filter((_, i) => i !== index));
    };

    return (
        <div className="space-y-8">
            {/* Hero Cover Photos Section */}
            <Card>
                <CardHeader>
                    <CardTitle>Cover Photos</CardTitle>
                    <CardDescription>This is the first thing people will see. Upload up
                        to {maxPhotos} images.</CardDescription>
                </CardHeader>
                <CardContent>
                    {coverFiles.length > 0 ? (
                        <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                            <CarouselContent>
                                {coverFiles.map((file, index) => (
                                    <CarouselItem key={index} className="relative">
                                        <div className="aspect-[21/9] w-full relative">
                                            <Image src={URL.createObjectURL(file)} alt={`Cover photo ${index + 1}`}
                                                   fill className="object-cover rounded-lg"/>
                                            <Button type="button" variant="destructive" size="icon"
                                                    className="absolute top-4 right-4 h-8 w-8 z-10"
                                                    onClick={() => removeImage(index)}><X
                                                className="h-4 w-4"/></Button>
                                        </div>
                                    </CarouselItem>
                                ))}
                            </CarouselContent>
                            <CarouselPrevious type="button"
                                              className="absolute left-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                            <CarouselNext type="button"
                                          className="absolute right-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                        </Carousel>
                    ) : (
                        <div
                            className="flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-12 text-center aspect-[21/9] w-full">
                            <ImageIcon className="h-16 w-16 text-muted-foreground"/>
                            <p className="mt-4 text-lg font-medium">Add cover photos for your event</p>
                            <Button type="button" className="mt-4"
                                    onClick={() => fileInputRef.current?.click()}><Upload className="mr-2 h-4 w-4"/>Upload
                                Images</Button>
                        </div>
                    )}
                    <div className="mt-4 flex justify-center items-center gap-4">
                        {coverFiles.length > 0 && (<Button type="button" variant="outline"
                                                           onClick={() => fileInputRef.current?.click()}><PlusCircle
                            className="mr-2 h-4 w-4"/>Add More</Button>)}
                        <p className="text-sm text-muted-foreground">{coverFiles.length} of {maxPhotos} photos
                            selected.</p>
                    </div>
                    <Input id="picture" type="file" multiple accept="image/*" onChange={handleFileChange}
                           className="hidden" ref={fileInputRef}/>
                </CardContent>
            </Card>

            {/* Event Details Card with Category dropdown */}
            <Card>
                <CardHeader>
                    <CardTitle>Event Details</CardTitle>
                    <CardDescription>Provide the core information about your event.</CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                    <FormField control={control} name="title" render={({field}) => (
                        <FormItem><FormLabel>Event Title</FormLabel><FormControl><Input
                            placeholder="e.g., Annual Tech Conference 2025" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    <FormField control={control} name="categoryId" render={({field}) => (
                        <FormItem>
                            <FormLabel>Category</FormLabel>
                            <Select
                                onValueChange={(value) => {
                                    //  Find the full category object to get its name
                                    let selectedCategoryName: string | undefined;
                                    for (const parentCat of categories) {
                                        const subCat = parentCat.subCategories.find(sc => sc.id === value);
                                        if (subCat) {
                                            selectedCategoryName = subCat.name;
                                            break;
                                        }
                                    }
                                    //  Set both the ID and the name in the form state
                                    field.onChange(value);
                                    setValue('categoryName', selectedCategoryName);
                                }}
                                defaultValue={field.value}
                            >
                                <FormControl><SelectTrigger><SelectValue
                                    placeholder="Select a category for your event"/></SelectTrigger></FormControl>
                                <SelectContent>
                                    {categories.map((parentCat) => (
                                        <SelectGroup key={parentCat.id}>
                                            <SelectLabel>{parentCat.name}</SelectLabel>
                                            {parentCat.subCategories.map((subCat) => (
                                                <SelectItem key={subCat.id} value={subCat.id}>{subCat.name}</SelectItem>
                                            ))}
                                        </SelectGroup>
                                    ))}
                                </SelectContent>
                            </Select>
                            <FormMessage/>
                        </FormItem>
                    )}/>
                    <FormField control={control} name="description" render={({field}) => (
                        <FormItem><FormLabel>Short Description</FormLabel><FormControl><Textarea
                            placeholder="A brief summary of your event." {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    <FormField control={control} name="overview" render={({field}) => (
                        <FormItem><FormLabel>Overview</FormLabel><FormControl><Textarea
                            placeholder="Provide more details like schedule, speakers, etc."
                            className="min-h-32" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                </CardContent>
            </Card>
        </div>
    )
}

'use client';

import * as React from 'react';
import {useState, useEffect, useRef} from 'react';
import {useFormContext} from 'react-hook-form'; // Use context instead of creating new form
import {useOrganization} from '@/providers/OrganizationProvider';
import {useLimits} from '@/providers/LimitProvider';
import {CategoryResponse} from '@/types/category';
import {getAllCategories} from '@/lib/actions/categoryActions';
import {compressImage} from '@/lib/imageUtils';

import {Button} from '@/components/ui/button';
import {Input} from '@/components/ui/input';
import {Textarea} from '@/components/ui/textarea';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {FormControl, FormField, FormItem, FormLabel, FormMessage} from '@/components/ui/form';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
    SelectGroup,
    SelectLabel
} from '@/components/ui/select';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Image from 'next/image';
import {ImageIcon, X, PlusCircle, Upload} from 'lucide-react';
import {toast} from 'sonner';
import Autoplay from 'embla-carousel-autoplay';
import {CreateEventFormData} from "@/lib/validators/event";

interface CoreDetailsStepProps {
    coverFiles: File[];
    setCoverFilesAction: React.Dispatch<React.SetStateAction<File[]>>
}

export function CoreDetailsStep({coverFiles, setCoverFilesAction}: CoreDetailsStepProps) {
    const {organization} = useOrganization();
    const {myLimits} = useLimits();
    const [categories, setCategories] = useState<CategoryResponse[]>([]);
    const fileInputRef = useRef<HTMLInputElement>(null);

    //  Use the form context from parent instead of creating a new form
    const {control, setValue} = useFormContext<CreateEventFormData>();

    const maxPhotos = myLimits?.eventLimits.maxCoverPhotos || 1;

    useEffect(() => {
        getAllCategories().then(setCategories);
    }, [organization]);

    const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files) {
            const selectedFiles = Array.from(e.target.files);

            // Check if adding these files would exceed the maximum allowed
            if ((coverFiles.length + selectedFiles.length) > maxPhotos) {
                toast.error(`You can only upload a maximum of ${maxPhotos} photos.`);
                return;
            }

            // Define compression options for cover photos
            const coverPhotoCompressionOptions = {
                maxSizeMB: 1.5,            // Slightly larger than logos to maintain quality
                maxWidthOrHeight: 1920,    // HD resolution is good for cover photos
                useWebWorker: true,
                fileType: 'image/jpeg'     // JPEG works well for cover photos
            };

            // Show loading toast for long operations
            toast.loading('Processing images...');

            try {
                // Process files one by one with compression
                const processedFiles = await Promise.all(
                    selectedFiles.map(async (file) => {
                        return await compressImage(file, coverPhotoCompressionOptions);
                    })
                );

                // Add the compressed files to the state
                setCoverFilesAction(prev => [...prev, ...processedFiles]);
                toast.dismiss();
                toast.success(`${processedFiles.length} image(s) added successfully`);
            } catch (error) {
                console.error('Error processing images:', error);
                toast.dismiss();
                toast.error('Error processing images');
            }
        }
    };

    const removeImage = (index: number) => {
        setCoverFilesAction(prev => prev.filter((_, i) => i !== index));
    };

    return (
        <div className="space-y-8">
            {/* Hero Cover Photos Section */}
            <Card>
                <CardHeader>
                    <CardTitle>Cover Photos</CardTitle>
                    <CardDescription>This is the first thing people will see. Upload up
                        to {maxPhotos} images.</CardDescription>
                </CardHeader>
                <CardContent>
                    {coverFiles.length > 0 ? (
                        <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                            <CarouselContent>
                                {coverFiles.map((file, index) => (
                                    <CarouselItem key={index} className="relative">
                                        <div className="aspect-[21/9] w-full relative">
                                            <Image src={URL.createObjectURL(file)} alt={`Cover photo ${index + 1}`}
                                                   fill className="object-cover rounded-lg"/>
                                            <Button type="button" variant="destructive" size="icon"
                                                    className="absolute top-4 right-4 h-8 w-8 z-10"
                                                    onClick={() => removeImage(index)}><X
                                                className="h-4 w-4"/></Button>
                                        </div>
                                    </CarouselItem>
                                ))}
                            </CarouselContent>
                            <CarouselPrevious type="button"
                                              className="absolute left-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                            <CarouselNext type="button"
                                          className="absolute right-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                        </Carousel>
                    ) : (
                        <div
                            className="flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-12 text-center aspect-[21/9] w-full">
                            <ImageIcon className="h-16 w-16 text-muted-foreground"/>
                            <p className="mt-4 text-lg font-medium">Add cover photos for your event</p>
                            <Button type="button" className="mt-4"
                                    onClick={() => fileInputRef.current?.click()}><Upload className="mr-2 h-4 w-4"/>Upload
                                Images</Button>
                        </div>
                    )}
                    <div className="mt-4 flex justify-center items-center gap-4">
                        {coverFiles.length > 0 && (<Button type="button" variant="outline"
                                                           onClick={() => fileInputRef.current?.click()}><PlusCircle
                            className="mr-2 h-4 w-4"/>Add More</Button>)}
                        <p className="text-sm text-muted-foreground">{coverFiles.length} of {maxPhotos} photos
                            selected.</p>
                    </div>
                    <Input id="picture" type="file" multiple accept="image/*" onChange={handleFileChange}
                           className="hidden" ref={fileInputRef}/>
                </CardContent>
            </Card>

            {/* Event Details Card with Category dropdown */}
            <Card>
                <CardHeader>
                    <CardTitle>Event Details</CardTitle>
                    <CardDescription>Provide the core information about your event.</CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                    <FormField control={control} name="title" render={({field}) => (
                        <FormItem><FormLabel>Event Title</FormLabel><FormControl><Input
                            placeholder="e.g., Annual Tech Conference 2025" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    <FormField control={control} name="categoryId" render={({field}) => (
                        <FormItem>
                            <FormLabel>Category</FormLabel>
                            <Select
                                onValueChange={(value) => {
                                    //  Find the full category object to get its name
                                    let selectedCategoryName: string | undefined;
                                    for (const parentCat of categories) {
                                        const subCat = parentCat.subCategories.find(sc => sc.id === value);
                                        if (subCat) {
                                            selectedCategoryName = subCat.name;
                                            break;
                                        }
                                    }
                                    //  Set both the ID and the name in the form state
                                    field.onChange(value);
                                    setValue('categoryName', selectedCategoryName);
                                }}
                                defaultValue={field.value}
                            >
                                <FormControl><SelectTrigger><SelectValue
                                    placeholder="Select a category for your event"/></SelectTrigger></FormControl>
                                <SelectContent>
                                    {categories.map((parentCat) => (
                                        <SelectGroup key={parentCat.id}>
                                            <SelectLabel>{parentCat.name}</SelectLabel>
                                            {parentCat.subCategories.map((subCat) => (
                                                <SelectItem key={subCat.id} value={subCat.id}>{subCat.name}</SelectItem>
                                            ))}
                                        </SelectGroup>
                                    ))}
                                </SelectContent>
                            </Select>
                            <FormMessage/>
                        </FormItem>
                    )}/>
                    <FormField control={control} name="description" render={({field}) => (
                        <FormItem><FormLabel>Short Description</FormLabel><FormControl><Textarea
                            placeholder="A brief summary of your event." {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    <FormField control={control} name="overview" render={({field}) => (
                        <FormItem><FormLabel>Overview</FormLabel><FormControl><Textarea
                            placeholder="Provide more details like schedule, speakers, etc."
                            className="min-h-32" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                </CardContent>
            </Card>
        </div>
    )
}

'use client';

import * as React from 'react';
import {useDraggable} from '@dnd-kit/core';
import {Block, Tier} from '@/lib/validators/event';
import {cn} from '@/lib/utils';
import {Button} from '@/components/ui/button';

// --- Interactive Draggable Block for Seated Grids ---
interface DraggableBlockProps {
    block: Block;
    tiers: Tier[];
    onSeatClick: (blockId: string, rowId: string, seatId: string) => void;
    onApplyToAllSeats?: (blockId: string) => void;  // New prop for handling apply to all
}

export function InteractiveDraggableBlock({block, tiers, onSeatClick, onApplyToAllSeats}: DraggableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true, // Dragging is disabled in assignment mode
    });

    const style = {
        // No transform needed as blocks are static in this editor
        left: block.position.x,
        top: block.position.y,
    };

    const getTierColor = (tierId?: string) => {
        if (tierId === 'RESERVED') return 'hsla(var(--muted-foreground), 0.5)'; // Added opacity
        if (!tierId) return undefined;
        const tier = tiers.find(t => t.id === tierId);
        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return 'hsla(var(--primary), 0.5)'; // Added opacity
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute p-3 bg-card border rounded-lg">
            <div className="flex flex-col text-center relative">
                <div className="flex items-center justify-between w-full mb-2">
                    <span className="text-sm font-medium">{block.name}</span>
                    {onApplyToAllSeats && (
                        <Button
                            size="sm"
                            variant="outline"
                            type={'button'}
                            className="text-xs py-0 h-6"
                            onClick={(e) => {
                                e.stopPropagation();
                                onApplyToAllSeats(block.id);
                            }}
                        >
                            Apply to All
                        </Button>
                    )}
                </div>
                <div className="grid gap-1.5"
                     style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, 1fr)`}}>
                    {block.rows?.map(row =>
                        row.seats.map(seat => (
                            <button
                                key={seat.id}
                                type="button"
                                onClick={() => onSeatClick(block.id, row.id, seat.id)}
                                className={cn(
                                    "h-6 w-6 rounded-full border text-xs font-mono transition-all hover:scale-110 flex items-center justify-center",
                                    seat.status === 'RESERVED' && "bg-muted-foreground/50 text-primary-foreground line-through border-destructive border-2"
                                )}
                                style={{backgroundColor: seat.tierId && seat.status !== 'RESERVED' ? getTierColor(seat.tierId) : undefined}}
                            >
                                {seat.label}
                            </button>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
}

