export function InteractiveResizableBlock({block, tiers, onClick}: ResizableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true,
    });

    const style = {
        width: block.width,
        width: block.width,
        width: block.width,
        height: block.height,

'use client';

import * as React from 'react';
import {useDraggable} from '@dnd-kit/core';
import {Block, Tier} from '@/lib/validators/event';
import {cn} from '@/lib/utils';
import {Button} from '@/components/ui/button';

// --- Interactive Draggable Block for Seated Grids ---
interface DraggableBlockProps {
    block: Block;
    tiers: Tier[];
    onSeatClick: (blockId: string, rowId: string, seatId: string) => void;
    onApplyToAllSeats?: (blockId: string) => void;  // New prop for handling apply to all
}

export function InteractiveDraggableBlock({block, tiers, onSeatClick, onApplyToAllSeats}: DraggableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true, // Dragging is disabled in assignment mode
    });

    const style = {
        // No transform needed as blocks are static in this editor
        left: block.position.x,
        top: block.position.y,
    };

    const getTierColor = (tierId?: string) => {
        if (tierId === 'RESERVED') return 'hsla(var(--muted-foreground), 0.5)'; // Added opacity
        if (!tierId) return undefined;
        const tier = tiers.find(t => t.id === tierId);
        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return 'hsla(var(--primary), 0.5)'; // Added opacity
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute p-3 bg-card border rounded-lg">
            <div className="flex flex-col text-center relative">
                <div className="flex items-center justify-between w-full mb-2">
                    <span className="text-sm font-medium">{block.name}</span>
                    {onApplyToAllSeats && (
                        <Button
                            size="sm"
                            variant="outline"
                            type={'button'}
                            className="text-xs py-0 h-6"
                            onClick={(e) => {
                                e.stopPropagation();
                                onApplyToAllSeats(block.id);
                            }}
                        >
                            Apply to All
                        </Button>
                    )}
                </div>
                <div className="grid gap-1.5"
                     style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, 1fr)`}}>
                    {block.rows?.map(row =>
                        row.seats.map(seat => (
                            <button
                                key={seat.id}
                                type="button"
                                onClick={() => onSeatClick(block.id, row.id, seat.id)}
                                className={cn(
                                    "h-6 w-6 rounded-full border text-xs font-mono transition-all hover:scale-110 flex items-center justify-center",
                                    seat.status === 'RESERVED' && "bg-muted-foreground/50 text-primary-foreground line-through border-destructive border-2"
                                )}
                                style={{backgroundColor: seat.tierId && seat.status !== 'RESERVED' ? getTierColor(seat.tierId) : undefined}}
                            >
                                {seat.label}
                            </button>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
}

// --- Interactive Resizable Block for Standing Capacity ---
interface ResizableBlockProps {
    block: Block;
    tiers: Tier[];
    onClick: (blockId: string) => void;
}

export function InteractiveResizableBlock({block, tiers, onClick}: ResizableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true,
    });

    // Fix the TypeScript error by ensuring width and height are valid CSS values
    const style: React.CSSProperties = {
        left: block.position.x,
        top: block.position.y,
        width: block.width ?? undefined,
        height: block.height ?? undefined,
    };

    // Determine the background color based on the tier of the seats inside the block
    // with 50% opacity
    const getBlockTierColor = () => {
        const firstTieredSeat = block.seats?.find(s => s.tierId);
        if (!firstTieredSeat || !firstTieredSeat.tierId) return undefined;
        const tier = tiers.find(t => t.id === firstTieredSeat.tierId);

        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return undefined;
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute">
            <div
                onClick={() => onClick(block.id)}
                className="p-2 bg-card border rounded-lg shadow-md box-border flex flex-col relative cursor-pointer h-full w-full"
                style={{backgroundColor: getBlockTierColor()}}
            >
                <div className="flex items-center gap-2 flex-grow">
                    <div className="flex flex-col text-center flex-grow">
                        <span className="text-sm font-medium">{block.name}</span>
                        <span className="text-xs text-muted-foreground">{block.capacity} capacity</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

import {Controller, useForm} from "react-hook-form";
import {addDays, addWeeks, format, setHours, setMinutes, subDays, subHours} from "date-fns";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle
} from "@/components/ui/dialog";
import {FormControl, FormItem, FormLabel} from "@/components/ui/form";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Input} from "@/components/ui/input";
import {Popover, PopoverContent, PopoverTrigger} from "@/components/ui/popover";
import {Button} from "@/components/ui/button";
import {Calendar as CalendarIcon} from "lucide-react";
import {Calendar} from "@/components/ui/calendar";
import * as React from "react";
import {SessionFormData} from "@/lib/validators/event";
import {Switch} from "@/components/ui/switch";
import {toast} from "sonner";
import {Enums, SessionType} from "@/lib/validators/enums";

interface RecurringSessionFormValues {
    frequency: 'daily' | 'weekly';
    interval: number;
    count: number;
    startDate: Date;
    startTime: string;
    durationHours: number;
    salesStartRuleType: Enums;
    salesStartHoursBefore: number;
    salesStartFixedDatetime: Date;
    salesStartFixedTime: string; // Added field for fixed time
    isDaysNotHours: boolean;
}

export function RecurringSessionsDialog({open, setOpen, onGenerate, currentSessionCount, maxSessions}: {
    open: boolean,
    setOpen: (open: boolean) => void,
    onGenerate: (sessions: SessionFormData[]) => void,
    currentSessionCount: number,
    maxSessions: number
}) {
    const {control, handleSubmit, watch, setValue} = useForm<RecurringSessionFormValues>({
        defaultValues: {
            frequency: 'weekly',
            interval: 1,
            count: 5,
            startDate: new Date(),
            startTime: '19:00',
            durationHours: 2,
            salesStartRuleType: Enums.ROLLING,
            salesStartHoursBefore: 168,
            salesStartFixedDatetime: new Date(),
            salesStartFixedTime: '12:00', // Default fixed time
            isDaysNotHours: true,
        }
    });

    const salesStartHoursBefore = watch('salesStartHoursBefore');
    const isDaysNotHours = watch('isDaysNotHours');

    // Handle toggling between days and hours
    const handleUnitToggle = (checked: boolean) => {
        setValue('isDaysNotHours', checked);
        if (checked) {
            // Converting hours to days (rounded)
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24) * 24);
        } else {
            // Converting days to hours
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24));
        }
    };

    const onSubmit = (data: RecurringSessionFormValues) => {
        // Check if adding these sessions would exceed the limit
        if (currentSessionCount + data.count > maxSessions) {
            toast.error(`Cannot add ${data.count} sessions. You are limited to ${maxSessions} total sessions (${currentSessionCount} already added).`);
            return;
        }

        const newSessions: SessionFormData[] = [];
        let currentStartTime = setMinutes(
            setHours(
                new Date(data.startDate), // Ensure we have a fresh Date object
                parseInt(data.startTime.split(':')[0])
            ),
            parseInt(data.startTime.split(':')[1])
        );

        // For fixed sales start, calculate the datetime once
        let fixedSalesStartTime: Date | undefined;

        if (data.salesStartRuleType === Enums.FIXED) {
            const [hours, minutes] = data.salesStartFixedTime.split(':').map(num => parseInt(num));
            fixedSalesStartTime = setMinutes(
                setHours(
                    new Date(data.salesStartFixedDatetime),
                    hours
                ),
                minutes
            );

            // Validate that sales start time is before the first event start time
            if (fixedSalesStartTime >= currentStartTime) {
                toast.error("Sales start time must be before the first event start time");
                return;
            }
        }

        for (let i = 0; i < data.count; i++) {
            const endTime = new Date(currentStartTime.getTime() + data.durationHours * 60 * 60 * 1000);

            // Calculate sales start time based on the rule type default to seven days before start
            let salesStartTime = new Date(subDays(currentStartTime, 7)).toISOString();

            if (data.salesStartRuleType === Enums.IMMEDIATE) {
                // For immediate sales, use current time
                salesStartTime = new Date().toISOString();
            } else if (data.salesStartRuleType === Enums.ROLLING) {
                // For rolling sales, subtract the specified hours from each event start time
                salesStartTime = subHours(currentStartTime, data.salesStartHoursBefore).toISOString();
            } else if (data.salesStartRuleType === Enums.FIXED && fixedSalesStartTime) {
                // For fixed date, use the pre-calculated time (same for all sessions)
                salesStartTime = fixedSalesStartTime.toISOString();
            }

            newSessions.push({
                startTime: currentStartTime.toISOString(),
                sessionType: SessionType.PHYSICAL,
                endTime: endTime.toISOString(),
                salesStartTime: salesStartTime,
                layoutData: {name: null, layout: {blocks: []}},
            });

            if (data.frequency === 'daily') {
                currentStartTime = addDays(currentStartTime, data.interval);
            } else if (data.frequency === 'weekly') {
                currentStartTime = addWeeks(currentStartTime, data.interval);
            }
        }

        console.log("Generated sessions:", newSessions);
        onGenerate(newSessions);
        setOpen(false);
    };

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>Add Recurring Sessions</DialogTitle>
                    <DialogDescription>
                        Generate a series of sessions based on a recurring pattern.
                        {maxSessions > 0 && (
                            <span className="block mt-1 text-sm text-muted-foreground">
                                Limit: {currentSessionCount}/{maxSessions} sessions used.
                            </span>
                        )}
                    </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="frequency" control={control} render={({field}) => (
                            <FormItem><FormLabel>Frequency</FormLabel><Select onValueChange={field.onChange}
                                                                              defaultValue={field.value}><FormControl><SelectTrigger><SelectValue/></SelectTrigger></FormControl><SelectContent><SelectItem
                                value="daily">Daily</SelectItem><SelectItem
                                value="weekly">Weekly</SelectItem></SelectContent></Select></FormItem>
                        )}/>
                        <Controller name="interval" control={control} render={({field}) => (
                            <FormItem><FormLabel>Repeat Every</FormLabel><FormControl><Input type="number"
                                                                                             min="1" {...field}
                                                                                             onChange={e => field.onChange(parseInt(e.target.value))}/></FormControl></FormItem>
                        )}/>
                    </div>
                    <Controller name="count" control={control} render={({field}) => (
                        <FormItem><FormLabel>Number of Sessions</FormLabel><FormControl><Input type="number" min="1"
                                                                                               max="52" {...field}
                                                                                               onChange={e => field.onChange(parseInt(e.target.value))}/></FormControl></FormItem>
                    )}/>
                    <Controller name="startDate" control={control} render={({field}) => (
                        <FormItem><FormLabel>Start Date</FormLabel><Popover><PopoverTrigger asChild><Button
                            variant="outline" className="w-full justify-start font-normal"><CalendarIcon
                            className="mr-2 h-4 w-4"/>{field.value ? format(field.value, 'PPP') :
                            <span>Pick a date</span>}</Button></PopoverTrigger><PopoverContent
                            className="w-auto p-0"><Calendar mode="single" selected={field.value}
                                                             onSelect={field.onChange}
                                                             autoFocus/></PopoverContent></Popover></FormItem>
                    )}/>
                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="startTime" control={control} render={({field}) => (
                            <FormItem><FormLabel>Start Time</FormLabel><FormControl><Input
                                type="time" {...field}/></FormControl></FormItem>
                        )}/>
                        <Controller name="durationHours" control={control} render={({field}) => (
                            <FormItem><FormLabel>Duration (hours)</FormLabel><FormControl><Input type="number" min="0.5"
                                                                                                 step="0.5" {...field}
                                                                                                 onChange={e => field.onChange(parseFloat(e.target.value))}/></FormControl></FormItem>
                        )}/>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="salesStartRuleType" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Sales Start Rule</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                    <FormControl>
                                        <SelectTrigger>
                                            <SelectValue/>
                                        </SelectTrigger>
                                    </FormControl>
                                    <SelectContent>
                                        <SelectItem value="IMMEDIATE">Immediate</SelectItem>
                                        <SelectItem value="ROLLING">Rolling (Before Event)</SelectItem>
                                        <SelectItem value="FIXED">Fixed Datetime</SelectItem>
                                    </SelectContent>
                                </Select>
                            </FormItem>
                        )}/>


                        {watch('salesStartRuleType') === 'ROLLING' && (
                            <div className="space-y-2">
                                <div className="flex items-center justify-between">
                                    <FormLabel>Unit</FormLabel>
                                    <div className="flex items-center space-x-2">
                                        <span className="text-sm text-muted-foreground">Hours</span>
                                        <Controller
                                            name="isDaysNotHours"
                                            control={control}
                                            render={({field}) => (
                                                <Switch
                                                    checked={field.value}
                                                    onCheckedChange={handleUnitToggle}
                                                />
                                            )}
                                        />
                                        <span className="text-sm text-muted-foreground">Days</span>
                                    </div>
                                </div>

                                <Controller
                                    name="salesStartHoursBefore"
                                    control={control}
                                    render={({field}) => (
                                        <FormItem>
                                            <FormLabel>
                                                {isDaysNotHours ? 'Days before event' : 'Hours before event'}
                                            </FormLabel>
                                            <FormControl>
                                                <Input
                                                    type="number"
                                                    min="1"
                                                    {...field}
                                                    value={isDaysNotHours ? Math.round(field.value / 24) : field.value}
                                                    onChange={(e) => {
                                                        const value = parseInt(e.target.value);
                                                        if (isDaysNotHours) {
                                                            // If days, store as hours in the form data
                                                            field.onChange(value * 24);
                                                        } else {
                                                            field.onChange(value);
                                                        }
                                                    }}
                                                />
                                            </FormControl>
                                        </FormItem>
                                    )}
                                />
                            </div>
                        )}

                        {watch('salesStartRuleType') === 'FIXED' && (
                            <>
                                <Controller name="salesStartFixedDatetime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Date</FormLabel>
                                        <Popover>
                                            <PopoverTrigger asChild>
                                                <Button variant="outline" className="w-full justify-start font-normal">
                                                    <CalendarIcon className="mr-2 h-4 w-4"/>
                                                    {field.value ? format(field.value, 'PPP') :
                                                        <span>Pick a date</span>}
                                                </Button>
                                            </PopoverTrigger>
                                            <PopoverContent className="w-auto p-0">
                                                <Calendar
                                                    mode="single"
                                                    selected={field.value}
                                                    onSelect={field.onChange}
                                                    autoFocus
                                                />
                                            </PopoverContent>
                                        </Popover>
                                    </FormItem>
                                )}/>
                                <Controller name="salesStartFixedTime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Time</FormLabel>
                                        <FormControl>
                                            <Input type="time" {...field} />
                                        </FormControl>
                                    </FormItem>
                                )}/>
                            </>
                        )}
                    </div>

                    <DialogFooter>
                        <Button type="submit">Generate Sessions</Button>
                    </DialogFooter>
                </form>
            </DialogContent>
        </Dialog>
    );
}

import {Controller, useForm} from "react-hook-form";
import {addDays, addWeeks, format, setHours, setMinutes, subDays, subHours} from "date-fns";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle
} from "@/components/ui/dialog";
import {FormControl, FormItem, FormLabel} from "@/components/ui/form";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Input} from "@/components/ui/input";
import {Popover, PopoverContent, PopoverTrigger} from "@/components/ui/popover";
import {Button} from "@/components/ui/button";
import {Calendar as CalendarIcon} from "lucide-react";
import {Calendar} from "@/components/ui/calendar";
import * as React from "react";
import {SessionFormData} from "@/lib/validators/event";
import {Switch} from "@/components/ui/switch";
import {toast} from "sonner";
import {Enums, SessionType} from "@/lib/validators/enums";

interface RecurringSessionFormValues {
    frequency: 'daily' | 'weekly';
    interval: number;
    count: number;
    startDate: Date;
    startTime: string;
    durationHours: number;
    salesStartRuleType: Enums;
    salesStartHoursBefore: number;
    salesStartFixedDatetime: Date;
    salesStartFixedTime: string; // Added field for fixed time
    isDaysNotHours: boolean;
}

export function RecurringSessionsDialog({open, setOpen, onGenerate, currentSessionCount, maxSessions}: {
    open: boolean,
    setOpen: (open: boolean) => void,
    onGenerate: (sessions: SessionFormData[]) => void,
    currentSessionCount: number,
    maxSessions: number
}) {
    const {control, handleSubmit, watch, setValue} = useForm<RecurringSessionFormValues>({
        defaultValues: {
            frequency: 'weekly',
            interval: 1,
            count: 5,
            startDate: new Date(),
            startTime: '19:00',
            durationHours: 2,
            salesStartRuleType: Enums.ROLLING,
            salesStartHoursBefore: 168,
            salesStartFixedDatetime: new Date(),
            salesStartFixedTime: '12:00', // Default fixed time
            isDaysNotHours: true,
        }
    });

    const salesStartHoursBefore = watch('salesStartHoursBefore');
    const isDaysNotHours = watch('isDaysNotHours');

    // Handle toggling between days and hours
    const handleUnitToggle = (checked: boolean) => {
        setValue('isDaysNotHours', checked);
        if (checked) {
            // Converting hours to days (rounded)
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24) * 24);
        } else {
            // Converting days to hours
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24));
        }
    };

    const onSubmit = (data: RecurringSessionFormValues) => {
        // Check if adding these sessions would exceed the limit
        if (currentSessionCount + data.count > maxSessions) {
            toast.error(`Cannot add ${data.count} sessions. You are limited to ${maxSessions} total sessions (${currentSessionCount} already added).`);
            return;
        }

        const newSessions: SessionFormData[] = [];
        let currentStartTime = setMinutes(
            setHours(
                new Date(data.startDate), // Ensure we have a fresh Date object
                parseInt(data.startTime.split(':')[0])
            ),
            parseInt(data.startTime.split(':')[1])
        );

        // For fixed sales start, calculate the datetime once
        let fixedSalesStartTime: Date | undefined;

        if (data.salesStartRuleType === Enums.FIXED) {
            const [hours, minutes] = data.salesStartFixedTime.split(':').map(num => parseInt(num));
            fixedSalesStartTime = setMinutes(
                setHours(
                    new Date(data.salesStartFixedDatetime),
                    hours
                ),
                minutes
            );

            // Validate that sales start time is before the first event start time
            if (fixedSalesStartTime >= currentStartTime) {
                toast.error("Sales start time must be before the first event start time");
                return;
            }
        }

        for (let i = 0; i < data.count; i++) {
            const endTime = new Date(currentStartTime.getTime() + data.durationHours * 60 * 60 * 1000);

            // Calculate sales start time based on the rule type default to seven days before start
            let salesStartTime = new Date(subDays(currentStartTime, 7)).toISOString();

            if (data.salesStartRuleType === Enums.IMMEDIATE) {
                // For immediate sales, use current time
                salesStartTime = new Date().toISOString();
            } else if (data.salesStartRuleType === Enums.ROLLING) {
                // For rolling sales, subtract the specified hours from each event start time
                salesStartTime = subHours(currentStartTime, data.salesStartHoursBefore).toISOString();
            } else if (data.salesStartRuleType === Enums.FIXED && fixedSalesStartTime) {
                // For fixed date, use the pre-calculated time (same for all sessions)
                salesStartTime = fixedSalesStartTime.toISOString();
            }

            newSessions.push({
                startTime: currentStartTime.toISOString(),
                sessionType: SessionType.PHYSICAL,
                endTime: endTime.toISOString(),
                salesStartTime: salesStartTime,
                layoutData: {name: null, layout: {blocks: []}},
            });

            if (data.frequency === 'daily') {
                currentStartTime = addDays(currentStartTime, data.interval);
            } else if (data.frequency === 'weekly') {
                currentStartTime = addWeeks(currentStartTime, data.interval);
            }
        }

        console.log("Generated sessions:", newSessions);
        onGenerate(newSessions);
        setOpen(false);
    };

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>Add Recurring Sessions</DialogTitle>
                    <DialogDescription>
                        Generate a series of sessions based on a recurring pattern.
                        {maxSessions > 0 && (
                            <span className="block mt-1 text-sm text-muted-foreground">
                                Limit: {currentSessionCount}/{maxSessions} sessions used.
                            </span>
                        )}
                    </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="frequency" control={control} render={({field}) => (
                            <FormItem><FormLabel>Frequency</FormLabel><Select onValueChange={field.onChange}
                                                                              defaultValue={field.value}><FormControl><SelectTrigger><SelectValue/></SelectTrigger></FormControl><SelectContent><SelectItem
                                value="daily">Daily</SelectItem><SelectItem
                                value="weekly">Weekly</SelectItem></SelectContent></Select></FormItem>
                        )}/>
                        <Controller name="interval" control={control} render={({field}) => (
                            <FormItem><FormLabel>Repeat Every</FormLabel><FormControl><Input type="number"
                                                                                             min="1" {...field}
                                                                                             onChange={e => field.onChange(parseInt(e.target.value))}/></FormControl></FormItem>
                        )}/>
                    </div>
                    <Controller name="count" control={control} render={({field}) => (
                        <FormItem><FormLabel>Number of Sessions</FormLabel><FormControl><Input type="number" min="1"
                                                                                               max="52" {...field}
                                                                                               onChange={e => field.onChange(parseInt(e.target.value))}/></FormControl></FormItem>
                    )}/>
                    <Controller name="startDate" control={control} render={({field}) => (
                        <FormItem><FormLabel>Start Date</FormLabel><Popover><PopoverTrigger asChild><Button
                            variant="outline" className="w-full justify-start font-normal"><CalendarIcon
                            className="mr-2 h-4 w-4"/>{field.value ? format(field.value, 'PPP') :
                            <span>Pick a date</span>}</Button></PopoverTrigger><PopoverContent
                            className="w-auto p-0"><Calendar mode="single" selected={field.value}
                                                             onSelect={field.onChange}
                                                             autoFocus/></PopoverContent></Popover></FormItem>
                    )}/>
                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="startTime" control={control} render={({field}) => (
                            <FormItem><FormLabel>Start Time</FormLabel><FormControl><Input
                                type="time" {...field}/></FormControl></FormItem>
                        )}/>
                        <Controller name="durationHours" control={control} render={({field}) => (
                            <FormItem><FormLabel>Duration (hours)</FormLabel><FormControl><Input type="number" min="0.5"
                                                                                                 step="0.5" {...field}
                                                                                                 onChange={e => field.onChange(parseFloat(e.target.value))}/></FormControl></FormItem>
                        )}/>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="salesStartRuleType" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Sales Start Rule</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                    <FormControl>
                                        <SelectTrigger>
                                            <SelectValue/>
                                        </SelectTrigger>
                                    </FormControl>
                                    <SelectContent>
                                        <SelectItem value="IMMEDIATE">Immediate</SelectItem>
                                        <SelectItem value="ROLLING">Rolling (Before Event)</SelectItem>
                                        <SelectItem value="FIXED">Fixed Datetime</SelectItem>
                                    </SelectContent>
                                </Select>
                            </FormItem>
                        )}/>


                        {watch('salesStartRuleType') === 'ROLLING' && (
                            <div className="space-y-2">
                                <div className="flex items-center justify-between">
                                    <FormLabel>Unit</FormLabel>
                                    <div className="flex items-center space-x-2">
                                        <span className="text-sm text-muted-foreground">Hours</span>
                                        <Controller
                                            name="isDaysNotHours"
                                            control={control}
                                            render={({field}) => (
                                                <Switch
                                                    checked={field.value}
                                                    onCheckedChange={handleUnitToggle}
                                                />
                                            )}
                                        />
                                        <span className="text-sm text-muted-foreground">Days</span>
                                    </div>
                                </div>

                                <Controller
                                    name="salesStartHoursBefore"
                                    control={control}
                                    render={({field}) => (
                                        <FormItem>
                                            <FormLabel>
                                                {isDaysNotHours ? 'Days before event' : 'Hours before event'}
                                            </FormLabel>
                                            <FormControl>
                                                <Input
                                                    type="number"
                                                    min="1"
                                                    {...field}
                                                    value={isDaysNotHours ? Math.round(field.value / 24) : field.value}
                                                    onChange={(e) => {
                                                        const value = parseInt(e.target.value);
                                                        if (isDaysNotHours) {
                                                            // If days, store as hours in the form data
                                                            field.onChange(value * 24);
                                                        } else {
                                                            field.onChange(value);
                                                        }
                                                    }}
                                                />
                                            </FormControl>
                                        </FormItem>
                                    )}
                                />
                            </div>
                        )}

                        {watch('salesStartRuleType') === 'FIXED' && (
                            <>
                                <Controller name="salesStartFixedDatetime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Date</FormLabel>
                                        <Popover>
                                            <PopoverTrigger asChild>
                                                <Button variant="outline" className="w-full justify-start font-normal">
                                                    <CalendarIcon className="mr-2 h-4 w-4"/>
                                                    {field.value ? format(field.value, 'PPP') :
                                                        <span>Pick a date</span>}
                                                </Button>
                                            </PopoverTrigger>
                                            <PopoverContent className="w-auto p-0">
                                                <Calendar
                                                    mode="single"
                                                    selected={field.value}
                                                    onSelect={field.onChange}
                                                    autoFocus
                                                />
                                            </PopoverContent>
                                        </Popover>
                                    </FormItem>
                                )}/>
                                <Controller name="salesStartFixedTime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Time</FormLabel>
                                        <FormControl>
                                            <Input type="time" {...field} />
                                        </FormControl>
                                    </FormItem>
                                )}/>
                            </>
                        )}
                    </div>

                    <DialogFooter>
                        <Button type="button" onClick={handleSubmit(onSubmit)}>Generate Sessions</Button>
                    </DialogFooter>
                </form>
            </DialogContent>
        </Dialog>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData} from '@/lib/validators/event';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {
    SessionListItemSeating
} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({ onConfigModeChange }: SeatingStepProps) {
    const { control, formState: { errors }, watch, getValues, setValue } = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const { fields } = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: any) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-4">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div> {/* Empty div for flexbox spacing */}
                </div>

                <Card>
                    <CardHeader>
                        <CardTitle>
                            {currentSession.isOnline
                                ? "Online Capacity Configuration"
                                : "Physical Seating Configuration"}
                        </CardTitle>
                        <CardDescription>
                            {currentSession.isOnline
                                ? "Set the capacity and ticket tier for your online event."
                                : "Choose a layout template or create a new one, then assign your tiers."}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        {currentSession.isOnline ? (
                            <OnlineConfigView onSave={handleSave} />
                        ) : (
                            <PhysicalConfigView onSave={handleSave} />
                        )}
                    </CardContent>
                </Card>

                <div className="flex items-center justify-between border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <Card>
            <CardHeader>
                <CardTitle>Seating & Tier Assignment</CardTitle>
                <CardDescription>
                    Configure the seating layout and assign ticket tiers for each session.
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData} from '@/lib/validators/event';
import {SessionListItemSeating} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({ onConfigModeChange }: SeatingStepProps) {
    const { control, formState: { errors }, watch, getValues, setValue } = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const { fields } = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: any) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-6">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div> {/* Empty div for flexbox spacing */}
                </div>

                <div className="space-y-2">
                    <h3 className="text-lg font-medium">
                        {currentSession.isOnline
                            ? "Online Capacity Configuration"
                            : "Physical Seating Configuration"}
                    </h3>
                    <p className="text-muted-foreground text-sm">
                        {currentSession.isOnline
                            ? "Set the capacity and ticket tier for your online event."
                            : "Choose a layout template or create a new one, then assign your tiers."}
                    </p>
                </div>

                <div className="border rounded-md p-6 bg-card">
                    {currentSession.isOnline ? (
                        <OnlineConfigView onSave={handleSave} />
                    ) : (
                        <PhysicalConfigView onSave={handleSave} />
                    )}
                </div>

                <div className="flex items-center border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <div className="space-y-6">
            <div>
                <h2 className="text-xl font-semibold mb-1">Seating & Tier Assignment</h2>
                <p className="text-sm text-muted-foreground">
                    Configure the seating layout and assign ticket tiers for each session.
                </p>
            </div>

            <div className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </div>
        </div>
    );
}

import {Controller, useForm} from "react-hook-form";
import {format, setHours, setMinutes, subDays, subHours} from "date-fns";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle
} from "@/components/ui/dialog";
import {FormControl, FormItem, FormLabel} from "@/components/ui/form";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Input} from "@/components/ui/input";
import {Popover, PopoverContent, PopoverTrigger} from "@/components/ui/popover";
import {Button} from "@/components/ui/button";
import {Calendar as CalendarIcon} from "lucide-react";
import {Calendar} from "@/components/ui/calendar";
import * as React from "react";
import {SessionFormData} from "@/lib/validators/event";
import {Switch} from "@/components/ui/switch";
import {toast} from "sonner";
import {Enums, SessionType} from "@/lib/validators/enums";

interface SingleSessionFormValues {
    startDate: Date;
    startTime: string;
    durationHours: number;
    salesStartRuleType: Enums;
    salesStartHoursBefore: number;
    salesStartFixedDatetime: Date;
    salesStartFixedTime: string; // Added field for fixed time
    isDaysNotHours: boolean;
}

export function SingleSessionDialog({open, setOpen, onAdd, currentSessionCount, maxSessions}: {
    open: boolean,
    setOpen: (open: boolean) => void,
    onAdd: (session: SessionFormData) => void,
    currentSessionCount: number,
    maxSessions: number
}) {
    const {control, handleSubmit, watch, setValue} = useForm<SingleSessionFormValues>({
        defaultValues: {
            startDate: new Date(),
            startTime: '19:00',
            durationHours: 2,
            salesStartRuleType: Enums.ROLLING,
            salesStartHoursBefore: 168,
            salesStartFixedDatetime: new Date(),
            salesStartFixedTime: '12:00', // Default fixed time
            isDaysNotHours: true, // Field to track if user is entering days or hours
        }
    });

    const salesStartHoursBefore = watch('salesStartHoursBefore');
    const isDaysNotHours = watch('isDaysNotHours');

    // Handle toggling between days and hours
    const handleUnitToggle = (checked: boolean) => {
        setValue('isDaysNotHours', checked);
        if (checked) {
            // Converting hours to days (rounded)
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24) * 24);
        } else {
            // Converting days to hours
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24));
        }
    };

    const onSubmit = (data: SingleSessionFormValues) => {
        // Check if adding this session would exceed the limit
        console.log("Current session count:", currentSessionCount);
        console.log("Submitted data:", data);
        if (currentSessionCount >= maxSessions) {
            toast.error(`Cannot add more sessions. You have reached the limit of ${maxSessions} sessions.`);
            return;
        }

        // Create the session start time by combining date and time
        const startTime = setMinutes(
            setHours(
                new Date(data.startDate), // Ensure we have a fresh Date object
                parseInt(data.startTime.split(':')[0])
            ),
            parseInt(data.startTime.split(':')[1])
        );

        // Calculate end time based on duration
        const endTime = new Date(startTime.getTime() + data.durationHours * 60 * 60 * 1000);

        // Calculate sales start time based on the rule type
        let salesStartTime = new Date(subDays(startTime, 7)).toISOString();

        if (data.salesStartRuleType === Enums.IMMEDIATE) {
            // For immediate sales, use current time
            salesStartTime = new Date().toISOString();
        } else if (data.salesStartRuleType === Enums.ROLLING) {
            // For rolling sales, subtract the specified hours from the event start time
            salesStartTime = subHours(startTime, data.salesStartHoursBefore).toISOString();
        } else if (data.salesStartRuleType === Enums.FIXED) {
            // For fixed datetime, combine the selected date with the time
            const [hours, minutes] = data.salesStartFixedTime.split(':').map(num => parseInt(num));
            const fixedSalesStartTime = setMinutes(
                setHours(
                    new Date(data.salesStartFixedDatetime),
                    hours
                ),
                minutes
            );

            // Validate that sales start time is before event start time
            if (fixedSalesStartTime >= startTime) {
                toast.error("Sales start time must be before the event start time");
                return;
            }

            salesStartTime = fixedSalesStartTime.toISOString();
        }

        // Create the session object
        const newSession: SessionFormData = {
            startTime: startTime.toISOString(),
            endTime: endTime.toISOString(),
            sessionType: SessionType.PHYSICAL,
            salesStartTime: salesStartTime, // Use the calculated sales start time
            layoutData: {name: null, layout: {blocks: []}}
        };

        console.log("New session:", newSession);
        onAdd(newSession);
        setOpen(false);
    };

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>Add Single Session</DialogTitle>
                    <DialogDescription>
                        Configure the details for this session.
                        {maxSessions > 0 && (
                            <span className="block mt-1 text-sm text-muted-foreground">
                                Limit: {currentSessionCount}/{maxSessions} sessions used.
                            </span>
                        )}
                    </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                    <Controller name="startDate" control={control} render={({field}) => (
                        <FormItem>
                            <FormLabel>Date</FormLabel>
                            <Popover>
                                <PopoverTrigger asChild>
                                    <Button variant="outline" className="w-full justify-start font-normal">
                                        <CalendarIcon className="mr-2 h-4 w-4"/>
                                        {field.value ? format(field.value, 'PPP') : <span>Pick a date</span>}
                                    </Button>
                                </PopoverTrigger>
                                <PopoverContent className="w-auto p-0">
                                    <Calendar
                                        mode="single"
                                        selected={field.value}
                                        onSelect={field.onChange}
                                        autoFocus
                                    />
                                </PopoverContent>
                            </Popover>
                        </FormItem>
                    )}/>

                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="startTime" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Start Time</FormLabel>
                                <FormControl>
                                    <Input type="time" {...field}/>
                                </FormControl>
                            </FormItem>
                        )}/>
                        <Controller name="durationHours" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Duration (hours)</FormLabel>
                                <FormControl>
                                    <Input
                                        type="number"
                                        min="0.5"
                                        step="0.5"
                                        {...field}
                                        onChange={e => field.onChange(parseFloat(e.target.value))}
                                    />
                                </FormControl>
                            </FormItem>
                        )}/>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="salesStartRuleType" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Sales Start Rule</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                    <FormControl>
                                        <SelectTrigger>
                                            <SelectValue/>
                                        </SelectTrigger>
                                    </FormControl>
                                    <SelectContent>
                                        <SelectItem value="IMMEDIATE">Immediate</SelectItem>
                                        <SelectItem value="ROLLING">Rolling (Before Event)</SelectItem>
                                        <SelectItem value="FIXED">Fixed Datetime</SelectItem>
                                    </SelectContent>
                                </Select>
                            </FormItem>
                        )}/>

                        {watch('salesStartRuleType') === 'ROLLING' && (
                            <div className="space-y-2">
                                <div className="flex items-center justify-between">
                                    <FormLabel>Unit</FormLabel>
                                    <div className="flex items-center space-x-2">
                                        <span className="text-sm text-muted-foreground">Hours</span>
                                        <Controller
                                            name="isDaysNotHours"
                                            control={control}
                                            render={({field}) => (
                                                <Switch
                                                    checked={field.value}
                                                    onCheckedChange={handleUnitToggle}
                                                />
                                            )}
                                        />
                                        <span className="text-sm text-muted-foreground">Days</span>
                                    </div>
                                </div>

                                <Controller
                                    name="salesStartHoursBefore"
                                    control={control}
                                    render={({field}) => (
                                        <FormItem>
                                            <FormLabel>
                                                {isDaysNotHours ? 'Days before event' : 'Hours before event'}
                                            </FormLabel>
                                            <FormControl>
                                                <Input
                                                    type="number"
                                                    min="1"
                                                    {...field}
                                                    value={isDaysNotHours ? Math.round(field.value / 24) : field.value}
                                                    onChange={(e) => {
                                                        const value = parseInt(e.target.value);
                                                        if (isDaysNotHours) {
                                                            // If days, store as hours in the form data
                                                            field.onChange(value * 24);
                                                        } else {
                                                            field.onChange(value);
                                                        }
                                                    }}
                                                />
                                            </FormControl>
                                        </FormItem>
                                    )}
                                />
                            </div>
                        )}

                        {watch('salesStartRuleType') === 'FIXED' && (
                            <>
                                <Controller name="salesStartFixedDatetime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Date</FormLabel>
                                        <Popover>
                                            <PopoverTrigger asChild>
                                                <Button variant="outline" className="w-full justify-start font-normal">
                                                    <CalendarIcon className="mr-2 h-4 w-4"/>
                                                    {field.value ? format(field.value, 'PPP') :
                                                        <span>Pick a date</span>}
                                                </Button>
                                            </PopoverTrigger>
                                            <PopoverContent className="w-auto p-0">
                                                <Calendar
                                                    mode="single"
                                                    selected={field.value}
                                                    onSelect={field.onChange}
                                                    autoFocus
                                                />
                                            </PopoverContent>
                                        </Popover>
                                    </FormItem>
                                )}/>
                                <Controller name="salesStartFixedTime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Time</FormLabel>
                                        <FormControl>
                                            <Input type="time" {...field} />
                                        </FormControl>
                                    </FormItem>
                                )}/>
                            </>
                        )}
                    </div>

                    <DialogFooter>
                        <Button type="submit">Add Session</Button>
                    </DialogFooter>
                </form>
            </DialogContent>
        </Dialog>
    );
}

import {Controller, useForm} from "react-hook-form";
import {format, setHours, setMinutes, subDays, subHours} from "date-fns";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle
} from "@/components/ui/dialog";
import {FormControl, FormItem, FormLabel} from "@/components/ui/form";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Input} from "@/components/ui/input";
import {Popover, PopoverContent, PopoverTrigger} from "@/components/ui/popover";
import {Button} from "@/components/ui/button";
import {Calendar as CalendarIcon} from "lucide-react";
import {Calendar} from "@/components/ui/calendar";
import * as React from "react";
import {SessionFormData} from "@/lib/validators/event";
import {Switch} from "@/components/ui/switch";
import {toast} from "sonner";
import {Enums, SessionType} from "@/lib/validators/enums";

interface SingleSessionFormValues {
    startDate: Date;
    startTime: string;
    durationHours: number;
    salesStartRuleType: Enums;
    salesStartHoursBefore: number;
    salesStartFixedDatetime: Date;
    salesStartFixedTime: string; // Added field for fixed time
    isDaysNotHours: boolean;
}

export function SingleSessionDialog({open, setOpen, onAdd, currentSessionCount, maxSessions}: {
    open: boolean,
    setOpen: (open: boolean) => void,
    onAdd: (session: SessionFormData) => void,
    currentSessionCount: number,
    maxSessions: number
}) {
    const {control, handleSubmit, watch, setValue} = useForm<SingleSessionFormValues>({
        defaultValues: {
            startDate: new Date(),
            startTime: '19:00',
            durationHours: 2,
            salesStartRuleType: Enums.ROLLING,
            salesStartHoursBefore: 168,
            salesStartFixedDatetime: new Date(),
            salesStartFixedTime: '12:00', // Default fixed time
            isDaysNotHours: true, // Field to track if user is entering days or hours
        }
    });

    const salesStartHoursBefore = watch('salesStartHoursBefore');
    const isDaysNotHours = watch('isDaysNotHours');

    // Handle toggling between days and hours
    const handleUnitToggle = (checked: boolean) => {
        setValue('isDaysNotHours', checked);
        if (checked) {
            // Converting hours to days (rounded)
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24) * 24);
        } else {
            // Converting days to hours
            setValue('salesStartHoursBefore', Math.round(salesStartHoursBefore / 24));
        }
    };

    const onSubmit = (data: SingleSessionFormValues) => {
        // Check if adding this session would exceed the limit
        console.log("Current session count:", currentSessionCount);
        console.log("Submitted data:", data);
        if (currentSessionCount >= maxSessions) {
            toast.error(`Cannot add more sessions. You have reached the limit of ${maxSessions} sessions.`);
            return;
        }

        // Create the session start time by combining date and time
        const startTime = setMinutes(
            setHours(
                new Date(data.startDate), // Ensure we have a fresh Date object
                parseInt(data.startTime.split(':')[0])
            ),
            parseInt(data.startTime.split(':')[1])
        );

        // Calculate end time based on duration
        const endTime = new Date(startTime.getTime() + data.durationHours * 60 * 60 * 1000);

        // Calculate sales start time based on the rule type
        let salesStartTime = new Date(subDays(startTime, 7)).toISOString();

        if (data.salesStartRuleType === Enums.IMMEDIATE) {
            // For immediate sales, use current time
            salesStartTime = new Date().toISOString();
        } else if (data.salesStartRuleType === Enums.ROLLING) {
            // For rolling sales, subtract the specified hours from the event start time
            salesStartTime = subHours(startTime, data.salesStartHoursBefore).toISOString();
        } else if (data.salesStartRuleType === Enums.FIXED) {
            // For fixed datetime, combine the selected date with the time
            const [hours, minutes] = data.salesStartFixedTime.split(':').map(num => parseInt(num));
            const fixedSalesStartTime = setMinutes(
                setHours(
                    new Date(data.salesStartFixedDatetime),
                    hours
                ),
                minutes
            );

            // Validate that sales start time is before event start time
            if (fixedSalesStartTime >= startTime) {
                toast.error("Sales start time must be before the event start time");
                return;
            }

            salesStartTime = fixedSalesStartTime.toISOString();
        }

        // Create the session object
        const newSession: SessionFormData = {
            startTime: startTime.toISOString(),
            endTime: endTime.toISOString(),
            sessionType: SessionType.PHYSICAL,
            salesStartTime: salesStartTime, // Use the calculated sales start time
            layoutData: {name: null, layout: {blocks: []}}
        };

        console.log("New session:", newSession);
        onAdd(newSession);
        setOpen(false);
    };

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>Add Single Session</DialogTitle>
                    <DialogDescription>
                        Configure the details for this session.
                        {maxSessions > 0 && (
                            <span className="block mt-1 text-sm text-muted-foreground">
                                Limit: {currentSessionCount}/{maxSessions} sessions used.
                            </span>
                        )}
                    </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                    <Controller name="startDate" control={control} render={({field}) => (
                        <FormItem>
                            <FormLabel>Date</FormLabel>
                            <Popover>
                                <PopoverTrigger asChild>
                                    <Button variant="outline" className="w-full justify-start font-normal">
                                        <CalendarIcon className="mr-2 h-4 w-4"/>
                                        {field.value ? format(field.value, 'PPP') : <span>Pick a date</span>}
                                    </Button>
                                </PopoverTrigger>
                                <PopoverContent className="w-auto p-0">
                                    <Calendar
                                        mode="single"
                                        selected={field.value}
                                        onSelect={field.onChange}
                                        autoFocus
                                    />
                                </PopoverContent>
                            </Popover>
                        </FormItem>
                    )}/>

                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="startTime" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Start Time</FormLabel>
                                <FormControl>
                                    <Input type="time" {...field}/>
                                </FormControl>
                            </FormItem>
                        )}/>
                        <Controller name="durationHours" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Duration (hours)</FormLabel>
                                <FormControl>
                                    <Input
                                        type="number"
                                        min="0.5"
                                        step="0.5"
                                        {...field}
                                        onChange={e => field.onChange(parseFloat(e.target.value))}
                                    />
                                </FormControl>
                            </FormItem>
                        )}/>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <Controller name="salesStartRuleType" control={control} render={({field}) => (
                            <FormItem>
                                <FormLabel>Sales Start Rule</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                    <FormControl>
                                        <SelectTrigger>
                                            <SelectValue/>
                                        </SelectTrigger>
                                    </FormControl>
                                    <SelectContent>
                                        <SelectItem value="IMMEDIATE">Immediate</SelectItem>
                                        <SelectItem value="ROLLING">Rolling (Before Event)</SelectItem>
                                        <SelectItem value="FIXED">Fixed Datetime</SelectItem>
                                    </SelectContent>
                                </Select>
                            </FormItem>
                        )}/>

                        {watch('salesStartRuleType') === 'ROLLING' && (
                            <div className="space-y-2">
                                <div className="flex items-center justify-between">
                                    <FormLabel>Unit</FormLabel>
                                    <div className="flex items-center space-x-2">
                                        <span className="text-sm text-muted-foreground">Hours</span>
                                        <Controller
                                            name="isDaysNotHours"
                                            control={control}
                                            render={({field}) => (
                                                <Switch
                                                    checked={field.value}
                                                    onCheckedChange={handleUnitToggle}
                                                />
                                            )}
                                        />
                                        <span className="text-sm text-muted-foreground">Days</span>
                                    </div>
                                </div>

                                <Controller
                                    name="salesStartHoursBefore"
                                    control={control}
                                    render={({field}) => (
                                        <FormItem>
                                            <FormLabel>
                                                {isDaysNotHours ? 'Days before event' : 'Hours before event'}
                                            </FormLabel>
                                            <FormControl>
                                                <Input
                                                    type="number"
                                                    min="1"
                                                    {...field}
                                                    value={isDaysNotHours ? Math.round(field.value / 24) : field.value}
                                                    onChange={(e) => {
                                                        const value = parseInt(e.target.value);
                                                        if (isDaysNotHours) {
                                                            // If days, store as hours in the form data
                                                            field.onChange(value * 24);
                                                        } else {
                                                            field.onChange(value);
                                                        }
                                                    }}
                                                />
                                            </FormControl>
                                        </FormItem>
                                    )}
                                />
                            </div>
                        )}

                        {watch('salesStartRuleType') === 'FIXED' && (
                            <>
                                <Controller name="salesStartFixedDatetime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Date</FormLabel>
                                        <Popover>
                                            <PopoverTrigger asChild>
                                                <Button variant="outline" className="w-full justify-start font-normal">
                                                    <CalendarIcon className="mr-2 h-4 w-4"/>
                                                    {field.value ? format(field.value, 'PPP') :
                                                        <span>Pick a date</span>}
                                                </Button>
                                            </PopoverTrigger>
                                            <PopoverContent className="w-auto p-0">
                                                <Calendar
                                                    mode="single"
                                                    selected={field.value}
                                                    onSelect={field.onChange}
                                                    autoFocus
                                                />
                                            </PopoverContent>
                                        </Popover>
                                    </FormItem>
                                )}/>
                                <Controller name="salesStartFixedTime" control={control} render={({field}) => (
                                    <FormItem>
                                        <FormLabel>Sales Start Time</FormLabel>
                                        <FormControl>
                                            <Input type="time" {...field} />
                                        </FormControl>
                                    </FormItem>
                                )}/>
                            </>
                        )}
                    </div>

                    <DialogFooter>
                        <Button type="button" onClick={handleSubmit(onSubmit)}>Add Session</Button>
                    </DialogFooter>
                </form>
            </DialogContent>
        </Dialog>
    );
}

'use client';

import * as React from 'react';
import {z} from 'zod';
import {useForm} from 'react-hook-form';
import {zodResolver} from '@hookform/resolvers/zod';

import {Button} from '@/components/ui/button';
import {Input} from '@/components/ui/input';
import {Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter} from '@/components/ui/dialog';
import {Form, FormControl, FormField, FormItem, FormLabel, FormMessage} from '@/components/ui/form';

const tierSchema = z.object({
    name: z.string().min(1, {message: "Tier name is required"}),
    price: z.number().min(0, {message: "Price must be a positive number"}),
    color: z.string().min(1, {message: "Color is required"})
});

type TierFormValues = z.infer<typeof tierSchema>;

interface TierDialogProps {
    open: boolean;
    setOpen: (open: boolean) => void;
    onSave: (tier: { name: string, price: number, color: string }) => void;
    initialValues?: { name: string, price: number, color: string };
    mode: 'create' | 'edit';
}

export function TierDialog({open, setOpen, onSave, initialValues, mode}: TierDialogProps) {
    // Default values when creating a new tier
    const defaultValues: TierFormValues = {
        name: '',
        price: 0,
        color: '#8B5CF6' // Default purple color
    };

    const form = useForm<TierFormValues>({
        resolver: zodResolver(tierSchema),
        defaultValues: initialValues || defaultValues
    });

    const handleSubmit = (values: TierFormValues) => {
        onSave({
            name: values.name,
            price: values.price,
            color: values.color
        });
        setOpen(false);
        form.reset();
    };

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>{mode === 'create' ? 'Create New Tier' : 'Edit Tier'}</DialogTitle>
                </DialogHeader>

                <Form {...form}>
                    <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6 py-4">
                        <FormField
                            control={form.control}
                            name="name"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Tier Name</FormLabel>
                                    <FormControl>
                                        <Input
                                            placeholder="e.g., VIP, Premium, Gold"
                                            {...field}
                                        />
                                    </FormControl>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="price"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Price (USD)</FormLabel>
                                    <FormControl>
                                        <Input
                                            type="number"
                                            min="0"
                                            step="0.01"
                                            placeholder="50.00"
                                            {...field}
                                            onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                                        />
                                    </FormControl>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="color"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Theme Color</FormLabel>
                                    <FormControl>
                                        <div className="flex gap-2 items-center">
                                            <Input
                                                type="color"
                                                className="p-1 h-10 w-16 cursor-pointer bg-background"
                                                {...field}
                                            />
                                            <Input
                                                type="text"
                                                placeholder="#8B5CF6"
                                                className="flex-1 font-mono text-sm"
                                                {...field}
                                            />
                                        </div>
                                    </FormControl>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <DialogFooter>
                            <Button type="button" variant="outline" onClick={() => setOpen(false)}>
                                Cancel
                            </Button>
                            <Button
                                onClick={(e) => {
                                    e.preventDefault();
                                    form.handleSubmit(handleSubmit)(e);
                                }}
                                type="button"
                            >
                                {mode === 'create' ? 'Create' : 'Save'}
                            </Button>
                        </DialogFooter>
                    </form>
                </Form>
            </DialogContent>
        </Dialog>
    );
}

