'use client';

import * as React from 'react';
import {useEffect, useState} from 'react';
import {CoreDetailsStep} from "@/app/manage/organization/[organization_id]/event/_components/CoreDetailsStep";
import {Progress} from "@/components/ui/progress";
import {CreateEventRequest} from "@/types/event";
import {toast} from "sonner";
import {FormProvider, useForm} from "react-hook-form";
import {Button} from "@/components/ui/button";
import {TiersStep} from "@/app/manage/organization/[organization_id]/event/_components/TierStep";
import {zodResolver} from "@hookform/resolvers/zod";
import {coreDetailsSchema, tiersSchema} from "@/lib/validators/event";
import {WizardSidebar} from "../_components/WizardSideBar";
import {useSidebar} from "@/components/ui/sidebar";

// --- Main Wizard Parent Component ---
export default function CreateEventPage() {
    const [step, setStep] = useState(1);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const totalSteps = 5;
    const {setOpen} = useSidebar();

    const steps = [
        {number: 1, title: "Core Details", description: "Name, category, and location"},
        {number: 2, title: "Tiers & Pricing", description: "Set your ticket prices"},
        {number: 3, title: "Scheduling", description: "Add dates and times"},
        {number: 4, title: "Seating", description: "Configure seating maps"},
        {number: 5, title: "Review & Publish", description: "Final check before submission"},
    ];

    // Collapse the main sidebar when component mounts
    useEffect(() => {
        setOpen(false);
    }, [setOpen]);

    //  The single form instance for the entire wizard is created here.
    const methods = useForm<CreateEventRequest>({
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            organizationId: '', // Should be set from context/params
            categoryId: '',
            isOnline: false,
            tiers: [],
            sessions: [],
        },
        mode: 'onSubmit', // Only validate when explicitly triggered
    });

    // Update the validation schema based on the current step
    useEffect(() => {
        if (step === 1) {
            methods.clearErrors();
        } else if (step === 2) {
            methods.clearErrors();
        }
    }, [step, methods]);

    const validateCoreDetails = async () => {
        try {
            // Check for cover photos
            if (coverFiles.length === 0) {
                toast.error("Please upload at least one cover photo.");
                return false;
            }

            // Get form values for core details
            const values = methods.getValues();
            const coreData = {
                title: values.title,
                description: values.description || "",
                overview: values.overview || "",
                categoryId: values.categoryId || "",
                isOnline: values.isOnline || false,
                venueId: values.venueId,
                onlineLink: values.onlineLink,
                locationDescription: values.locationDescription,
            };

            // Validate with Zod schema
            const result = coreDetailsSchema.safeParse(coreData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.path.join('.')}: ${err.message}`);
                });

                // Also set form errors for field highlighting
                result.error.errors.forEach(err => {
                    const path = err.path.join('.') as keyof CreateEventRequest;
                    methods.setError(path, { 
                        type: 'manual', 
                        message: err.message 
                    });
                });

                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Validation error:", error);
            toast.error("Please check all required fields");
            return false;
        }
    };

    const validateTiers = async () => {
        try {
            // Get tiers data
            const values = methods.getValues();
            const tiersData = { tiers: values.tiers || [] };

            // Validate with Zod schema
            const result = tiersSchema.safeParse(tiersData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.message}`);
                });
                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Tier validation error:", error);
            toast.error("Please add at least one tier with valid information");
            return false;
        }
    };

    const onNext = async () => {
        let isValid = false;

        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        } else {
            // For future steps that don't have validation yet
            isValid = true;
        }

        if (isValid) {
            setStep(s => Math.min(totalSteps, s + 1));
        }
    };

    const onPrev = () => setStep(s => Math.max(1, s - 1));

    const onSubmit = async (data: CreateEventRequest) => {
        // Perform final validation before submission
        let isValid = true;

        // Validate the current step first
        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        }

        if (isValid) {
            console.log("Final Assembled Form Data:", data);
            console.log("Final Cover Files:", coverFiles);
            // Here you would call your createEvent API action
            toast.success("Event submitted for approval!");
        }
    };

    const renderStep = () => {
        switch (step) {
            case 1:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
            case 2:
                return <TiersStep/>;
            // Add cases for other steps here
            default:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
        }
    };

    return (
        <div className="flex min-h-screen bg-muted/40">
            <WizardSidebar currentStep={step} steps={steps}/>
            <main className="flex-1 p-4 md:p-8 overflow-y-auto ml-72">
                <div className="max-w-4xl mx-auto">
                    <div className="mb-8">
                        <p className="text-sm text-muted-foreground">Step {step} of {totalSteps}</p>
                        <Progress value={(step / totalSteps) * 100} className="mt-2"/>
                    </div>

                    {/*  The FormProvider broadcasts the form state to all children */}
                    <FormProvider {...methods}>
                        <form onSubmit={methods.handleSubmit(onSubmit)}>
                            {renderStep()}

                            <div className="flex justify-between mt-8">
                                <Button type="button" variant="outline" onClick={onPrev} disabled={step === 1}>
                                    Previous
                                </Button>
                                {step < totalSteps ? (
                                    <Button type="button" onClick={onNext}>
                                        Next
                                    </Button>
                                ) : (
                                    <Button type="submit">
                                        Submit for Approval
                                    </Button>
                                )}
                            </div>
                        </form>
                    </FormProvider>
                </div>
            </main>
        </div>
    );
}

import {apiFetch} from '@/lib/api';
import {CreateEventFormData} from '@/lib/validators/event';
import {EventDetailDTO, EventStatus, EventSummaryDTO} from '@/lib/validators/event';

import {PaginatedResponse} from "@/types/paginatedResponse";
import {EventResponseDTO} from "@/types/event";

const API_BASE_PATH = '/event-seating/v1/events';

// ================================================================================
// General User & Organizer Actions
// ================================================================================

/**
 * Creates a new event.
 */
export const createEvent = (eventData: CreateEventFormData, coverImages: File[]): Promise<EventResponseDTO> => {
    const formData = new FormData();
    formData.append('request', JSON.stringify(eventData));
    if (coverImages?.length > 0) {
        coverImages.forEach(file => formData.append('coverImages', file));
    }
    return apiFetch<EventResponseDTO>(API_BASE_PATH, {
        method: 'POST',
        body: formData,
    });
};

/**
 * Fetches event details for an event the current user OWNS.
 */
export const getMyEventById = (eventId: string): Promise<EventDetailDTO> => {
    return apiFetch<EventDetailDTO>(`${API_BASE_PATH}/${eventId}`);
};

/**
 * Fetches a paginated list of events for an organization the current user OWNS.
 */
export const getMyOrganizationEvents = (
    organizationId: string,
    status?: EventStatus,
    search?: string,
    page: number = 0,
    size: number = 10
): Promise<PaginatedResponse<EventSummaryDTO>> => {
    const params = new URLSearchParams({
        page: page.toString(),
        size: size.toString(),
    });
    if (status) params.append('status', status);
    if (search) params.append('search', search);

    return apiFetch<PaginatedResponse<EventSummaryDTO>>(`${API_BASE_PATH}/organization/${organizationId}?${params.toString()}`);
};

/**
 * Deletes an event the current user OWNS.
 */
export const deleteEvent = (eventId: string): Promise<void> => {
    return apiFetch<void>(`${API_BASE_PATH}/${eventId}`, {
        method: 'DELETE',
    });
};


