                <Skeleton className="h-4 w-3/4"/>
                <Skeleton className="h-4 w-full"/>
                <Skeleton className="h-4 w-5/6"/>
            </div>

            {/* Overview Skeleton */}
            <div className="space-y-4">
                <Skeleton className="h-6 w-1/3"/> {/* Heading */}
                <div className="space-y-2">
                    <Skeleton className="h-4 w-full"/>
                    <Skeleton className="h-4 w-11/12"/>
                    <Skeleton className="h-4 w-10/12"/>
                </div>
            </div>
        </div>
    );
};

import * as React from 'react';
import {Skeleton} from "@/components/ui/skeleton";
import ReactMarkdown from 'react-markdown';
import rehypeRaw from 'rehype-raw';

interface ReviewEventDetailsProps {
    description?: string;
    overview?: string;
}

export const ReviewEventDetails: React.FC<ReviewEventDetailsProps> = ({
                                                                          description,
                                                                          overview
                                                                      }) => {
    if (!description && !overview) return null;

    return (
        <div className="space-y-6">
            {description && (
                <div className="text-lg leading-relaxed">
                    {description}
                </div>
            )}

            {overview && (
                <div className="prose prose-stone max-w-none dark:prose-invert">
                    <ReactMarkdown 
                        rehypePlugins={[rehypeRaw]}
                        components={{
                            h1: ({node, ...props}) => <h1 className="text-2xl font-bold mt-6 mb-4" {...props} />,
                            h2: ({node, ...props}) => <h2 className="text-xl font-bold mt-5 mb-3" {...props} />,
                            h3: ({node, ...props}) => <h3 className="text-lg font-bold mt-4 mb-2" {...props} />,
                            h4: ({node, ...props}) => <h4 className="text-base font-bold mt-3 mb-2" {...props} />,
                            h5: ({node, ...props}) => <h5 className="text-sm font-bold mt-3 mb-1" {...props} />,
                            h6: ({node, ...props}) => <h6 className="text-sm font-bold mt-3 mb-1" {...props} />,
                        }}
                    >
                        {overview}
                    </ReactMarkdown>
                </div>
            )}
        </div>
    );
};

export const ReviewEventDetailsSkeleton: React.FC = () => {
    return (
        <div className="space-y-6">
            {/* Description Skeleton */}
            <div className="space-y-2">
                <Skeleton className="h-4 w-3/4"/>
                <Skeleton className="h-4 w-full"/>
                <Skeleton className="h-4 w-5/6"/>
            </div>

            {/* Overview Skeleton */}
            <div className="space-y-4">
                <Skeleton className="h-6 w-1/3"/> {/* Heading */}
                <div className="space-y-2">
                    <Skeleton className="h-4 w-full"/>
                    <Skeleton className="h-4 w-11/12"/>
                    <Skeleton className="h-4 w-10/12"/>
                </div>
            </div>
        </div>
    );
};

"use client";

import * as React from "react";

export default function EventSessionsPage() {
  return (
    <div className="p-4 md:p-8 w-full">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-2xl font-bold mb-6">Event Sessions</h1>
        <p className="text-muted-foreground">
          Manage your event sessions and scheduling here.
        </p>
      </div>
    </div>
  );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData} from '@/lib/validators/event';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {
    SessionListItemSeating
} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({ onConfigModeChange }: SeatingStepProps) {
    const { control, formState: { errors }, watch, getValues, setValue } = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const { fields } = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: any) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-4">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div> {/* Empty div for flexbox spacing */}
                </div>

                <Card>
                    <CardHeader>
                        <CardTitle>
                            {currentSession.isOnline
                                ? "Online Capacity Configuration"
                                : "Physical Seating Configuration"}
                        </CardTitle>
                        <CardDescription>
                            {currentSession.isOnline
                                ? "Set the capacity and ticket tier for your online event."
                                : "Choose a layout template or create a new one, then assign your tiers."}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        {currentSession.isOnline ? (
                            <OnlineConfigView onSave={handleSave} />
                        ) : (
                            <PhysicalConfigView onSave={handleSave} />
                        )}
                    </CardContent>
                </Card>

                <div className="flex items-center justify-between border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <Card>
            <CardHeader>
                <CardTitle>Seating & Tier Assignment</CardTitle>
                <CardDescription>
                    Configure the seating layout and assign ticket tiers for each session.
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData} from '@/lib/validators/event';
import {SessionListItemSeating} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({ onConfigModeChange }: SeatingStepProps) {
    const { control, formState: { errors }, watch, getValues, setValue } = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const { fields } = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: any) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-6">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div> {/* Empty div for flexbox spacing */}
                </div>

                <div className="space-y-2">
                    <h3 className="text-lg font-medium">
                        {currentSession.isOnline
                            ? "Online Capacity Configuration"
                            : "Physical Seating Configuration"}
                    </h3>
                    <p className="text-muted-foreground text-sm">
                        {currentSession.isOnline
                            ? "Set the capacity and ticket tier for your online event."
                            : "Choose a layout template or create a new one, then assign your tiers."}
                    </p>
                </div>

                <div className="border rounded-md p-6 bg-card">
                    {currentSession.isOnline ? (
                        <OnlineConfigView onSave={handleSave} />
                    ) : (
                        <PhysicalConfigView onSave={handleSave} />
                    )}
                </div>

                <div className="flex items-center border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <div className="space-y-6">
            <div>
                <h2 className="text-xl font-semibold mb-1">Seating & Tier Assignment</h2>
                <p className="text-sm text-muted-foreground">
                    Configure the seating layout and assign ticket tiers for each session.
                </p>
            </div>

            <div className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect, useRef} from 'react';
import {useForm, FormProvider} from 'react-hook-form';
import {zodResolver} from '@hookform/resolvers/zod';
import {useOrganization} from '@/providers/OrganizationProvider';
import {useLimits} from '@/providers/LimitProvider';
import {CategoryResponse} from '@/types/category';
import {getAllCategories} from '@/lib/actions/categoryActions';
import {coreDetailsSchema, CoreDetailsData} from '@/lib/validators/event';

import {Button} from '@/components/ui/button';
import {Input} from '@/components/ui/input';
import {Textarea} from '@/components/ui/textarea';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {FormControl, FormField, FormItem, FormLabel, FormMessage} from '@/components/ui/form';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
    SelectGroup,
    SelectLabel
} from '@/components/ui/select';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Image from 'next/image';
import {ImageIcon, X, PlusCircle, Upload} from 'lucide-react';
import {toast} from 'sonner';
import Autoplay from 'embla-carousel-autoplay';

// --- Step 1: Core Details Component ---
export function CoreDetailsStep({onNextAction}: { onNextAction: () => void }) {
    const {organization} = useOrganization();
    const {myLimits} = useLimits();
    const [categories, setCategories] = useState<CategoryResponse[]>([]);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const fileInputRef = useRef<HTMLInputElement>(null);

    const maxPhotos = myLimits?.eventLimits.maxCoverPhotos || 1;

    const form = useForm<CoreDetailsData>({
        resolver: zodResolver(coreDetailsSchema),
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            categoryId: '', // Ensure categoryId is present
            isOnline: false,
        },
    });

    useEffect(() => {
        getAllCategories().then(setCategories);
    }, [organization]);

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files) {
            const files = Array.from(e.target.files);
            if ((coverFiles.length + files.length) > maxPhotos) {
                toast.error(`You can only upload a maximum of ${maxPhotos} photos.`);
                return;
            }
            setCoverFiles(prev => [...prev, ...files]);
        }
    };

    const removeImage = (index: number) => {
        setCoverFiles(prev => prev.filter((_, i) => i !== index));
    };

    function onSubmit(data: CoreDetailsData) {
        console.log("Step 1 Data:", data);
        console.log("Cover Files:", coverFiles);
        onNextAction();
    }

    return (
        <FormProvider {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
                {/* Hero Cover Photos Section */}
                <Card>
                    <CardHeader>
                        <CardTitle>Cover Photos</CardTitle>
                        <CardDescription>This is the first thing people will see. Upload up
                            to {maxPhotos} images.</CardDescription>
                    </CardHeader>
                    <CardContent>
                        {coverFiles.length > 0 ? (
                            <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                                <CarouselContent>
                                    {coverFiles.map((file, index) => (
                                        <CarouselItem key={index} className="relative">
                                            <div className="aspect-[21/9] w-full relative">
                                                <Image src={URL.createObjectURL(file)} alt={`Cover photo ${index + 1}`}
                                                       fill className="object-cover rounded-lg"/>
                                                <Button type="button" variant="destructive" size="icon"
                                                        className="absolute top-4 right-4 h-8 w-8 z-10"
                                                        onClick={() => removeImage(index)}><X
                                                    className="h-4 w-4"/></Button>
                                            </div>
                                        </CarouselItem>
                                    ))}
                                </CarouselContent>
                                <CarouselPrevious type="button"
                                                  className="absolute left-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                                <CarouselNext type="button"
                                              className="absolute right-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                            </Carousel>
                        ) : (
                            <div
                                className="flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-12 text-center aspect-[21/9] w-full">
                                <ImageIcon className="h-16 w-16 text-muted-foreground"/>
                                <p className="mt-4 text-lg font-medium">Add cover photos for your event</p>
                                <Button type="button" className="mt-4"
                                        onClick={() => fileInputRef.current?.click()}><Upload className="mr-2 h-4 w-4"/>Upload
                                    Images</Button>
                            </div>
                        )}
                        <div className="mt-4 flex justify-center items-center gap-4">
                            {coverFiles.length > 0 && (<Button type="button" variant="outline"
                                                               onClick={() => fileInputRef.current?.click()}><PlusCircle
                                className="mr-2 h-4 w-4"/>Add More</Button>)}
                            <p className="text-sm text-muted-foreground">{coverFiles.length} of {maxPhotos} photos
                                selected.</p>
                        </div>
                        <Input id="picture" type="file" multiple accept="image/*" onChange={handleFileChange}
                               className="hidden" ref={fileInputRef}/>
                    </CardContent>
                </Card>

                {/* Event Details Card with Category dropdown */}
                <Card>
                    <CardHeader>
                        <CardTitle>Event Details</CardTitle>
                        <CardDescription>Provide the core information about your event.</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                        <FormField control={form.control} name="title" render={({field}) => (
                            <FormItem><FormLabel>Event Title</FormLabel><FormControl><Input
                                placeholder="e.g., Annual Tech Conference 2025" {...field} /></FormControl><FormMessage/></FormItem>)}/>

                        {/* Category Selector as dropdown */}
                        <FormField
                            control={form.control}
                            name="categoryId"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Category</FormLabel>
                                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select a category for your event"/>
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent className="max-h-100">
                                            {categories.map((parentCat) => (
                                                <SelectGroup key={parentCat.id}>
                                                    <SelectLabel>{parentCat.name}</SelectLabel>
                                                    {parentCat.subCategories.map((subCat) => (
                                                        <SelectItem key={subCat.id} value={subCat.id}>
                                                            {subCat.name}
                                                        </SelectItem>
                                                    ))}
                                                </SelectGroup>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <FormField control={form.control} name="description" render={({field}) => (
                            <FormItem><FormLabel>Short Description</FormLabel><FormControl><Textarea
                                placeholder="A brief summary of your event." {...field} /></FormControl><FormMessage/></FormItem>)}/>
                        <FormField control={form.control} name="overview" render={({field}) => (
                            <FormItem><FormLabel>Overview</FormLabel><FormControl><Textarea
                                placeholder="Provide more details like schedule, speakers, etc."
                                className="min-h-32" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    </CardContent>
                </Card>
                <div className="flex justify-end mt-8">
                    <Button type="submit">Next: Tiers & Pricing</Button>
                </div>
            </form>
        </FormProvider>
    )
}

'use client';

import * as React from 'react';
import {useState, useEffect, useRef} from 'react';
import {useForm, FormProvider} from 'react-hook-form';
import {zodResolver} from '@hookform/resolvers/zod';
import {useOrganization} from '@/providers/OrganizationProvider';
import {useLimits} from '@/providers/LimitProvider';
import {CategoryResponse} from '@/types/category';
import {getAllCategories} from '@/lib/actions/categoryActions';
import {coreDetailsSchema, CoreDetailsData} from '@/lib/validators/event';

import {Button} from '@/components/ui/button';
import {Input} from '@/components/ui/input';
import {Textarea} from '@/components/ui/textarea';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {FormControl, FormField, FormItem, FormLabel, FormMessage} from '@/components/ui/form';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
    SelectGroup,
    SelectLabel
} from '@/components/ui/select';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Image from 'next/image';
import {ImageIcon, X, PlusCircle, Upload} from 'lucide-react';
import {toast} from 'sonner';
import Autoplay from 'embla-carousel-autoplay';

// Updated props interface to match the required signature
interface CoreDetailsStepProps {
    coverFiles: File[];
    setCoverFilesAction: React.Dispatch<React.SetStateAction<File[]>>;
    onNextAction?: () => void;
}

// --- Step 1: Core Details Component ---
export function CoreDetailsStep({ coverFiles, setCoverFilesAction, onNextAction }: CoreDetailsStepProps) {
    const {organization} = useOrganization();
    const {myLimits} = useLimits();
    const [categories, setCategories] = useState<CategoryResponse[]>([]);
    const fileInputRef = useRef<HTMLInputElement>(null);

    const maxPhotos = myLimits?.eventLimits.maxCoverPhotos || 1;

    const form = useForm<CoreDetailsData>({
        resolver: zodResolver(coreDetailsSchema),
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            categoryId: '', // Ensure categoryId is present
            isOnline: false,
        },
    });

    useEffect(() => {
        getAllCategories().then(setCategories);
    }, [organization]);

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files) {
            const files = Array.from(e.target.files);
            if ((coverFiles.length + files.length) > maxPhotos) {
                toast.error(`You can only upload a maximum of ${maxPhotos} photos.`);
                return;
            }
            setCoverFilesAction(prev => [...prev, ...files]);
        }
    };

    const removeImage = (index: number) => {
        setCoverFilesAction(prev => prev.filter((_, i) => i !== index));
    };

    function onSubmit(data: CoreDetailsData) {
        console.log("Step 1 Data:", data);
        console.log("Cover Files:", coverFiles);
        if (onNextAction) onNextAction();
    }

    return (
        <FormProvider {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
                {/* Hero Cover Photos Section */}
                <Card>
                    <CardHeader>
                        <CardTitle>Cover Photos</CardTitle>
                        <CardDescription>This is the first thing people will see. Upload up
                            to {maxPhotos} images.</CardDescription>
                    </CardHeader>
                    <CardContent>
                        {coverFiles.length > 0 ? (
                            <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                                <CarouselContent>
                                    {coverFiles.map((file, index) => (
                                        <CarouselItem key={index} className="relative">
                                            <div className="aspect-[21/9] w-full relative">
                                                <Image src={URL.createObjectURL(file)} alt={`Cover photo ${index + 1}`}
                                                       fill className="object-cover rounded-lg"/>
                                                <Button type="button" variant="destructive" size="icon"
                                                        className="absolute top-4 right-4 h-8 w-8 z-10"
                                                        onClick={() => removeImage(index)}><X
                                                    className="h-4 w-4"/></Button>
                                            </div>
                                        </CarouselItem>
                                    ))}
                                </CarouselContent>
                                <CarouselPrevious type="button"
                                                  className="absolute left-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                                <CarouselNext type="button"
                                              className="absolute right-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                            </Carousel>
                        ) : (
                            <div
                                className="flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-12 text-center aspect-[21/9] w-full">
                                <ImageIcon className="h-16 w-16 text-muted-foreground"/>
                                <p className="mt-4 text-lg font-medium">Add cover photos for your event</p>
                                <Button type="button" className="mt-4"
                                        onClick={() => fileInputRef.current?.click()}><Upload className="mr-2 h-4 w-4"/>Upload
                                    Images</Button>
                            </div>
                        )}
                        <div className="mt-4 flex justify-center items-center gap-4">
                            {coverFiles.length > 0 && (<Button type="button" variant="outline"
                                                               onClick={() => fileInputRef.current?.click()}><PlusCircle
                                className="mr-2 h-4 w-4"/>Add More</Button>)}
                            <p className="text-sm text-muted-foreground">{coverFiles.length} of {maxPhotos} photos
                                selected.</p>
                        </div>
                        <Input id="picture" type="file" multiple accept="image/*" onChange={handleFileChange}
                               className="hidden" ref={fileInputRef}/>
                    </CardContent>
                </Card>

                {/* Event Details Card with Category dropdown */}
                <Card>
                    <CardHeader>
                        <CardTitle>Event Details</CardTitle>
                        <CardDescription>Provide the core information about your event.</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                        <FormField control={form.control} name="title" render={({field}) => (
                            <FormItem><FormLabel>Event Title</FormLabel><FormControl><Input
                                placeholder="e.g., Annual Tech Conference 2025" {...field} /></FormControl><FormMessage/></FormItem>)}/>

                        {/* Category Selector as dropdown */}
                        <FormField
                            control={form.control}
                            name="categoryId"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Category</FormLabel>
                                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select a category for your event"/>
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent className="max-h-100">
                                            {categories.map((parentCat) => (
                                                <SelectGroup key={parentCat.id}>
                                                    <SelectLabel>{parentCat.name}</SelectLabel>
                                                    {parentCat.subCategories.map((subCat) => (
                                                        <SelectItem key={subCat.id} value={subCat.id}>
                                                            {subCat.name}
                                                        </SelectItem>
                                                    ))}
                                                </SelectGroup>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <FormField control={form.control} name="description" render={({field}) => (
                            <FormItem><FormLabel>Short Description</FormLabel><FormControl><Textarea
                                placeholder="A brief summary of your event." {...field} /></FormControl><FormMessage/></FormItem>)}/>
                        <FormField control={form.control} name="overview" render={({field}) => (
                            <FormItem><FormLabel>Overview</FormLabel><FormControl><Textarea
                                placeholder="Provide more details like schedule, speakers, etc."
                                className="min-h-32" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    </CardContent>
                </Card>
                <div className="flex justify-end mt-8">
                    <Button type="submit">Next: Tiers & Pricing</Button>
                </div>
            </form>
        </FormProvider>
    )
}

'use client';

import * as React from 'react';
import {format, parseISO} from 'date-fns';
import {Calendar, MapPin, Tag, Users, Clock, Armchair, LinkIcon} from 'lucide-react';
import {
    Accordion,
    AccordionContent,
    AccordionItem,
    AccordionTrigger,
} from "@/components/ui/accordion";
import {Badge} from '@/components/ui/badge';
import {
    SessionFormData,
    SalesStartRuleType,
    SessionType,
    Seat,
    Tier
} from '@/lib/validators/event';
import {
    Popover,
    PopoverContent,
    PopoverTrigger
} from '@/components/ui/popover';
import {Button} from '@/components/ui/button';
import {GoogleMap, useJsApiLoader, Marker} from '@react-google-maps/api';

interface ReviewSessionsProps {
    sessions: SessionFormData[];
    tiers: Tier[];
}

export const ReviewSessions: React.FC<ReviewSessionsProps> = ({sessions, tiers}) => {
    if (sessions.length === 0) return null;

    return (
        <div className="space-y-6">
            <h2 className="text-2xl font-semibold">Schedule</h2>
            <Accordion type="single" collapsible className="w-full">
                {sessions.map((session, index) => (
                    <SessionAccordionItem
                        key={`session-${index}`}
                        session={session}
                        index={index}
                        tiers={tiers}
                    />
                ))}
            </Accordion>
        </div>
    );
};

interface SessionAccordionItemProps {
    session: SessionFormData;
    tiers: Tier[];
    index: number;
}

const SessionAccordionItem: React.FC<SessionAccordionItemProps> = ({session, index, tiers}) => {
    const startDate = parseISO(session.startTime);
    const endDate = parseISO(session.endTime);
    const isOnline = session.sessionType === SessionType.ONLINE;
    const {layoutData} = session;

    return (
        <AccordionItem
            value={`item-${index}`}
            className="border rounded-lg mb-4 overflow-hidden"
        >
            <AccordionTrigger className="px-4 py-3 hover:no-underline hover:bg-muted/40">
                <div className="flex items-center gap-3 text-left">
                    <Calendar className="h-4 w-4 text-muted-foreground"/>
                    <div>
                        <div className="font-medium">
                            {format(startDate, "EEEE, MMMM d, yyyy")}
                        </div>
                        <div className="text-sm text-muted-foreground">
                            {format(startDate, "h:mm a")} - {format(endDate, "h:mm a")}
                        </div>
                    </div>
                    <Badge variant={isOnline ? "secondary" : "default"} className="ml-4">
                        {isOnline ? 'Online' : 'Physical'}
                    </Badge>
                </div>
            </AccordionTrigger>
            <AccordionContent className="px-4 pt-2 pb-4">
                <div className="grid grid-cols-1 gap-6">
                    <SessionDetails session={session}/>
                    {layoutData && layoutData.layout.blocks.length > 0 && (
                        <SeatingInformation
                            isOnline={isOnline}
                            session={session}
                            tiers={tiers}
                        />
                    )}
                </div>
            </AccordionContent>
        </AccordionItem>
    );
};

interface SessionDetailsProps {
    session: SessionFormData;
}

const SessionDetails: React.FC<SessionDetailsProps> = ({session}) => {
    const startDate = parseISO(session.startTime);
    const endDate = parseISO(session.endTime);
    const isOnline = session.sessionType === SessionType.ONLINE;
    const {venueDetails} = session;

    // Calculate event duration
    const getDuration = (): string => {
        try {
            const durationMs = endDate.getTime() - startDate.getTime();
            const hours = Math.floor(durationMs / (1000 * 60 * 60));
            const minutes = Math.floor((durationMs % (1000 * 60 * 60)) / (1000 * 60));

            return hours > 0
                ? `${hours} hour${hours !== 1 ? 's' : ''}${minutes > 0 ? ` ${minutes} min` : ''}`
                : `${minutes} minutes`;
        } catch (e) {
            console.error("Error calculating duration:", e);
            return "Duration unavailable";
        }
    };

    // Sales rule description
    const getSalesRuleDescription = (): string => {
        switch (session.salesStartRuleType) {
            case SalesStartRuleType.IMMEDIATE:
                return "On sale immediately";
            case SalesStartRuleType.FIXED:
                return session.salesStartFixedDatetime
                    ? `Sales start on ${format(parseISO(session.salesStartFixedDatetime), 'MMM d, yyyy h:mm a')}`
                    : "Fixed date not set";
            case SalesStartRuleType.ROLLING:
                if (session.salesStartHoursBefore === undefined || session.salesStartHoursBefore === null || session.salesStartHoursBefore < 0) {
                    return "Rolling hours not set or invalid";
                } else if (session.salesStartHoursBefore < 24) {
                    return `Sales start ${session.salesStartHoursBefore} hour(s) before the session`;
                } else {
                    const days = Math.floor(session.salesStartHoursBefore / 24);
                    const hours = session.salesStartHoursBefore % 24;
                    return `Sales start ${days} day(s)${hours > 0 ? ` and ${hours} hour(s)` : ''} before the session`;
                }
            default:
                return "Not set";
        }
    };

    return (
        <div className="space-y-4">
            <div className="flex items-center gap-2">
                <Clock className="h-4 w-4 text-muted-foreground"/>
                <span>Duration: {getDuration()}</span>
            </div>

            <div className="flex items-start gap-2">
                {isOnline ? (
                    <>
                        <LinkIcon className="h-4 w-4 text-muted-foreground mt-0.5"/>
                        <div>
                            <div>Online Event</div>
                            {venueDetails?.onlineLink ? (
                                <div className="text-sm text-muted-foreground break-all">
                                    {venueDetails.onlineLink}
                                </div>
                            ) : (
                                <div className="text-sm text-destructive">
                                    Link not provided
                                </div>
                            )}
                        </div>
                    </>
                ) : (
                    <>
                        <MapPin className="h-4 w-4 text-muted-foreground mt-0.5"/>
                        <div className="w-full">
                            {venueDetails?.name ? (
                                <>
                                    <div>{venueDetails.name}</div>
                                    {venueDetails.address && (
                                        <div className="text-sm text-muted-foreground mb-2">
                                            {venueDetails.address}
                                        </div>
                                    )}
                                </>
                            ) : (
                                <div className="text-destructive">
                                    Venue not specified
                                </div>
                            )}
                        </div>
                    </>
                )}
            </div>

            <div className="flex items-center gap-2">
                <Tag className="h-4 w-4 text-muted-foreground"/>
                <span>{getSalesRuleDescription()}</span>
            </div>
        </div>
    );
};

interface SeatingInformationProps {
    isOnline: boolean;
    session: SessionFormData;
    tiers: Tier[];
}

const SeatingInformation: React.FC<SeatingInformationProps> = ({isOnline, session, tiers}) => {
    const {layoutData} = session;
    const {venueDetails} = session;


    // For physical events with coordinates, prepare Google Map
    const {isLoaded} = useJsApiLoader({
        id: 'google-map-script',
    const googleMapsApiKey = process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY;
    if (!googleMapsApiKey) {
        throw new Error('Google Maps API key is missing. Please set NEXT_PUBLIC_GOOGLE_MAPS_API_KEY in your environment.');
    }
    const {isLoaded} = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey,
    });

    const mapCenter = venueDetails?.latitude && venueDetails?.longitude
        ? {lat: venueDetails.latitude, lng: venueDetails.longitude}
        : {lat: 6.9271, lng: 79.8612}; // Default: Colombo, Sri Lanka

    // Function to count seats by tier
    const getSeatCountByTier = () => {
        const tierCounts: Record<string, number> = {};

        layoutData.layout.blocks.forEach(block => {
            if (block.rows) {
                // For seated blocks with rows
                block.rows.forEach(row => {
                    row.seats.forEach(seat => {
                        if (seat.status !== 'RESERVED') {
                            const tierId = seat.tierId || 'unassigned';
                            tierCounts[tierId] = (tierCounts[tierId] || 0) + 1;
                        }
                    });
                });
            } else if (block.seats) {
                // For blocks with direct seats array
                block.seats.forEach(seat => {
                    if (seat.status !== 'RESERVED') {
                        const tierId = seat.tierId || 'unassigned';
                        tierCounts[tierId] = (tierCounts[tierId] || 0) + 1;
                    }
                });
            } else if (block.capacity && block.type === 'standing_capacity') {
                // For standing blocks
                // Fix the TypeScript error by explicitly typing the block.seats access
                const blockSeats = block.seats as Seat[] | undefined;
                const tierId = blockSeats?.[0]?.tierId || 'unassigned';
                tierCounts[tierId] = (tierCounts[tierId] || 0) + (block.capacity || 0);
            }
        });

        return tierCounts;
    };

    const seatCountByTier = getSeatCountByTier();
    const totalSeats = Object.values(seatCountByTier).reduce((sum, count) => sum + count, 0);

    // Display for online events
    if (isOnline) {
        return (
            <div className="bg-muted/30 p-4 rounded-lg">
                <div className="flex items-start gap-2 mb-2">
                    <Users className="h-4 w-4 text-muted-foreground mt-0.5"/>
                    <span className="font-medium">Capacity Information</span>
                </div>
                <div className="text-sm ml-6">
                    <p>Total capacity: {totalSeats}</p>
                    {Object.entries(seatCountByTier).map(([tierId, count]) => (
                        <div key={tierId} className="flex items-center gap-2 mt-1">
                            {tierId !== 'unassigned' && (
                                <div
                                    className="h-3 w-3 rounded-full"
                                    style={{
                                        backgroundColor: getTierColor(tierId, session, tiers)
                                    }}
                                />
                            )}
                            <span>
                                {getTierName(tierId, session, tiers)}: {count} {count === 1 ? 'seat' : 'seats'}
                            </span>
                        </div>
                    ))}
                </div>
            </div>
        );
    }

    // Display for physical events - side by side layout
    return (
        <div className="space-y-4">
            <h3 className="font-semibold">Venue & Seating Information</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {/* Left side: Google Map */}
                <div className="h-[300px] rounded-md overflow-hidden border">
                    {isLoaded ? (
                        <GoogleMap
                            mapContainerStyle={{
                                width: '100%',
                                height: '100%',
                            }}
                            center={mapCenter}
                            zoom={15}
                        >
                            <Marker position={mapCenter}/>
                        </GoogleMap>
                    ) : (
                        <div className="h-full w-full bg-muted flex items-center justify-center">
                            <p className="text-sm text-muted-foreground">Loading map...</p>
                        </div>
                    )}
                </div>

                {/* Right side: Seating summary */}
                <div className="space-y-4">
                    <div className="bg-muted/30 p-4 rounded-lg">
                        <div className="flex items-start gap-2 mb-2">
                            <Armchair className="h-4 w-4 text-muted-foreground mt-0.5"/>
                            <span className="font-medium">Seating Summary</span>
                        </div>
                        <div className="text-sm ml-6">
                            <p>Total capacity: {totalSeats}</p>
                            {Object.entries(seatCountByTier).map(([tierId, count]) => (
                                <div key={tierId} className="flex items-center gap-2 mt-1">
                                    {tierId !== 'unassigned' && (
                                        <div
                                            className="h-3 w-3 rounded-full"
                                            style={{
                                                backgroundColor: getTierColor(tierId, session, tiers)
                                            }}
                                        />
                                    )}
                                    <span>
                                        {getTierName(tierId, session, tiers)}: {count} {count === 1 ? 'seat' : 'seats'}
                                    </span>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Seating Layout below the map and summary */}
            {layoutData && (
                <SeatingLayout session={session} tiers={tiers}/>
            )}
        </div>
    );
};

// Helper to get tier color
const getTierColor = (tierId: string, session: SessionFormData, tiers: Tier[]): string => {
    if (tierId === 'unassigned') return '#d1d5db'; // gray-300

    // We need to check if tiers exist in the session
    const tier = tiers.find(t => t.id === tierId);
    return tier?.color || '#6b7280'; // gray-500 as fallback
};

// Helper to get tier name
const getTierName = (tierId: string, session: SessionFormData, tiers: Tier[]): string => {
    if (tierId === 'unassigned') return 'Unassigned';

    const tier = tiers.find(t => t.id === tierId);
    return tier?.name || 'Unknown Tier';
};

interface SeatingLayoutProps {
    session: SessionFormData;
    tiers: Tier[];
}

const SeatingLayout: React.FC<SeatingLayoutProps> = ({session, tiers}) => {
    // const [selectedSeat, setSelectedSeat] = useState<{
    //     seat: Seat;
    //     rowLabel?: string;
    //     blockName: string;
    //     tier?: string;
    // } | null>(null);

    const {layoutData} = session;

    // Only render for physical events with layout data
    if (!layoutData || session.sessionType !== SessionType.PHYSICAL) return null;

    // const handleSeatClick = (
    //     seat: Seat,
    //     blockName: string,
    //     rowLabel?: string
    // ) => {
    //     setSelectedSeat({
    //         seat,
    //         rowLabel,
    //         blockName,
    //         tier: seat.tierId ? getTierName(seat.tierId, session, tiers) : undefined
    //     });
    // };

    return (
        <div className="border rounded-lg p-4">
            <h3 className="font-semibold mb-4">Seating Layout</h3>
            <div className="relative bg-muted/30 min-h-[300px] p-4 rounded-lg overflow-auto">
                {layoutData.layout.blocks.map(block => (
                    <div
                        key={block.id}
                        className="absolute bg-card border rounded-lg p-3 shadow-sm"
                        style={{
                            left: block.position.x,
                            top: block.position.y,
                            width: block.width ? `${block.width}px` : 'auto',
                            height: block.height ? `${block.height}px` : 'auto'
                        }}
                    >
                        <div className="text-sm font-medium mb-1">{block.name}</div>

                        {block.type === 'seated_grid' && block.rows && (
                            <div
                                className="grid gap-1"
                                style={{
                                    gridTemplateColumns: `repeat(${block.rows[0]?.seats?.length || 1}, 1fr)`
                                }}
                            >
                                {block.rows.map(row =>
                                    row.seats.map(seat => (
                                        <Popover key={seat.id}>
                                            <PopoverTrigger asChild>
                                                <Button
                                                    type="button"
                                                    variant="ghost"
                                                    className="h-6 w-6 p-0 rounded-full text-xs font-mono"
                                                    style={{
                                                        backgroundColor: seat.tierId ?
                                                            `${getTierColor(seat.tierId, session, tiers)}80` : // 50% opacity
                                                            undefined,
                                                        opacity: seat.status === 'RESERVED' ? 0.3 : 1
                                                    }}
                                                    // onClick={() => handleSeatClick(seat, block.name, row.label)}
                                                >
                                                    {seat.label}
                                                </Button>
                                            </PopoverTrigger>
                                            <PopoverContent side="top" className="w-60 p-0">
                                                <div className="p-4">
                                                    <div className="font-semibold mb-2">Seat Information</div>
                                                    <div className="space-y-2 text-sm">
                                                        <div className="flex justify-between">
                                                            <span className="text-muted-foreground">Block:</span>
                                                            <span>{block.name}</span>
                                                        </div>
                                                        <div className="flex justify-between">
                                                            <span className="text-muted-foreground">Row:</span>
                                                            <span>{row.label}</span>
                                                        </div>
                                                        <div className="flex justify-between">
                                                            <span className="text-muted-foreground">Seat:</span>
                                                            <span>{seat.label}</span>
                                                        </div>
                                                        <div className="flex justify-between">
                                                            <span className="text-muted-foreground">Status:</span>
                                                            <Badge
                                                                variant={seat.status === 'RESERVED' ? 'destructive' : 'outline'}>
                                                                {seat.status || 'Available'}
                                                            </Badge>
                                                        </div>
                                                        <div className="flex justify-between">
                                                            <span className="text-muted-foreground">Tier:</span>
                                                            <div className="flex items-center gap-1">
                                                                {seat.tierId && (
                                                                    <div
                                                                        className="h-3 w-3 rounded-full"
                                                                        style={{
                                                                            backgroundColor: getTierColor(seat.tierId, session, tiers)
                                                                        }}
                                                                    />
                                                                )}
                                                                <span>
                                                                    {seat.tierId ? getTierName(seat.tierId, session, tiers) : 'Unassigned'}
                                                                </span>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </PopoverContent>
                                        </Popover>
                                    ))
                                )}
                            </div>
                        )}

                        {block.type === 'standing_capacity' && (
                            <div className="flex items-center justify-center h-full">
                                <p className="text-sm text-center">
                                    <span className="block font-medium">Standing Area</span>
                                    <span className="text-muted-foreground">
                                        Capacity: {block.capacity || 0}
                                    </span>
                                </p>
                            </div>
                        )}

                        {block.type === 'non_sellable' && (
                            <div className="flex items-center justify-center h-full">
                                <p className="text-sm text-muted-foreground">
                                    Non-sellable area
                                </p>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

'use client';

import * as React from 'react';
import {format, parseISO} from 'date-fns';
import {Calendar, MapPin, Tag, Users, Clock, Armchair, LinkIcon} from 'lucide-react';
import {
    Accordion,
    AccordionContent,
    AccordionItem,
    AccordionTrigger,
} from "@/components/ui/accordion";
import {Badge} from '@/components/ui/badge';
