"use client";

import * as React from "react";
import {useState, useEffect} from "react";
import {useParams} from "next/navigation";
import {toast} from "sonner";
import {getMyEventById} from "@/lib/actions/eventActions";
import {EventDetailDTO} from "@/lib/validators/event";
import {Skeleton} from "@/components/ui/skeleton";
import {EventStatusTracker} from "../_components/EventStatusTracker";
import {EventPreview} from "@/app/manage/_components/review/EventPreview";
import {Separator} from "@/components/ui/separator";
import {getMyOrganizationById} from "@/lib/actions/organizationActions";
import {OrganizationResponse} from "@/types/oraganizations";

export default function EventDetailsPage() {
    const params = useParams();
    const eventId = params.eventId as string;

    const [event, setEvent] = useState<EventDetailDTO | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [organization, setOrganization] = useState<OrganizationResponse | null>(null);

    // Load event data
    useEffect(() => {
        console.log("Fetching event data for ID:", eventId);
        if (eventId) {
            setIsLoading(true);
            getMyEventById(eventId)
                .then((eventData) => {
                    setEvent(eventData);
                    // After we have event data, fetch organization details
                    if (eventData && eventData.organizationId) {
                        return getMyOrganizationById(eventData.organizationId);
                    }
                    return null;
                })
                .then((orgData) => {
                    if (orgData) {
                        setOrganization(orgData);
                    }
                })
                .catch((error) => {
                    console.error("Error fetching data:", error);
                    toast.error(error.message || "Failed to load event details.");
                })
                .finally(() => {
                    setIsLoading(false);
                });
        }
    }, [eventId]); // Only depend on eventId, not the event state itself

    if (isLoading) {
        return (
            <div className="p-4 md:p-8 max-w-4xl mx-auto space-y-8">
                <Skeleton className="h-24 w-full"/>
                <Skeleton className="h-48 w-full"/>
                <Skeleton className="h-32 w-full"/>
            </div>
        );
    }

    if (!organization || !event) {
        return (
            <>
                <div className="p-4 md:p-8 max-w-4xl mx-auto space-y-8">
                    <h1 className="text-2xl font-semibold">Event Not Found</h1>
                </div>
            </>
        );
    }

    return (
        <div className="space-y-8 max-w-5xl mx-auto p-4 md:p-8">
            <EventStatusTracker status={event.status} rejectionReason={event.rejectionReason}/>
            <Separator className="my-6"/>
            <EventPreview event={event} organization={organization}/>

            {/* Sales Summary Section */}
            {/*<Card className="p-6">*/}
            {/*    <h2 className="text-xl font-semibold mb-4">Sales Summary</h2>*/}
            {/*    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">*/}
            {/*        <div className="bg-muted p-4 rounded-lg text-center">*/}
            {/*            <p className="text-muted-foreground text-sm">Total Sales</p>*/}
            {/*            <p className="text-2xl font-bold">$0</p>*/}
            {/*        </div>*/}
            {/*        <div className="bg-muted p-4 rounded-lg text-center">*/}
            {/*            <p className="text-muted-foreground text-sm">Tickets Sold</p>*/}
            {/*            <p className="text-2xl font-bold">0</p>*/}
            {/*        </div>*/}
            {/*        <div className="bg-muted p-4 rounded-lg text-center">*/}
            {/*            <p className="text-muted-foreground text-sm">Remaining</p>*/}
            {/*            <p className="text-2xl font-bold">0</p>*/}
            {/*        </div>*/}
            {/*    </div>*/}
            {/*</Card>*/}

            {/* Sessions Schedule Section */}

            {/* Analytics Section */}
            {/*<Card className="p-6">*/}
            {/*    <h2 className="text-xl font-semibold mb-4">Event Performance</h2>*/}
            {/*    <div className="text-center p-12 bg-muted rounded-lg">*/}
            {/*        <p className="text-muted-foreground">Analytics features will be available once the event is*/}
            {/*            published.</p>*/}
            {/*    </div>*/}
            {/*</Card>*/}
        </div>
    );
}

"use client";

import * as React from "react";
import {useState, useEffect} from "react";
import {useParams} from "next/navigation";
import {toast} from "sonner";
import {getMyEventById} from "@/lib/actions/eventActions";
import {EventDetailDTO} from "@/lib/validators/event";
import {Skeleton} from "@/components/ui/skeleton";
import {EventStatusTracker} from "../_components/EventStatusTracker";
import {EventPreview} from "@/app/manage/_components/review/EventPreview";
import {Separator} from "@/components/ui/separator";
import {getMyOrganizationById} from "@/lib/actions/organizationActions";
import {OrganizationResponse} from "@/types/oraganizations";
import {Card} from "@/components/ui/card";

export default function EventDetailsPage() {
    const params = useParams();
    const eventId = params.eventId as string;

    const [event, setEvent] = useState<EventDetailDTO | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [organization, setOrganization] = useState<OrganizationResponse | null>(null);

    // Load event data
    useEffect(() => {
        console.log("Fetching event data for ID:", eventId);
        if (eventId) {
            setIsLoading(true);
            getMyEventById(eventId)
                .then((eventData) => {
                    setEvent(eventData);
                    // After we have event data, fetch organization details
                    if (eventData && eventData.organizationId) {
                        return getMyOrganizationById(eventData.organizationId);
                    }
                    return null;
                })
                .then((orgData) => {
                    if (orgData) {
                        setOrganization(orgData);
                    }
                })
                .catch((error) => {
                    console.error("Error fetching data:", error);
                    toast.error(error.message || "Failed to load event details.");
                })
                .finally(() => {
                    setIsLoading(false);
                });
        }
    }, [eventId]); // Only depend on eventId, not the event state itself

    if (isLoading) {
        return (
            <div className="p-4 md:p-8 grid grid-cols-1 lg:grid-cols-3 gap-8 w-full">
                <div className="lg:col-span-2 space-y-8">
                    <Skeleton className="h-64 w-full"/>
                    <Skeleton className="h-48 w-full"/>
                </div>
                <div className="lg:col-span-1 space-y-8">
                    <Skeleton className="h-48 w-full"/>
                    <Skeleton className="h-64 w-full"/>
                </div>
            </div>
        );
    }

    if (!organization || !event) {
        return (
            <div className="p-8 text-center w-full">Event Not Found</div>
        );
    }

    return (
        <div className="p-4 md:p-8 grid grid-cols-1 lg:grid-cols-3 gap-8 items-start w-full">
            {/* Main Content - The Event Preview */}
            <div className="lg:col-span-2">
                <EventStatusTracker status={event.status} rejectionReason={event.rejectionReason}/>
                <Separator className="my-6"/>
                <EventPreview
                    event={event}
                    organization={organization}
                />
            </div>

            {/* Sidebar - Fixed on scroll */}
            <div className="lg:col-span-1 space-y-6 lg:sticky lg:top-24 lg:self-start max-h-[calc(100vh-120px)] overflow-y-auto">
                <Card className="p-6">
                    <h2 className="text-xl font-semibold mb-4">Sales Summary</h2>
                    <div className="grid grid-cols-1 gap-4">
                        <div className="bg-muted p-4 rounded-lg text-center">
                            <p className="text-muted-foreground text-sm">Total Sales</p>
                            <p className="text-2xl font-bold">$0</p>
                        </div>
                        <div className="bg-muted p-4 rounded-lg text-center">
                            <p className="text-muted-foreground text-sm">Tickets Sold</p>
                            <p className="text-2xl font-bold">0</p>
                        </div>
                        <div className="bg-muted p-4 rounded-lg text-center">
                            <p className="text-muted-foreground text-sm">Remaining</p>
                            <p className="text-2xl font-bold">0</p>
                        </div>
                    </div>
                </Card>
                <Card className="p-6">
                    <h2 className="text-xl font-semibold mb-4">Event Performance</h2>
                    <div className="text-center p-4 bg-muted rounded-lg">
                        <p className="text-muted-foreground">Analytics features will be available once the event is published.</p>
                    </div>
                </Card>
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useDraggable} from '@dnd-kit/core';
import {Block, Tier} from '@/lib/validators/event';
import {cn} from '@/lib/utils';
import {Button} from '@/components/ui/button';

// --- Interactive Draggable Block for Seated Grids ---
interface DraggableBlockProps {
    block: Block;
    tiers: Tier[];
    onSeatClick: (blockId: string, rowId: string, seatId: string) => void;
    onApplyToAllSeats?: (blockId: string) => void;  // New prop for handling apply to all
}

export function InteractiveDraggableBlock({block, tiers, onSeatClick, onApplyToAllSeats}: DraggableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true, // Dragging is disabled in assignment mode
    });

    const style = {
        // No transform needed as blocks are static in this editor
        left: block.position.x,
        top: block.position.y,
    };

    const getTierColor = (tierId?: string) => {
        if (tierId === 'RESERVED') return 'hsla(var(--muted-foreground), 0.5)'; // Added opacity
        if (!tierId) return undefined;
        const tier = tiers.find(t => t.id === tierId);
        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return 'hsla(var(--primary), 0.5)'; // Added opacity
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute p-3 bg-card border rounded-lg">
            <div className="flex flex-col text-center relative">
                <div className="flex items-center justify-between w-full mb-2">
                    <span className="text-sm font-medium">{block.name}</span>
                    {onApplyToAllSeats && (
                        <Button
                            size="sm"
                            variant="outline"
                            type={'button'}
                            className="text-xs py-0 h-6"
                            onClick={(e) => {
                                e.stopPropagation();
                                onApplyToAllSeats(block.id);
                            }}
                        >
                            Apply to All
                        </Button>
                    )}
                </div>
                <div className="grid gap-1.5"
                     style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, 1fr)`}}>
                    {block.rows?.map(row =>
                        row.seats.map(seat => (
                            <button
                                key={seat.id}
                                type="button"
                                onClick={() => onSeatClick(block.id, row.id, seat.id)}
                                className={cn(
                                    "h-6 w-6 rounded-full border text-xs font-mono transition-all hover:scale-110 flex items-center justify-center",
                                    seat.status === 'RESERVED' && "bg-muted-foreground/50 text-primary-foreground line-through border-destructive border-2"
                                )}
                                style={{backgroundColor: seat.tierId && seat.status !== 'RESERVED' ? getTierColor(seat.tierId) : undefined}}
                            >
                                {seat.label}
                            </button>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
}

// --- Interactive Resizable Block for Standing Capacity ---
interface ResizableBlockProps {
    block: Block;
    tiers: Tier[];
    onClick: (blockId: string) => void;
}

export function InteractiveResizableBlock({block, tiers, onClick}: ResizableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true,
    });

    const style = {
        left: block.position.x,
        top: block.position.y,
        width: block.width,
        height: block.height,
    };

    // Determine the background color based on the tier of the seats inside the block
    // with 50% opacity
    const getBlockTierColor = () => {
        const firstTieredSeat = block.seats?.find(s => s.tierId);
        if (!firstTieredSeat || !firstTieredSeat.tierId) return undefined;
        const tier = tiers.find(t => t.id === firstTieredSeat.tierId);

        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return undefined;
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute">
            <div
                onClick={() => onClick(block.id)}
                className="p-2 bg-card border rounded-lg shadow-md box-border flex flex-col relative cursor-pointer h-full w-full"
                style={{backgroundColor: getBlockTierColor()}}
            >
                <div className="flex items-center gap-2 flex-grow">
                    <div className="flex flex-col text-center flex-grow">
                        <span className="text-sm font-medium">{block.name}</span>
                        <span className="text-xs text-muted-foreground">{block.capacity} capacity</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

