// --- Interactive Resizable Block for Standing Capacity ---
interface ResizableBlockProps {
    block: Block;
    tiers: Tier[];
    onClick: (blockId: string) => void;
}

export function InteractiveResizableBlock({block, tiers, onClick}: ResizableBlockProps) {
    const {setNodeRef} = useDraggable({
        id: block.id,
        disabled: true,
    });

    // Fix the TypeScript error by ensuring width and height are valid CSS values
    const style: React.CSSProperties = {
        left: block.position.x,
        top: block.position.y,
        width: block.width ?? undefined,
        height: block.height ?? undefined,
    };

    // Determine the background color based on the tier of the seats inside the block
    // with 50% opacity
    const getBlockTierColor = () => {
        const firstTieredSeat = block.seats?.find(s => s.tierId);
        if (!firstTieredSeat || !firstTieredSeat.tierId) return undefined;
        const tier = tiers.find(t => t.id === firstTieredSeat.tierId);

        // Add opacity to the color
        if (tier?.color) {
            // Handle HSL color format
            if (tier.color.startsWith('hsl')) {
                return tier.color.replace('hsl', 'hsla').replace(')', ', 0.5)');
            }
            // Handle hex or other formats by adding opacity suffix
            return `${tier.color}80`; // 80 is equivalent to 50% opacity in hex
        }
        return undefined;
    };

    return (
        <div ref={setNodeRef} style={style} className="absolute">
            <div
                onClick={() => onClick(block.id)}
                className="p-2 bg-card border rounded-lg shadow-md box-border flex flex-col relative cursor-pointer h-full w-full"
                style={{backgroundColor: getBlockTierColor()}}
            >
                <div className="flex items-center gap-2 flex-grow">
                    <div className="flex flex-col text-center flex-grow">
                        <span className="text-sm font-medium">{block.name}</span>
                        <span className="text-xs text-muted-foreground">{block.capacity} capacity</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData} from '@/lib/validators/event';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {
    SessionListItemSeating
} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({ onConfigModeChange }: SeatingStepProps) {
    const { control, formState: { errors }, watch, getValues, setValue } = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const { fields } = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: any) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-4">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div> {/* Empty div for flexbox spacing */}
                </div>

                <Card>
                    <CardHeader>
                        <CardTitle>
                            {currentSession.isOnline
                                ? "Online Capacity Configuration"
                                : "Physical Seating Configuration"}
                        </CardTitle>
                        <CardDescription>
                            {currentSession.isOnline
                                ? "Set the capacity and ticket tier for your online event."
                                : "Choose a layout template or create a new one, then assign your tiers."}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        {currentSession.isOnline ? (
                            <OnlineConfigView onSave={handleSave} />
                        ) : (
                            <PhysicalConfigView onSave={handleSave} />
                        )}
                    </CardContent>
                </Card>

                <div className="flex items-center justify-between border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <Card>
            <CardHeader>
                <CardTitle>Seating & Tier Assignment</CardTitle>
                <CardDescription>
                    Configure the seating layout and assign ticket tiers for each session.
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData} from '@/lib/validators/event';
import {SessionListItemSeating} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({ onConfigModeChange }: SeatingStepProps) {
    const { control, formState: { errors }, watch, getValues, setValue } = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const { fields } = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: any) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-6">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div> {/* Empty div for flexbox spacing */}
                </div>

                <div className="space-y-2">
                    <h3 className="text-lg font-medium">
                        {currentSession.isOnline
                            ? "Online Capacity Configuration"
                            : "Physical Seating Configuration"}
                    </h3>
                    <p className="text-muted-foreground text-sm">
                        {currentSession.isOnline
                            ? "Set the capacity and ticket tier for your online event."
                            : "Choose a layout template or create a new one, then assign your tiers."}
                    </p>
                </div>

                <div className="border rounded-md p-6 bg-card">
                    {currentSession.isOnline ? (
                        <OnlineConfigView onSave={handleSave} />
                    ) : (
                        <PhysicalConfigView onSave={handleSave} />
                    )}
                </div>

                <div className="flex items-center border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <div className="space-y-6">
            <div>
                <h2 className="text-xl font-semibold mb-1">Seating & Tier Assignment</h2>
                <p className="text-sm text-muted-foreground">
                    Configure the seating layout and assign ticket tiers for each session.
                </p>
            </div>

            <div className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect, useRef} from 'react';
import {useForm, FormProvider} from 'react-hook-form';
import {zodResolver} from '@hookform/resolvers/zod';
import {useOrganization} from '@/providers/OrganizationProvider';
import {useLimits} from '@/providers/LimitProvider';
import {CategoryResponse} from '@/types/category';
import {getAllCategories} from '@/lib/actions/categoryActions';
import {coreDetailsSchema, CoreDetailsData} from '@/lib/validators/event';

import {Button} from '@/components/ui/button';
import {Input} from '@/components/ui/input';
import {Textarea} from '@/components/ui/textarea';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {FormControl, FormField, FormItem, FormLabel, FormMessage} from '@/components/ui/form';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
    SelectGroup,
    SelectLabel
} from '@/components/ui/select';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Image from 'next/image';
import {ImageIcon, X, PlusCircle, Upload} from 'lucide-react';
import {toast} from 'sonner';
import Autoplay from 'embla-carousel-autoplay';

// --- Step 1: Core Details Component ---
export function CoreDetailsStep({onNextAction}: { onNextAction: () => void }) {
    const {organization} = useOrganization();
    const {myLimits} = useLimits();
    const [categories, setCategories] = useState<CategoryResponse[]>([]);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const fileInputRef = useRef<HTMLInputElement>(null);

    const maxPhotos = myLimits?.eventLimits.maxCoverPhotos || 1;

    const form = useForm<CoreDetailsData>({
        resolver: zodResolver(coreDetailsSchema),
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            categoryId: '', // Ensure categoryId is present
            isOnline: false,
        },
    });

    useEffect(() => {
        getAllCategories().then(setCategories);
    }, [organization]);

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files) {
            const files = Array.from(e.target.files);
            if ((coverFiles.length + files.length) > maxPhotos) {
                toast.error(`You can only upload a maximum of ${maxPhotos} photos.`);
                return;
            }
            setCoverFiles(prev => [...prev, ...files]);
        }
    };

    const removeImage = (index: number) => {
        setCoverFiles(prev => prev.filter((_, i) => i !== index));
    };

    function onSubmit(data: CoreDetailsData) {
        console.log("Step 1 Data:", data);
        console.log("Cover Files:", coverFiles);
        onNextAction();
    }

    return (
        <FormProvider {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
                {/* Hero Cover Photos Section */}
                <Card>
                    <CardHeader>
                        <CardTitle>Cover Photos</CardTitle>
                        <CardDescription>This is the first thing people will see. Upload up
                            to {maxPhotos} images.</CardDescription>
                    </CardHeader>
                    <CardContent>
                        {coverFiles.length > 0 ? (
                            <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                                <CarouselContent>
                                    {coverFiles.map((file, index) => (
                                        <CarouselItem key={index} className="relative">
                                            <div className="aspect-[21/9] w-full relative">
                                                <Image src={URL.createObjectURL(file)} alt={`Cover photo ${index + 1}`}
                                                       fill className="object-cover rounded-lg"/>
                                                <Button type="button" variant="destructive" size="icon"
                                                        className="absolute top-4 right-4 h-8 w-8 z-10"
                                                        onClick={() => removeImage(index)}><X
                                                    className="h-4 w-4"/></Button>
                                            </div>
                                        </CarouselItem>
                                    ))}
                                </CarouselContent>
                                <CarouselPrevious type="button"
                                                  className="absolute left-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                                <CarouselNext type="button"
                                              className="absolute right-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                            </Carousel>
                        ) : (
                            <div
                                className="flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-12 text-center aspect-[21/9] w-full">
                                <ImageIcon className="h-16 w-16 text-muted-foreground"/>
                                <p className="mt-4 text-lg font-medium">Add cover photos for your event</p>
                                <Button type="button" className="mt-4"
                                        onClick={() => fileInputRef.current?.click()}><Upload className="mr-2 h-4 w-4"/>Upload
                                    Images</Button>
                            </div>
                        )}
                        <div className="mt-4 flex justify-center items-center gap-4">
                            {coverFiles.length > 0 && (<Button type="button" variant="outline"
                                                               onClick={() => fileInputRef.current?.click()}><PlusCircle
                                className="mr-2 h-4 w-4"/>Add More</Button>)}
                            <p className="text-sm text-muted-foreground">{coverFiles.length} of {maxPhotos} photos
                                selected.</p>
                        </div>
                        <Input id="picture" type="file" multiple accept="image/*" onChange={handleFileChange}
                               className="hidden" ref={fileInputRef}/>
                    </CardContent>
                </Card>

                {/* Event Details Card with Category dropdown */}
                <Card>
                    <CardHeader>
                        <CardTitle>Event Details</CardTitle>
                        <CardDescription>Provide the core information about your event.</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                        <FormField control={form.control} name="title" render={({field}) => (
                            <FormItem><FormLabel>Event Title</FormLabel><FormControl><Input
                                placeholder="e.g., Annual Tech Conference 2025" {...field} /></FormControl><FormMessage/></FormItem>)}/>

                        {/* Category Selector as dropdown */}
                        <FormField
                            control={form.control}
                            name="categoryId"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Category</FormLabel>
                                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select a category for your event"/>
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent className="max-h-100">
                                            {categories.map((parentCat) => (
                                                <SelectGroup key={parentCat.id}>
                                                    <SelectLabel>{parentCat.name}</SelectLabel>
                                                    {parentCat.subCategories.map((subCat) => (
                                                        <SelectItem key={subCat.id} value={subCat.id}>
                                                            {subCat.name}
                                                        </SelectItem>
                                                    ))}
                                                </SelectGroup>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <FormField control={form.control} name="description" render={({field}) => (
                            <FormItem><FormLabel>Short Description</FormLabel><FormControl><Textarea
                                placeholder="A brief summary of your event." {...field} /></FormControl><FormMessage/></FormItem>)}/>
                        <FormField control={form.control} name="overview" render={({field}) => (
                            <FormItem><FormLabel>Overview</FormLabel><FormControl><Textarea
                                placeholder="Provide more details like schedule, speakers, etc."
                                className="min-h-32" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    </CardContent>
                </Card>
                <div className="flex justify-end mt-8">
                    <Button type="submit">Next: Tiers & Pricing</Button>
                </div>
            </form>
        </FormProvider>
    )
}

