 * [ADMIN] Fetches event details for ANY event by its ID.
 */
export const getAnyEventById_Admin = (eventId: string): Promise<EventDetailDTO> => {
    return apiFetch<EventDetailDTO>(`${API_BASE_PATH}/admin/${eventId}`);
};

