option.value:
// --- Physical Configuration View ---
import {Block, CreateEventFormData, Row, SessionSeatingMapRequest} from "@/lib/validators/event";
import {useFormContext} from "react-hook-form";
import * as React from "react";
import {useCallback, useEffect, useState} from "react";
import {LayoutData, SeatingLayoutTemplateResponse} from "@/types/seatingLayout";
import {
    createSeatingLayoutTemplate,
    deleteSeatingLayoutTemplate,
    getSeatingLayoutTemplatesByOrg,
    updateSeatingLayoutTemplate
} from "@/lib/actions/seatingLayoutTemplateActions";
import {toast} from "sonner";
import {LayoutEditor} from "@/app/manage/organization/[organization_id]/seating/_components/LayoutEditor";
import {TierAssignmentEditor} from "@/app/manage/organization/[organization_id]/event/_components/TierAssignmentEditor";
import {ProgressSteps} from "./physical-config/ProgressSteps";
import {NavigationButtons} from "./physical-config/NavigationButtons";
import {LayoutSelector} from "./physical-config/LayoutSelector";
import {DeleteConfirmationDialog} from "./physical-config/DeleteConfirmationDialog";
import {getRowLabel} from "@/app/manage/organization/[organization_id]/seating/create/_lib/getRowLabel";
import {Button} from "@/components/ui/button";

type Step = {
    id: string;
    label: string;
}

export function PhysicalConfigView({onSave, initialConfig}: {
    onSave: (layout: SessionSeatingMapRequest) => void;
    initialConfig?: SessionSeatingMapRequest;
}) {
    const {watch} = useFormContext<CreateEventFormData>();
    const organizationId = watch('organizationId');
    const tiers = watch('tiers');
    const [templates, setTemplates] = useState<SeatingLayoutTemplateResponse[]>([]);
    const [mode, setMode] = useState<'select' | 'create' | 'assign'>('select');
    const [selectedLayout, setSelectedLayout] = useState<LayoutData | null>(null);
    const [selectedTemplateId, setSelectedTemplateId] = useState<string | null>(null);
    const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
    const [layoutToDelete, setLayoutToDelete] = useState<{ id: string, name: string } | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const [currentAssignedLayout, setCurrentAssignedLayout] = useState<SessionSeatingMapRequest | null>(null);

    // Progress steps configuration
    const steps: Step[] = [
        {id: 'select', label: 'Select Layout'},
        {id: 'create', label: 'Edit Layout'},
        {id: 'assign', label: 'Assign Tiers'},
    ];

    // If initialConfig is provided, we should start at the assign step
    useEffect(() => {
        if (initialConfig && initialConfig.name !== null) {
            setCurrentAssignedLayout(initialConfig);
            setMode('assign');
        }
    }, [initialConfig]);

    const loadTemplates = useCallback(async () => {
        setIsLoading(true);
        try {
            const res = await getSeatingLayoutTemplatesByOrg(organizationId, 0, 100);
            setTemplates(res.content);
        } catch (error) {
            toast.error("Failed to load layout templates");
            console.error(error);
        } finally {
            setIsLoading(false);
        }
    }, [organizationId]);

    // Fetch templates when component loads or organization changes
    useEffect(() => {
        if (organizationId) {
            loadTemplates().then();
        }
    }, [loadTemplates, organizationId]);

    // Transform layout for tier assignment when selectedLayout changes
    useEffect(() => {
        if (!selectedLayout || mode !== 'assign') return;

        const transformedBlocks = selectedLayout.layout.blocks.map((block) => {
            const newBlock: Block = {
                ...block,
                rows: [],
                seats: [],
            };

            if (block.type === 'seated_grid' && block.rows && block.columns) {
                const startRowIndex = block.startRowLabel ? block.startRowLabel.charCodeAt(0) - 'A'.charCodeAt(0) : 0;
                const startCol = block.startColumnLabel || 1;
                const numRows = block.rows;
                const numColumns = block.columns;

                newBlock.rows = Array.from({length: numRows}, (_, rowIndex) => {
                    const newRow: Row = {
                        id: `temp_row_${block.id}_${rowIndex}`,
                        label: `${getRowLabel(startRowIndex + rowIndex)}`,
                        seats: Array.from({length: numColumns}, (_, colIndex) => ({
                            id: `temp_seat_${block.id}_${rowIndex}_${colIndex}`,
                            label: `${startCol + colIndex}${getRowLabel(startRowIndex + rowIndex)}`,
                            status: 'AVAILABLE',
                        })),
                    };
                    return newRow;
                });
            } else if (block.type === 'standing_capacity' && block.capacity) {
                const capacity = block.capacity;
                newBlock.seats = Array.from({length: capacity}, (_, i) => ({
                    id: `temp_seat_${block.id}_${i}`,
                    label: `Slot ${i + 1}`,
                    status: 'AVAILABLE',
                }));
            }
            return newBlock;
        });

        setCurrentAssignedLayout({
            name: selectedLayout.name,
            layout: {
                blocks: transformedBlocks,
            },
        });
    }, [selectedLayout, mode]);

    // Check if we need to auto-navigate to step 1 when going to step 2 without a layout
    useEffect(() => {
        if (mode === 'create' && !selectedLayout && !initialConfig) {
            toast.error("Please select a layout first");
            setMode('select');
        }
    }, [mode, selectedLayout, initialConfig]);

    // Handle step navigation
    const handleStepClick = (stepId: string) => {
        // If trying to go to create step but no layout is selected, redirect to select
        if (stepId === 'create' && !selectedLayout) {
            toast.error("Please select a layout first");
            setMode('select');
            return;
        }

        // If trying to go to assign step but no layout is prepared, redirect to select
        if (stepId === 'assign' && !currentAssignedLayout && !selectedLayout) {
            toast.error("Please select and configure a layout first");
            setMode('select');
            return;
        }

        setMode(stepId as 'select' | 'create' | 'assign');
    };

    // Handle layout selection
    const handleLayoutSelect = (template: SeatingLayoutTemplateResponse) => {
        setSelectedLayout(template.layoutData);
        setSelectedTemplateId(template.id);
    };

    // Handle create from scratch
    const handleCreateFromScratch = () => {
        setSelectedLayout(null);
        setSelectedTemplateId(null);
        setMode('create');
    };

    // Handle delete confirmation - open dialog
    const handleDeleteConfirm = (id: string, name: string) => {
        setLayoutToDelete({id, name});
        setIsDeleteDialogOpen(true);
    };

    // Handle actual deletion
    const handleDeleteLayout = async () => {
        if (!layoutToDelete) return;

        try {
            await deleteSeatingLayoutTemplate(layoutToDelete.id);
            toast.success(`Layout "${layoutToDelete.name}" deleted successfully`);

            // Refresh the templates list
            await loadTemplates();

            // If deleted template was selected, clear selection
            if (selectedTemplateId === layoutToDelete.id) {
                setSelectedLayout(null);
                setSelectedTemplateId(null);
            }
        } catch (error) {
            toast.error("Failed to delete layout template");
            console.error(error);
        } finally {
            setIsDeleteDialogOpen(false);
            setLayoutToDelete(null);
        }
    };

    // Handle tier assignments updates from the editor
    const handleTierAssignmentUpdate = (layoutWithTiers: SessionSeatingMapRequest) => {
        setCurrentAssignedLayout(layoutWithTiers);
    };

    const handleTierAssignmentSave = () => {
        if (!currentAssignedLayout) {
            toast.error("No layout data available");
            return;
        }

        // Check if all seats and standing blocks have tier assignments
        let hasUnassignedElements = false;

        for (const block of currentAssignedLayout.layout.blocks) {
            // Check seated blocks (rows with seats)
            if (block.type === 'seated_grid' && block.rows) {
                for (const row of block.rows) {
                    for (const seat of row.seats) {
                        // Seat must either have a tierId or be marked as RESERVED
                        if (!seat.tierId && seat.status !== 'RESERVED') {
                            hasUnassignedElements = true;
                            break;
                        }
                    }
                    if (hasUnassignedElements) break;
                }
            }

            // Check standing capacity blocks
            if (block.type === 'standing_capacity' && block.seats) {
                // For standing blocks, check if at least one seat has a tier assignment
                const hasAnyAssignedSeat = block.seats.some(seat => seat.tierId);
                if (!hasAnyAssignedSeat && block.seats.length > 0) {
                    hasUnassignedElements = true;
                    break;
                }
            }

            if (hasUnassignedElements) break;
        }

        if (hasUnassignedElements) {
            toast.error("Please assign all seats to a tier or mark them as reserved. Standing areas must also have tier assignments.");
            return;
        }

        // All elements are properly assigned, proceed with save
        onSave(currentAssignedLayout);
    };

    const handleSave = async (layoutData: LayoutData) => {
        const request = {
            name: layoutData.name,
            organizationId,
            layoutData,
        };

        try {
            // If we're editing an existing template, update it instead of creating a new one
            if (selectedTemplateId) {
                const data = await updateSeatingLayoutTemplate(selectedTemplateId, request);
                toast.success(`Layout "${data.name}" updated successfully!`);

                // Proceed to assign mode with updated layout data
                setSelectedLayout(data.layoutData);
                setMode("assign");

                // Refresh the templates list to show the updated template
                await loadTemplates();
            } else {
                // Creating a new template from scratch
                const data = await createSeatingLayoutTemplate(request);
                toast.success(`Layout "${data.name}" saved successfully!`);

                // Proceed to assign mode with the new layout
                setSelectedLayout(data.layoutData);
                setSelectedTemplateId(data.id);
                setMode("assign");

                // Refresh the templates list to include the new template
                await loadTemplates();
            }
        } catch (err) {
            if (err instanceof Error) {
                toast.error(err.message || 'Failed to save layout');
            } else {
                toast.error('An unexpected error occurred while saving the layout');
            }
            console.error(err);
        }
    };

    // Navigation between steps
    const goToPrevStep = () => {
        if (mode === 'create') {
            setMode('select');
        } else if (mode === 'assign') {
            setMode('create');

            // Check if we have a layout to edit
            if (!selectedLayout) {
                toast.error("No layout available to edit");
                setMode('select');
            }
        }
    };

    const goToNextStep = () => {
        if (mode === 'select') {
            if (!selectedLayout) {
                toast.error("Please select a layout first");
                return;
            }
            setMode('create');
        } else if (mode === 'create') {
            setMode('assign');
        }
    };

    // Render different steps based on current mode
    const renderCurrentStep = () => {
        switch (mode) {
            case 'create':
                return (
                    <div className={"h-[70vh] ring-1 ring-primary rounded-lg overflow-hidden"}>
                        <LayoutEditor
                            onSave={handleSave}
                            initialData={selectedLayout ?? undefined}
                        />
                    </div>
                );
            case 'assign':
                // If we have initialConfig or currentAssignedLayout, show the TierAssignmentEditor
                const layoutToEdit = currentAssignedLayout || initialConfig;

                return layoutToEdit ? (
                    <TierAssignmentEditor
                        layoutData={layoutToEdit}
                        onChange={handleTierAssignmentUpdate}
                        tiers={tiers}
                    />
                ) : (
                    <div className="p-4 text-center">
                        <p>No layout data available.</p>
                        <Button
                            variant="outline"
                            onClick={() => setMode('select')}
                            className="mt-4"
                        >
                            Go to layout selection
                        </Button>
                    </div>
                );
            case 'select':
            default:
                return (
                    <LayoutSelector
                        templates={templates}
                        selectedTemplateId={selectedTemplateId}
                        isLoading={isLoading}
                        onLayoutSelect={handleLayoutSelect}
                        onCreateFromScratch={handleCreateFromScratch}
                        onDeleteLayout={handleDeleteConfirm}
                    />
                );
        }
    };

    return (
        <>
            <ProgressSteps
                steps={steps}
                currentMode={mode}
                onStepClick={handleStepClick}
            />

            {renderCurrentStep()}

            <NavigationButtons
                currentMode={mode}
                canProgress={!!selectedLayout}
                onPrevious={goToPrevStep}
                onNext={goToNextStep}
                onFinish={handleTierAssignmentSave}
            />

            <DeleteConfirmationDialog
                isOpen={isDeleteDialogOpen}
                onOpenChange={setIsDeleteDialogOpen}
                layoutName={layoutToDelete?.name}
                onConfirmDelete={handleDeleteLayout}
            />
        </>
    );
}

// --- Physical Configuration View ---
import {Block, CreateEventFormData, Row, SessionSeatingMapRequest} from "@/lib/validators/event";
import {useFormContext} from "react-hook-form";
import * as React from "react";
import {useCallback, useEffect, useState} from "react";
import {LayoutData, SeatingLayoutTemplateResponse} from "@/types/seatingLayout";
import {
    createSeatingLayoutTemplate,
    deleteSeatingLayoutTemplate,
    getSeatingLayoutTemplatesByOrg,
    updateSeatingLayoutTemplate
} from "@/lib/actions/seatingLayoutTemplateActions";
import {toast} from "sonner";
import {LayoutEditor} from "@/app/manage/organization/[organization_id]/seating/_components/LayoutEditor";
import {TierAssignmentEditor} from "@/app/manage/organization/[organization_id]/event/_components/TierAssignmentEditor";
import {ProgressSteps} from "./physical-config/ProgressSteps";
import {NavigationButtons} from "./physical-config/NavigationButtons";
import {LayoutSelector} from "./physical-config/LayoutSelector";
import {DeleteConfirmationDialog} from "./physical-config/DeleteConfirmationDialog";
import {getRowLabel} from "@/app/manage/organization/[organization_id]/seating/create/_lib/getRowLabel";
import {Button} from "@/components/ui/button";

type Step = {
    id: string;
    label: string;
}

export function PhysicalConfigView({onSave, initialConfig}: {
    onSave: (layout: SessionSeatingMapRequest) => void;
    initialConfig?: SessionSeatingMapRequest;
}) {
    const {watch} = useFormContext<CreateEventFormData>();
    const organizationId = watch('organizationId');
    const tiers = watch('tiers');
    const [templates, setTemplates] = useState<SeatingLayoutTemplateResponse[]>([]);
    const [mode, setMode] = useState<'select' | 'create' | 'assign'>('select');
    const [selectedLayout, setSelectedLayout] = useState<LayoutData | null>(null);
    const [selectedTemplateId, setSelectedTemplateId] = useState<string | null>(null);
    const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
    const [layoutToDelete, setLayoutToDelete] = useState<{ id: string, name: string } | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const [currentAssignedLayout, setCurrentAssignedLayout] = useState<SessionSeatingMapRequest | null>(null);

    // Progress steps configuration
    const steps: Step[] = [
        {id: 'select', label: 'Select Layout'},
        {id: 'create', label: 'Edit Layout'},
        {id: 'assign', label: 'Assign Tiers'},
    ];

    // If initialConfig is provided, we should start at the assign step
    useEffect(() => {
        if (initialConfig && initialConfig.name !== null) {
            setCurrentAssignedLayout(initialConfig);
            setMode('assign');
        }
    }, [initialConfig]);

    const loadTemplates = useCallback(async () => {
        setIsLoading(true);
        try {
            const res = await getSeatingLayoutTemplatesByOrg(organizationId, 0, 100);
            setTemplates(res.content);
        } catch (error) {
            toast.error("Failed to load layout templates");
            console.error(error);
        } finally {
            setIsLoading(false);
        }
    }, [organizationId]);

    // Fetch templates when component loads or organization changes
    useEffect(() => {
        if (organizationId) {
            loadTemplates().then();
        }
    }, [loadTemplates, organizationId]);

    // Transform layout for tier assignment when selectedLayout changes
    useEffect(() => {
        if (!selectedLayout || mode !== 'assign') return;

        const transformedBlocks = selectedLayout.layout.blocks.map((block) => {
            const newBlock: Block = {
                ...block,
                rows: [],
                seats: [],
            };

            if (block.type === 'seated_grid' && block.rows && block.columns) {
                const startRowIndex = block.startRowLabel ? block.startRowLabel.charCodeAt(0) - 'A'.charCodeAt(0) : 0;
                const startCol = block.startColumnLabel || 1;
                const numRows = block.rows;
                const numColumns = block.columns;

                newBlock.rows = Array.from({length: numRows}, (_, rowIndex) => {
                    const newRow: Row = {
                        id: `temp_row_${block.id}_${rowIndex}`,
                        label: `${getRowLabel(startRowIndex + rowIndex)}`,
                        seats: Array.from({length: numColumns}, (_, colIndex) => ({
                            id: `temp_seat_${block.id}_${rowIndex}_${colIndex}`,
                            label: `${startCol + colIndex}${getRowLabel(startRowIndex + rowIndex)}`,
                            status: 'AVAILABLE',
                        })),
                    };
                    return newRow;
                });
            } else if (block.type === 'standing_capacity' && block.capacity) {
                const capacity = block.capacity;
                newBlock.seats = Array.from({length: capacity}, (_, i) => ({
                    id: `temp_seat_${block.id}_${i}`,
                    label: `Slot ${i + 1}`,
                    status: 'AVAILABLE',
                }));
            }
            return newBlock;
        });

        setCurrentAssignedLayout({
            name: selectedLayout.name,
            layout: {
                blocks: transformedBlocks,
            },
        });
    }, [selectedLayout, mode]);

    // Check if we need to auto-navigate to step 1 when going to step 2 without a layout
    useEffect(() => {
        if (mode === 'create' && !selectedLayout && !initialConfig) {
            toast.error("Please select a layout first");
            setMode('select');
        }
    }, [mode, selectedLayout, initialConfig]);

    // Handle step navigation
    const handleStepClick = (stepId: string) => {
        // If trying to go to create step but no layout is selected, redirect to select
        if (stepId === 'create' && !selectedLayout) {
            toast.error("Please select a layout first");
            setMode('select');
            return;
        }

        // If trying to go to assign step but no layout is prepared, redirect to select
        if (stepId === 'assign' && !currentAssignedLayout && !selectedLayout) {
            toast.error("Please select and configure a layout first");
            setMode('select');
            return;
        }

        setMode(stepId as 'select' | 'create' | 'assign');
    };

    // Handle layout selection
    const handleLayoutSelect = (template: SeatingLayoutTemplateResponse) => {
        setSelectedLayout(template.layoutData);
        setSelectedTemplateId(template.id);
    };

    // Handle create from scratch
    const handleCreateFromScratch = () => {
        setSelectedLayout(null);
        setSelectedTemplateId(null);
        setMode('create');
    };

    // Handle delete confirmation - open dialog
    const handleDeleteConfirm = (id: string, name: string) => {
        setLayoutToDelete({id, name});
        setIsDeleteDialogOpen(true);
    };

    // Handle actual deletion
    const handleDeleteLayout = async () => {
        if (!layoutToDelete) return;

        try {
            await deleteSeatingLayoutTemplate(layoutToDelete.id);
            toast.success(`Layout "${layoutToDelete.name}" deleted successfully`);

            // Refresh the templates list
            await loadTemplates();

            // If deleted template was selected, clear selection
            if (selectedTemplateId === layoutToDelete.id) {
                setSelectedLayout(null);
                setSelectedTemplateId(null);
            }
        } catch (error) {
            toast.error("Failed to delete layout template");
            console.error(error);
        } finally {
            setIsDeleteDialogOpen(false);
            setLayoutToDelete(null);
        }
    };

    // Handle tier assignments updates from the editor
    const handleTierAssignmentUpdate = (layoutWithTiers: SessionSeatingMapRequest) => {
        setCurrentAssignedLayout(layoutWithTiers);
    };

    const handleTierAssignmentSave = () => {
        if (!currentAssignedLayout) {
            toast.error("No layout data available");
            return;
        }

        // Check if all seats and standing blocks have tier assignments
        let hasUnassignedElements = false;

        for (const block of currentAssignedLayout.layout.blocks) {
            // Check seated blocks (rows with seats)
            if (block.type === 'seated_grid' && block.rows) {
                for (const row of block.rows) {
                    for (const seat of row.seats) {
                        // Seat must either have a tierId or be marked as RESERVED
                        if (!seat.tierId && seat.status !== 'RESERVED') {
                            hasUnassignedElements = true;
                            break;
                        }
                    }
                    if (hasUnassignedElements) break;
                }
            }

            // Check standing capacity blocks
            if (block.type === 'standing_capacity' && block.seats) {
                // For standing blocks, check if at least one seat has a tier assignment
                const hasAnyAssignedSeat = block.seats.some(seat => seat.tierId);
                if (!hasAnyAssignedSeat && block.seats.length > 0) {
                    hasUnassignedElements = true;
                    break;
                }
            }

            if (hasUnassignedElements) break;
        }

        if (hasUnassignedElements) {
            toast.error("Please assign all seats to a tier or mark them as reserved. Standing areas must also have tier assignments.");
            return;
        }

        // All elements are properly assigned, proceed with save
        onSave(currentAssignedLayout);
    };

    const handleSave = async (layoutData: LayoutData) => {
        const request = {
            name: layoutData.name,
            organizationId,
            layoutData,
        };

        try {
            // If we're editing an existing template, update it instead of creating a new one
            if (selectedTemplateId) {
                const data = await updateSeatingLayoutTemplate(selectedTemplateId, request);
                toast.success(`Layout "${data.name}" updated successfully!`);

                // Only set layout data and change mode on success
                setSelectedLayout(data.layoutData);
                setMode("assign");

                // Refresh the templates list to show the updated template
                await loadTemplates();
            } else {
                // Creating a new template from scratch
                const data = await createSeatingLayoutTemplate(request);
                toast.success(`Layout "${data.name}" saved successfully!`);

                // Only set layout data and change mode on success
                setSelectedLayout(data.layoutData);
                setSelectedTemplateId(data.id);
                setMode("assign");

                // Refresh the templates list to include the new template
                await loadTemplates();
            }
        } catch (err) {
            if (err instanceof Error) {
                toast.error(err.message || 'Failed to save layout');
            } else {
                toast.error('An unexpected error occurred while saving the layout');
            }
            console.error(err);
        }
    };

    // Navigation between steps
    const goToPrevStep = () => {
        if (mode === 'create') {
            setMode('select');
        } else if (mode === 'assign') {
            setMode('create');

            // Check if we have a layout to edit
            if (!selectedLayout) {
                toast.error("No layout available to edit");
                setMode('select');
            }
        }
    };

    const goToNextStep = () => {
        if (mode === 'select') {
            if (!selectedLayout) {
                toast.error("Please select a layout first");
                return;
            }
            setMode('create');
        } else if (mode === 'create') {
            setMode('assign');
        }
    };

    // Render different steps based on current mode
    const renderCurrentStep = () => {
        switch (mode) {
            case 'create':
                return (
                    <div className={"h-[70vh] ring-1 ring-primary rounded-lg overflow-hidden"}>
                        <LayoutEditor
                            onSave={handleSave}
                            initialData={selectedLayout ?? undefined}
                        />
                    </div>
                );
            case 'assign':
                // If we have initialConfig or currentAssignedLayout, show the TierAssignmentEditor
                const layoutToEdit = currentAssignedLayout || initialConfig;

                return layoutToEdit ? (
                    <TierAssignmentEditor
                        layoutData={layoutToEdit}
                        onChange={handleTierAssignmentUpdate}
                        tiers={tiers}
                    />
                ) : (
                    <div className="p-4 text-center">
                        <p>No layout data available.</p>
                        <Button
                            variant="outline"
                            onClick={() => setMode('select')}
                            className="mt-4"
                        >
                            Go to layout selection
                        </Button>
                    </div>
                );
            case 'select':
            default:
                return (
                    <LayoutSelector
                        templates={templates}
                        selectedTemplateId={selectedTemplateId}
                        isLoading={isLoading}
                        onLayoutSelect={handleLayoutSelect}
                        onCreateFromScratch={handleCreateFromScratch}
                        onDeleteLayout={handleDeleteConfirm}
                    />
                );
        }
    };

    return (
        <>
            <ProgressSteps
                steps={steps}
                currentMode={mode}
                onStepClick={handleStepClick}
            />

            {renderCurrentStep()}

            <NavigationButtons
                currentMode={mode}
                canProgress={!!selectedLayout}
                onPrevious={goToPrevStep}
                onNext={goToNextStep}
                onFinish={handleTierAssignmentSave}
            />

            <DeleteConfirmationDialog
                isOpen={isDeleteDialogOpen}
                onOpenChange={setIsDeleteDialogOpen}
                layoutName={layoutToDelete?.name}
                onConfirmDelete={handleDeleteLayout}
            />
        </>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData, SessionSeatingMapRequest} from '@/lib/validators/event';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {
    SessionListItemSeating
} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({onConfigModeChange}: SeatingStepProps) {
    const {control, formState: {errors}, watch, getValues, setValue} = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const {fields} = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: SessionSeatingMapRequest) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-4">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4"/>
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div>
                    {/* Empty div for flexbox spacing */}
                </div>

                <Card>
                    <CardHeader>
                        <CardTitle>
                            {currentSession.isOnline
                                ? "Online Capacity Configuration"
                                : "Physical Seating Configuration"}
                        </CardTitle>
                        <CardDescription>
                            {currentSession.isOnline
                                ? "Set the capacity and ticket tier for your online event."
                                : "Choose a layout template or create a new one, then assign your tiers."}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        {currentSession.isOnline ? (
                            <OnlineConfigView onSave={handleSave}/>
                        ) : (
                            <PhysicalConfigView onSave={handleSave}/>
                        )}
                    </CardContent>
                </Card>

                <div className="flex items-center justify-between border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <Card>
            <CardHeader>
                <CardTitle>Seating & Tier Assignment</CardTitle>
                <CardDescription>
                    Configure the seating layout and assign ticket tiers for each session.
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import * as React from 'react';
import {useState, useEffect} from 'react';
import {useFieldArray, useFormContext} from 'react-hook-form';
import {CreateEventFormData, SessionSeatingMapRequest} from '@/lib/validators/event';
import {Card, CardContent, CardDescription, CardHeader, CardTitle} from '@/components/ui/card';
import {
    SessionListItemSeating
} from "@/app/manage/organization/[organization_id]/event/_components/SessionListItemSeating";
import {OnlineConfigView} from "@/app/manage/organization/[organization_id]/event/_components/OnlineConfigView";
import {PhysicalConfigView} from "@/app/manage/organization/[organization_id]/event/_components/PhysicalConfigView";
import {Button} from "@/components/ui/button";
import {Checkbox} from "@/components/ui/checkbox";
import {Label} from "@/components/ui/label";
import {toast} from "sonner";
import {ArrowLeft} from "lucide-react";

interface SeatingStepProps {
    onConfigModeChange?: (isInConfigMode: boolean) => void;
}

// --- Main Seating Step Component ---
export function SeatingStep({onConfigModeChange}: SeatingStepProps) {
    const {control, formState: {errors}, watch, getValues, setValue} = useFormContext<CreateEventFormData>();
    const [configuringIndex, setConfiguringIndex] = useState<number | null>(null);
    const [applyToAll, setApplyToAll] = useState(false);

    const {fields} = useFieldArray({
        control,
        name: "sessions",
    });

    // Get the session being configured
    const currentSession = configuringIndex !== null ? watch(`sessions.${configuringIndex}`) : null;

    // Notify parent component when configuration mode changes
    useEffect(() => {
        if (onConfigModeChange) {
            onConfigModeChange(configuringIndex !== null);
        }
    }, [configuringIndex, onConfigModeChange]);

    const handleSave = (layoutData: SessionSeatingMapRequest) => {
        if (configuringIndex === null) return;

        setValue(`sessions.${configuringIndex}.layoutData`, layoutData);

        if (applyToAll) {
            const allSessions = getValues('sessions');
            allSessions.forEach((s, i) => {
                // Apply only to sessions of the same type (online/physical)
                if (s.isOnline === currentSession?.isOnline) {
                    setValue(`sessions.${i}.layoutData`, layoutData);
                }
            });
            toast.success(`Seating applied to all ${currentSession?.isOnline ? 'online' : 'physical'} sessions.`);
        } else {
            toast.success(`Seating configured for Session ${configuringIndex + 1}.`);
        }

        setConfiguringIndex(null);
    };

    // If we're configuring a session, show the full-page configuration view
    if (configuringIndex !== null && currentSession) {
        return (
            <div className="space-y-4">
                <div className="flex items-center justify-between">
                    <Button
                        variant="ghost"
                        onClick={() => setConfiguringIndex(null)}
                        className="flex items-center gap-1"
                    >
                        <ArrowLeft className="h-4 w-4"/>
                        Back to Sessions
                    </Button>
                    <h2 className="text-xl font-semibold">
                        Configure Seating for Session {configuringIndex + 1}
                    </h2>
                    <div></div>
                    {/* Empty div for flexbox spacing */}
                </div>

                <Card>
                    <CardHeader>
                        <CardTitle>
                            {currentSession.isOnline
                                ? "Online Capacity Configuration"
                                : "Physical Seating Configuration"}
                        </CardTitle>
                        <CardDescription>
                            {currentSession.isOnline
                                ? "Set the capacity and ticket tier for your online event."
                                : "Choose a layout template or create a new one, then assign your tiers."}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        {currentSession.isOnline ? (
                            <OnlineConfigView onSave={handleSave}/>
                        ) : (
                            <PhysicalConfigView 
                                onSave={handleSave} 
                                initialConfig={currentSession.layoutData as SessionSeatingMapRequest}
                            />
                        )}
                    </CardContent>
                </Card>

                <div className="flex items-center justify-between border-t pt-4 mt-8">
                    <div className="flex items-center space-x-2">
                        <Checkbox
                            id="apply-to-all-seating"
                            checked={applyToAll}
                            onCheckedChange={(checked) => setApplyToAll(checked === true)}
                        />
                        <Label htmlFor="apply-to-all-seating">
                            Apply to all {currentSession.isOnline ? 'online' : 'physical'} sessions
                        </Label>
                    </div>
                </div>
            </div>
        );
    }

    // Otherwise show the session list
    return (
        <Card>
            <CardHeader>
                <CardTitle>Seating & Tier Assignment</CardTitle>
                <CardDescription>
                    Configure the seating layout and assign ticket tiers for each session.
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {fields.map((field, index) => (
                    <SessionListItemSeating
                        key={field.id}
                        field={field}
                        index={index}
                        onConfigure={() => setConfiguringIndex(index)}
                    />
                ))}

                {errors.sessions?.root && (
                    <p className="text-sm font-medium text-destructive">{errors.sessions.root.message}</p>
                )}
            </CardContent>
        </Card>
    );
}

