}

import * as React from "react";
import {CheckCircle2} from "lucide-react";
import {cn} from "@/lib/utils";

type Step = {
    id: string;
    label: string;
}

interface ProgressStepsProps {
    steps: Step[];
    currentMode: string;
    onStepClick: (step: string) => void;
}

export function ProgressSteps({ steps, currentMode, onStepClick }: ProgressStepsProps) {
    return (
        <div className="mb-6">
            <div className="flex items-center justify-center">
                {steps.map((step, idx) => {
                    // Determine if this step is clickable (can't skip ahead, but can go back)
                    const currentStepIndex = steps.findIndex(s => s.id === currentMode);
                    const isClickable = idx <= currentStepIndex;

                    return (
                        <React.Fragment key={step.id}>
                            {/* Step indicator */}
                            <div
                                className={cn(
                                    "flex flex-col items-center",
                                    isClickable && "cursor-pointer"
                                )}
                                onClick={() => {
                                    if (!isClickable) return;
                                    onStepClick(step.id);
                                }}
                            >
                                <div
                                    className={cn(
                                        "flex items-center justify-center h-8 w-8 rounded-full border-2",
                                        currentMode === step.id
                                            ? "border-primary bg-primary text-primary-foreground"
                                            : idx < steps.findIndex(s => s.id === currentMode)
                                                ? "border-primary bg-primary/10 text-primary"
                                                : "border-muted-foreground text-muted-foreground"
                                    )}
                                >
                                    {idx < steps.findIndex(s => s.id === currentMode) ? (
                                        <CheckCircle2 className="h-5 w-5"/>
                                    ) : (
                                        <span>{idx + 1}</span>
                                    )}
                                </div>
                                <span className={cn(
                                    "text-xs mt-1",
                                    currentMode === step.id ? "text-primary font-medium" : "text-muted-foreground"
                                )}>
                                    {step.label}
                                </span>
                            </div>

                            {/* Connector line between steps */}
                            {idx < steps.length - 1 && (
                                <div
                                    className={cn(
                                        "w-12 h-[2px] mx-1",
                                        idx < steps.findIndex(s => s.id === currentMode)
                                            ? "bg-primary"
                                            : "bg-muted-foreground/30"
                                    )}
                                />
                            )}
                        </React.Fragment>
                    );
                })}
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useEffect, useState} from 'react';
import {CoreDetailsStep} from "@/app/manage/organization/[organization_id]/event/_components/CoreDetailsStep";
import {Progress} from "@/components/ui/progress";
import {CreateEventRequest} from "@/types/event";
import {toast} from "sonner";
import {FormProvider, useForm} from "react-hook-form";
import {Button} from "@/components/ui/button";
import {TiersStep} from "@/app/manage/organization/[organization_id]/event/_components/TierStep";
import {zodResolver} from "@hookform/resolvers/zod";
import {coreDetailsSchema, tiersSchema} from "@/lib/validators/event";
import {WizardSidebar} from "../_components/WizardSideBar";
import {useSidebar} from "@/components/ui/sidebar";

// --- Main Wizard Parent Component ---
export default function CreateEventPage() {
    const [step, setStep] = useState(1);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const totalSteps = 5;
    const {setOpen} = useSidebar();

    const steps = [
        {number: 1, title: "Core Details", description: "Name, category, and location"},
        {number: 2, title: "Tiers & Pricing", description: "Set your ticket prices"},
        {number: 3, title: "Scheduling", description: "Add dates and times"},
        {number: 4, title: "Seating", description: "Configure seating maps"},
        {number: 5, title: "Review & Publish", description: "Final check before submission"},
    ];

    // Collapse the main sidebar when component mounts
    useEffect(() => {
        setOpen(false);
    }, [setOpen]);

    // ✅ The single form instance for the entire wizard is created here.
    const methods = useForm<CreateEventRequest>({
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            organizationId: '', // Should be set from context/params
            categoryId: '',
            isOnline: false,
            tiers: [],
            sessions: [],
        },
        mode: 'onSubmit', // Only validate when explicitly triggered
    });

    // Update the validation schema based on the current step
    useEffect(() => {
        if (step === 1) {
            methods.clearErrors();
        } else if (step === 2) {
            methods.clearErrors();
        }
    }, [step, methods]);

    const validateCoreDetails = async () => {
        try {
            // Check for cover photos
            if (coverFiles.length === 0) {
                toast.error("Please upload at least one cover photo.");
                return false;
            }

            // Get form values for core details
            const values = methods.getValues();
            const coreData = {
                title: values.title,
                description: values.description || "",
                overview: values.overview || "",
                categoryId: values.categoryId || "",
                isOnline: values.isOnline || false,
                venueId: values.venueId,
                onlineLink: values.onlineLink,
                locationDescription: values.locationDescription,
            };

            // Validate with Zod schema
            const result = coreDetailsSchema.safeParse(coreData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.path.join('.')}: ${err.message}`);
                });

                // Also set form errors for field highlighting
                result.error.errors.forEach(err => {
                    const path = err.path.join('.') as keyof CreateEventRequest;
                    methods.setError(path, { 
                        type: 'manual', 
                        message: err.message 
                    });
                });

                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Validation error:", error);
            toast.error("Please check all required fields");
            return false;
        }
    };

    const validateTiers = async () => {
        try {
            // Get tiers data
            const values = methods.getValues();
            const tiersData = { tiers: values.tiers || [] };

            // Validate with Zod schema
            const result = tiersSchema.safeParse(tiersData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.message}`);
                });
                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Tier validation error:", error);
            toast.error("Please add at least one tier with valid information");
            return false;
        }
    };

    const onNext = async () => {
        let isValid = false;

        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        } else {
            // For future steps that don't have validation yet
            isValid = true;
        }

        if (isValid) {
            setStep(s => Math.min(totalSteps, s + 1));
        }
    };

    const onPrev = () => setStep(s => Math.max(1, s - 1));

    const onSubmit = async (data: CreateEventRequest) => {
        // Perform final validation before submission
        let isValid = true;

        // Validate the current step first
        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        }

        if (isValid) {
            console.log("Final Assembled Form Data:", data);
            console.log("Final Cover Files:", coverFiles);
            // Here you would call your createEvent API action
            toast.success("Event submitted for approval!");
        }
    };

    const renderStep = () => {
        switch (step) {
            case 1:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
            case 2:
                return <TiersStep/>;
            // Add cases for other steps here
            default:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
        }
    };

    return (
        <div className="flex min-h-screen bg-muted/40">
            <WizardSidebar currentStep={step} steps={steps}/>
            <main className="flex-1 p-4 md:p-8 overflow-y-auto ml-72">
                <div className="max-w-4xl mx-auto">
                    <div className="mb-8">
                        <p className="text-sm text-muted-foreground">Step {step} of {totalSteps}</p>
                        <Progress value={(step / totalSteps) * 100} className="mt-2"/>
                    </div>

                    {/* ✅ The FormProvider broadcasts the form state to all children */}
                    <FormProvider {...methods}>
                        <form onSubmit={methods.handleSubmit(onSubmit)}>
                            {renderStep()}

                            <div className="flex justify-between mt-8">
                                <Button type="button" variant="outline" onClick={onPrev} disabled={step === 1}>
                                    Previous
                                </Button>
                                {step < totalSteps ? (
                                    <Button type="button" onClick={onNext}>
                                        Next
                                    </Button>
                                ) : (
                                    <Button type="submit">
                                        Submit for Approval
                                    </Button>
                                )}
                            </div>
                        </form>
                    </FormProvider>
                </div>
            </main>
        </div>
    );
}

'use client';

import React, { useEffect, useState } from 'react';
import { apiFetch } from '@/lib/api';
import keycloak from '@/lib/keycloak';

// Define a type for the expected API response
type ApiResponse = {
    message: string;
    // Add other properties you expect from the backend
    uid?: string;
    username?: string;
    scope?: string;
};

const Page = () => {
    // Initialize state to null or an object with a default message
    const [data, setData] = useState<ApiResponse | null>(null);
    const [error, setError] = useState<string | null>(null);


    useEffect(() => {
        if (!keycloak.authenticated) {
            setError('User not authenticated. Please log in.');
            return;
        }

        const fetchData = async () => {
            try {
                const response = await apiFetch<ApiResponse>('/event-seating/v1/protected/hello');
                setData(response);
            } catch (err) {
                console.error('Fetch error:', err);
                setError('Failed to connect to the API. Make sure the backend is running and accessible.');
            }
        };

        fetchData();
    }, []);

    // Render different UI based on the state
    let content;
    if (error) {
        content = <p style={{ color: 'red' }}>{error}</p>;
    } else if (!data) {
        content = <p>Loading...</p>;
    } else {
        // ✅ CORRECT: Render a specific property from the data object
        content = <p>{data.message}</p>;
    }

    return (
        <div>
            <h1>API Test Page</h1>
            {content}
        </div>
    );
};

export default Page;

'use client';

import React, { useEffect, useState } from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import { apiFetch } from '@/lib/api';
import keycloak from '@/lib/keycloak';
import { Button } from '@/components/ui/button';
import { Skeleton } from '@/components/ui/skeleton';

// Define a type for the expected API response
type ApiResponse = {
    message: string;
    // Add other properties you expect from the backend
    uid?: string;
    username?: string;
    scope?: string;
};

const MAP_CONTAINER_STYLE = {
    width: '100%',
    height: '500px',
    borderRadius: 'var(--radius)',
};

// Default center for the map (Colombo, Sri Lanka)
const DEFAULT_MAP_CENTER = {
    lat: 6.9271,
    lng: 79.8612,
};

const LIBRARIES: ("places")[] = ['places'];

const Page = () => {
    // Initialize state to null or an object with a default message
    const [data, setData] = useState<ApiResponse | null>(null);
    const [error, setError] = useState<string | null>(null);
    const [markerPosition, setMarkerPosition] = useState<google.maps.LatLngLiteral>(DEFAULT_MAP_CENTER);
    const [showMap, setShowMap] = useState(false);

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY!,
        libraries: LIBRARIES,
    });

    useEffect(() => {
        if (!keycloak.authenticated) {
            setError('User not authenticated. Please log in.');
            return;
        }

        const fetchData = async () => {
            try {
                const response = await apiFetch<ApiResponse>('/event-seating/v1/protected/hello');
                setData(response);
            } catch (err) {
                console.error('Fetch error:', err);
                setError('Failed to connect to the API. Make sure the backend is running and accessible.');
            }
        };

        fetchData();
    }, []);

    const handleMapClick = (event: google.maps.MapMouseEvent) => {
        if (event.latLng) {
            const newPos = { lat: event.latLng.lat(), lng: event.latLng.lng() };
            setMarkerPosition(newPos);
            console.log('Map clicked at:', newPos);
        }
    };

    // Render different UI based on the state
    let apiContent;
    if (error) {
        apiContent = <p style={{ color: 'red' }}>{error}</p>;
    } else if (!data) {
        apiContent = <p>Loading API data...</p>;
    } else {
        apiContent = <p>{data.message}</p>;
    }

    return (
        <div className="p-6 space-y-6">
            <h1 className="text-2xl font-bold mb-4">API Test Page</h1>
            {apiContent}

            <div className="space-y-4">
                <h2 className="text-xl font-semibold">Google Maps Test</h2>
                <p className="text-sm text-muted-foreground">
                    This is a test implementation of Google Maps to verify marker dragging functionality.
                </p>

                <Button onClick={() => setShowMap(!showMap)}>
                    {showMap ? 'Hide Map' : 'Show Map'}
                </Button>

                {showMap && (
                    <div className="border rounded-lg p-4 bg-card">
                        <div className="mb-4">
                            <p className="font-medium">Current Marker Position:</p>
                            <p className="text-sm">Latitude: {markerPosition.lat.toFixed(6)}</p>
                            <p className="text-sm">Longitude: {markerPosition.lng.toFixed(6)}</p>
                        </div>

                        <div className="h-[500px] relative">
                            {isLoaded ? (
                                <GoogleMap 
                                    mapContainerStyle={MAP_CONTAINER_STYLE} 
                                    center={markerPosition}
                                    zoom={15} 
                                    onClick={handleMapClick}
                                >
                                    <Marker
                                        position={markerPosition}
                                        draggable={true}
                                        onDragEnd={(e) => {
                                            if (e.latLng) {
                                                const newPos = { lat: e.latLng.lat(), lng: e.latLng.lng() };
                                                setMarkerPosition(newPos);
                                                console.log('Marker dragged to:', newPos);
                                            }
                                        }}
                                    />
                                </GoogleMap>
                            ) : (
                                <Skeleton className="w-full h-full" />
                            )}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Page;

'use client';

import React, {createContext, useContext, useState, useEffect, ReactNode, useMemo} from 'react';
import {useAuth} from '@/providers/AuthProvider';
import {getAppConfiguration} from '@/lib/actions/configActions';
import {AppConfig, TierLimitDetails, TierName} from '@/types/config';

// Define the shape of the context
interface LimitContextType {
    config: AppConfig | null;
    isLoading: boolean;
    error: string | null;
    currentUserTier: TierName;
    currentUserTierLimits: TierLimitDetails | null;
    getLimitsForTier: (tier: TierName) => TierLimitDetails | null;
}

const LimitContext = createContext<LimitContextType | undefined>(undefined);

export const LimitProvider = ({children}: { children: ReactNode }) => {
    const {keycloak} = useAuth();
    const [config, setConfig] = useState<AppConfig | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Fetch the entire configuration on initial application load
    useEffect(() => {
        const fetchConfig = async () => {
            try {
                const appConfig = await getAppConfiguration();
                setConfig(appConfig);
            } catch (err) {
                setError('Failed to load application configuration. Some features may be unavailable.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };
        fetchConfig();
    }, []);

    // A memoized helper to get the current user's highest tier from their JWT
    const currentUserTier = useMemo((): TierName => {
        if (!keycloak?.tokenParsed?.user_groups || !Array.isArray(keycloak.tokenParsed.user_groups)) return 'FREE';

        const userGroups: string[] = keycloak.tokenParsed.user_groups;
        let highestTier: TierName = 'FREE';
        let maxLevel = 0;

        const tierLevels: Record<TierName, number> = {FREE: 0, PRO: 1, ENTERPRISE: 2};

        userGroups.forEach(group => {
            if (group.startsWith('/Tiers/')) {
                const tierName = group.substring('/Tiers/'.length).toUpperCase() as TierName;
                if (tierLevels[tierName] !== undefined && tierLevels[tierName] > maxLevel) {
                    maxLevel = tierLevels[tierName];
                    highestTier = tierName;
                }
            }
        });
        return highestTier;
    }, [keycloak]);

    // A memoized value for the current user's specific limits, derived from the full config
    const currentUserTierLimits = useMemo(() => {
        if (!config) return null;
        return config.tierLimits[currentUserTier] || config.tierLimits.FREE;
    }, [config, currentUserTier]);

    // A function to get limits for any specified tier (useful for upgrade pages)
    const getLimitsForTier = (tier: TierName): TierLimitDetails | null => {
        if (!config) return null;
        return config.tierLimits[tier] || null;
    };

    const value = {config, isLoading, error, currentUserTier, currentUserTierLimits, getLimitsForTier};

    return (
        <LimitContext.Provider value={value}>
            {children}
        </LimitContext.Provider>
    );
};

// The custom hook to easily access the limits from any component
export const useLimits = (): LimitContextType => {
    const context = useContext(LimitContext);
    if (context === undefined) {
        throw new Error('useLimits must be used within a LimitProvider');
    }
    return context;
};

'use client';

import React, {createContext, useContext, useState, useEffect, ReactNode, useMemo} from 'react';
import {useAuth} from '@/providers/AuthProvider';
import {getAppConfiguration} from '@/lib/actions/configActions';
import {AppConfig, TierLimitDetails, TierName} from '@/types/config';

// Define the shape of the context
interface LimitContextType {
    config: AppConfig | null;
    isLoading: boolean;
    error: string | null;
    currentUserTier: TierName;
    currentUserTierLimits: TierLimitDetails | null;
    getLimitsForTier: (tier: TierName) => TierLimitDetails | null;
}

const LimitContext = createContext<LimitContextType | undefined>(undefined);

export const LimitProvider = ({children}: { children: ReactNode }) => {
    const {keycloak} = useAuth();
    const [config, setConfig] = useState<AppConfig | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Fetch the entire configuration on initial application load
    useEffect(() => {
        const fetchConfig = async () => {
            try {
                const appConfig = await getAppConfiguration();
                setConfig(appConfig);
            } catch (err) {
                setError('Failed to load application configuration. Some features may be unavailable.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };
        fetchConfig();
    }, []);

    // A memoized helper to get the current user's highest tier from their JWT
    const currentUserTier = useMemo((): TierName => {
        if (!keycloak?.tokenParsed?.user_groups || !Array.isArray(keycloak.tokenParsed.user_groups)) return 'FREE';

        const userGroups: string[] = keycloak.tokenParsed.user_groups;
        let highestTier: TierName = 'FREE';
        let maxLevel = 0;

        const tierLevels: Record<TierName, number> = {FREE: 0, PRO: 1, ENTERPRISE: 2};

        userGroups.forEach(group => {
            if (group.startsWith('/Tiers/')) {
                const tierName = group.substring('/Tiers/'.length).toUpperCase() as TierName;
                if (tierLevels[tierName] !== undefined && tierLevels[tierName] > maxLevel) {
                    maxLevel = tierLevels[tierName];
                    highestTier = tierName;
                }
            }
        });
        return highestTier;
    }, [keycloak]);

    // A memoized value for the current user's specific limits, derived from the full config
    const currentUserTierLimits = useMemo(() => {
        if (!config) return null;
        return config.tierLimits[currentUserTier] || config.tierLimits.FREE;
    }, [config, currentUserTier]);

    // A function to get limits for any specified tier (useful for upgrade pages)
    const getLimitsForTier = (tier: TierName): TierLimitDetails | null => {
        if (!config) return null;
        return config.tierLimits[tier] || null;
    };

    const value = {config, isLoading, error, currentUserTier, currentUserTierLimits, getLimitsForTier};

    return React.createElement(
        LimitContext.Provider,
        { value },
        children
    );
};

// The custom hook to easily access the limits from any component
export const useLimits = (): LimitContextType => {
    const context = useContext(LimitContext);
    if (context === undefined) {
        throw new Error('useLimits must be used within a LimitProvider');
    }
    return context;
};
