// --- Step 1: Core Details Component ---
export function CoreDetailsStep({onNext}: { onNext: () => void }) {
    const {organization} = useOrganization();
    const {myLimits} = useLimits();
    const [venues, setVenues] = useState<VenueResponse[]>([]);
    const [categories, setCategories] = useState<CategoryResponse[]>([]);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const fileInputRef = useRef<HTMLInputElement>(null);

    const maxPhotos = myLimits?.eventLimits.maxCoverPhotos || 1;

    const form = useForm<CoreDetailsData>({
        resolver: zodResolver(coreDetailsSchema),
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            categoryId: '',
            isOnline: false,
        },
    });

    useEffect(() => {
        if (organization) {
            getVenuesByOrganization(organization.id).then(setVenues);
        }
        getAllCategories().then(setCategories);
    }, [organization]);

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files) {
            const files = Array.from(e.target.files);
            if ((coverFiles.length + files.length) > maxPhotos) {
                toast.error(`You can only upload a maximum of ${maxPhotos} photos.`);
                return;
            }
            setCoverFiles(prev => [...prev, ...files]);
        }
    };

    const removeImage = (index: number) => {
        setCoverFiles(prev => prev.filter((_, i) => i !== index));
    };

    function onSubmit(data: CoreDetailsData) {
        console.log("Step 1 Data:", data);
        console.log("Cover Files:", coverFiles);
        onNext();
    }

    return (
        <FormProvider {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
                {/* Hero Cover Photos Section */}
                <Card>
                    <CardHeader>
                        <CardTitle>Cover Photos</CardTitle>
                        <CardDescription>This is the first thing people will see. Upload up
                            to {maxPhotos} images.</CardDescription>
                    </CardHeader>
                    <CardContent>
                        {coverFiles.length > 0 ? (
                            <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: true})]}>
                                <CarouselContent>
                                    {coverFiles.map((file, index) => (
                                        <CarouselItem key={index} className="relative">
                                            <div className="aspect-[21/9] w-full relative">
                                                <Image src={URL.createObjectURL(file)} alt={`Cover photo ${index + 1}`}
                                                       fill className="object-cover rounded-lg"/>
                                                <Button type="button" variant="destructive" size="icon"
                                                        className="absolute top-4 right-4 h-8 w-8 z-10"
                                                        onClick={() => removeImage(index)}><X
                                                    className="h-4 w-4"/></Button>
                                            </div>
                                        </CarouselItem>
                                    ))}
                                </CarouselContent>
                                <CarouselPrevious type="button"
                                                  className="absolute left-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                                <CarouselNext type="button"
                                              className="absolute right-4 bg-black/50 text-white border-none hover:bg-black/75"/>
                            </Carousel>
                        ) : (
                            <div
                                className="flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-12 text-center aspect-[21/9] w-full">
                                <ImageIcon className="h-16 w-16 text-muted-foreground"/>
                                <p className="mt-4 text-lg font-medium">Add cover photos for your event</p>
                                <Button type="button" className="mt-4"
                                        onClick={() => fileInputRef.current?.click()}><Upload className="mr-2 h-4 w-4"/>Upload
                                    Images</Button>
                            </div>
                        )}
                        <div className="mt-4 flex justify-center items-center gap-4">
                            {coverFiles.length > 0 && (<Button type="button" variant="outline"
                                                               onClick={() => fileInputRef.current?.click()}><PlusCircle
                                className="mr-2 h-4 w-4"/>Add More</Button>)}
                            <p className="text-sm text-muted-foreground">{coverFiles.length} of {maxPhotos} photos
                                selected.</p>
                        </div>
                        <Input id="picture" type="file" multiple accept="image/*" onChange={handleFileChange}
                               className="hidden" ref={fileInputRef}/>
                    </CardContent>
                </Card>

                {/* Event Details Card */}
                <Card>
                    <CardHeader>
                        <CardTitle>Event Details</CardTitle>
                        <CardDescription>Provide the core information about your event.</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                        <FormField control={form.control} name="title" render={({field}) => (
                            <FormItem><FormLabel>Event Title</FormLabel><FormControl><Input
                                placeholder="e.g., Annual Tech Conference 2025" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                        <FormField control={form.control} name="description" render={({field}) => (
                            <FormItem><FormLabel>Short Description</FormLabel><FormControl><Textarea
                                placeholder="A brief summary of your event." {...field} /></FormControl><FormMessage/></FormItem>)}/>
                        <FormField control={form.control} name="overview" render={({field}) => (
                            <FormItem><FormLabel>Overview</FormLabel><FormControl><Textarea
                                placeholder="Provide more details like schedule, speakers, etc."
                                className="min-h-32" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                    </CardContent>
                </Card>

                {/* Category Card - changed to single selection */}
                <Card>
                    <CardHeader>
                        <CardTitle>Category</CardTitle>
                        <CardDescription>Select one category that best fits your event.</CardDescription>
                    </CardHeader>
                    <CardContent>
                        <FormField
                            control={form.control}
                            name="categoryId"
                            render={({ field }) => (
                                <FormItem>
                                    <FormControl>
                                        <RadioGroup
                                            onValueChange={field.onChange}
                                            defaultValue={field.value}
                                            className="space-y-4"
                                        >
                                            {categories.map((parentCat) => (
                                                <div key={parentCat.id}>
                                                    <h3 className="font-semibold mb-2">{parentCat.name}</h3>
                                                    <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                                                        {parentCat.subCategories.map((subCat) => (
                                                            <FormItem key={subCat.id} className="flex items-center space-x-3 space-y-0">
                                                                <FormControl>
                                                                    <RadioGroupItem value={subCat.id} />
                                                                </FormControl>
                                                                <FormLabel className="font-normal">{subCat.name}</FormLabel>
                                                            </FormItem>
                                                        ))}
                                                    </div>
                                                </div>
                                            ))}
                                        </RadioGroup>
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />
                    </CardContent>
                </Card>

                {/* Location Card */}
                <Card>
                    <CardHeader>
                        <CardTitle>Location</CardTitle>
                        <CardDescription>Where will your event take place?</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-4">
                        <FormField control={form.control} name="isOnline" render={({field}) => (<FormItem
                            className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
                            <div className="space-y-0.5"><FormLabel>Online Event</FormLabel></div>
                            <FormControl><Switch checked={field.value} onCheckedChange={field.onChange}/></FormControl></FormItem>)}/>
                        {form.watch('isOnline') ? (
                            <FormField control={form.control} name="onlineLink" render={({field}) => (
                                <FormItem><FormLabel>Online Link (e.g., Zoom)</FormLabel><FormControl><Input
                                    placeholder="https://zoom.us/j/1234567890" {...field} /></FormControl><FormMessage/></FormItem>)}/>
                        ) : (
                            <FormField control={form.control} name="venueId" render={({field}) => (
                                <FormItem><FormLabel>Venue</FormLabel><Select onValueChange={field.onChange}
                                                                              defaultValue={field.value}><FormControl><SelectTrigger><SelectValue
                                    placeholder="Select a venue"/></SelectTrigger></FormControl><SelectContent>{venues.map(venue =>
                                    <SelectItem key={venue.id} value={venue.id}>{venue.name}</SelectItem>)}<SelectItem
                                    value="new-venue" className="text-primary"><PlusCircle
                                    className="inline-block mr-2 h-4 w-4"/>Create New Venue</SelectItem></SelectContent></Select><FormMessage/></FormItem>)}/>
                        )}
                    </CardContent>
                </Card>

                <div className="flex justify-end mt-8">
                    <Button type="submit">Next: Tiers & Pricing</Button>
                </div>
            </form>
        </FormProvider>
    )
}

'use client';

import * as React from 'react';
import {useState} from 'react';
import {CoreDetailsStep} from "@/app/manage/organization/[organization_id]/event/_components/coreDetailsStep";
import {WizardSidebar} from "@/app/manage/organization/[organization_id]/event/_components/WizardSideBar";

// --- Main Wizard Parent Component ---
export default function CreateEventPage() {
    const [step, setStep] = useState(1);
    const steps = [
        {number: 1, title: "Core Details", description: "Name, category, and location"},
        {number: 2, title: "Tiers & Pricing", description: "Set your ticket prices"},
        {number: 3, title: "Scheduling", description: "Add dates and times"},
        {number: 4, title: "Seating", description: "Configure seating maps"},
        {number: 5, title: "Review & Publish", description: "Final check before submission"},
    ];

    const renderStep = () => {
        switch (step) {
            case 1:
                return <CoreDetailsStep onNextAction={() => setStep(2)}/>;
            // Add cases for other steps here
            default:
                return <CoreDetailsStep onNextAction={() => setStep(2)}/>;
        }
    };

    return (
        <div className="flex min-h-screen bg-muted/40">
            <WizardSidebar currentStep={step} steps={steps}/>
            <main className="flex-1 p-4 md:p-8 overflow-y-auto">
                <div className="max-w-4xl mx-auto">
                    {renderStep()}
                </div>
            </main>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useEffect, useState} from 'react';
import {CoreDetailsStep} from "@/app/manage/organization/[organization_id]/event/_components/coreDetailsStep";
import {WizardSidebar} from "@/app/manage/organization/[organization_id]/event/_components/WizardSideBar";
import {useSidebar} from "@/components/ui/sidebar";

// --- Main Wizard Parent Component ---
export default function CreateEventPage() {
    const [step, setStep] = useState(1);
    const {setOpen} = useSidebar();
    const steps = [
        {number: 1, title: "Core Details", description: "Name, category, and location"},
        {number: 2, title: "Tiers & Pricing", description: "Set your ticket prices"},
        {number: 3, title: "Scheduling", description: "Add dates and times"},
        {number: 4, title: "Seating", description: "Configure seating maps"},
        {number: 5, title: "Review & Publish", description: "Final check before submission"},
    ];

    // Collapse the main sidebar when component mounts
    useEffect(() => {
        setOpen(false);
    }, [setOpen]);

    const renderStep = () => {
        switch (step) {
            case 1:
                return <CoreDetailsStep onNextAction={() => setStep(2)}/>;
            // Add cases for other steps here
            default:
                return <CoreDetailsStep onNextAction={() => setStep(2)}/>;
        }
    };

    return (
        <div className="flex min-h-screen bg-muted/40">
            <WizardSidebar currentStep={step} steps={steps}/>
            <main className="flex-1 p-4 md:p-8 overflow-y-auto ml-72">
                <div className="max-w-4xl mx-auto">
                    {renderStep()}
                </div>
            </main>
        </div>
    );
}

import {z} from 'zod';

export const coreDetailsSchema = z.object({
    title: z.string().min(3, {message: "Title must be at least 3 characters long."}).max(100),
    description: z.string().max(500).optional(),
    overview: z.string().optional(),
    categoryIds: z.string({required_error: "Please select a category."}),
    isOnline: z.boolean(),
    venueId: z.string().optional(),
    onlineLink: z.string().optional(),
    locationDescription: z.string().optional(),
}).refine(data => !data.isOnline ? !!data.venueId : true, {
    message: "A venue is required for physical events.",
    path: ["venueId"],
}).refine(data => data.isOnline ? !!data.onlineLink : true, {
    message: "An online link is required for online events.",
    path: ["onlineLink"],
});

export type CoreDetailsData = z.infer<typeof coreDetailsSchema>;

import {z} from 'zod';

export const coreDetailsSchema = z.object({
    title: z.string().min(3, {message: "Title must be at least 3 characters long."}).max(100),
    description: z.string().max(500).optional(),
    overview: z.string().optional(),
    categoryId: z.string().min(1, {message: "Please select a category."}),
    isOnline: z.boolean(),
    venueId: z.string().optional(),
    onlineLink: z.string().optional(),
    locationDescription: z.string().optional(),
}).refine(data => !data.isOnline ? !!data.venueId : true, {
    message: "A venue is required for physical events.",
    path: ["venueId"],
}).refine(data => data.isOnline ? !!data.onlineLink : true, {
    message: "An online link is required for online events.",
    path: ["onlineLink"],
});

export type CoreDetailsData = z.infer<typeof coreDetailsSchema>;

'use client';

import React, {createContext, useContext, useState, useEffect, ReactNode, useMemo} from 'react';
import {useAuth} from '@/providers/AuthProvider';
import {getAppConfiguration} from '@/lib/actions/configActions';
import {AppConfig, TierLimitDetails, TierName} from '@/types/config';

// Define the shape of the context
interface LimitContextType {
    config: AppConfig | null;
    isLoading: boolean;
    error: string | null;
    currentUserTier: TierName;
    currentUserTierLimits: TierLimitDetails | null;
    getLimitsForTier: (tier: TierName) => TierLimitDetails | null;
}

const LimitContext = createContext<LimitContextType | undefined>(undefined);

export const LimitProvider = ({children}: { children: ReactNode }) => {
    const {keycloak} = useAuth();
    const [config, setConfig] = useState<AppConfig | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Fetch the entire configuration on initial application load
    useEffect(() => {
        const fetchConfig = async () => {
            try {
                const appConfig = await getAppConfiguration();
                setConfig(appConfig);
            } catch (err) {
                setError('Failed to load application configuration. Some features may be unavailable.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };
        fetchConfig();
    }, []);

    // A memoized helper to get the current user's highest tier from their JWT
    const currentUserTier = useMemo((): TierName => {
        if (!keycloak?.tokenParsed?.user_groups || !Array.isArray(keycloak.tokenParsed.user_groups)) return 'FREE';

        const userGroups: string[] = keycloak.tokenParsed.user_groups;
        let highestTier: TierName = 'FREE';
        let maxLevel = 0;

        const tierLevels: Record<TierName, number> = {FREE: 0, PRO: 1, ENTERPRISE: 2};

        userGroups.forEach(group => {
            if (group.startsWith('/Tiers/')) {
                const tierName = group.substring('/Tiers/'.length).toUpperCase() as TierName;
                if (tierLevels[tierName] !== undefined && tierLevels[tierName] > maxLevel) {
                    maxLevel = tierLevels[tierName];
                    highestTier = tierName;
                }
            }
        });
        return highestTier;
    }, [keycloak]);

    // A memoized value for the current user's specific limits, derived from the full config
    const currentUserTierLimits = useMemo(() => {
        if (!config) return null;
        return config.tierLimits[currentUserTier] || config.tierLimits.FREE;
    }, [config, currentUserTier]);

    // A function to get limits for any specified tier (useful for upgrade pages)
    const getLimitsForTier = (tier: TierName): TierLimitDetails | null => {
        if (!config) return null;
        return config.tierLimits[tier] || null;
    };

    const value = {config, isLoading, error, currentUserTier, currentUserTierLimits, getLimitsForTier};

    return (
        <LimitContext.Provider value={value}>
            {children}
        </LimitContext.Provider>
    );
};

// The custom hook to easily access the limits from any component
export const useLimits = (): LimitContextType => {
    const context = useContext(LimitContext);
    if (context === undefined) {
        throw new Error('useLimits must be used within a LimitProvider');
    }
    return context;
};

