import * as React from "react";
import {
    AlertDialog,
    AlertDialogAction,
    AlertDialogCancel,
    AlertDialogContent,
    AlertDialogDescription,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogTitle
} from "@/components/ui/alert-dialog";

interface DeleteConfirmationDialogProps {
    isOpen: boolean;
    onOpenChange: (open: boolean) => void;
    layoutName: string | undefined;
    onConfirmDelete: () => void;
}

export function DeleteConfirmationDialog({
    isOpen,
    onOpenChange,
    layoutName,
    onConfirmDelete
}: DeleteConfirmationDialogProps) {
    return (
        <AlertDialog open={isOpen} onOpenChange={onOpenChange}>
            <AlertDialogContent>
                <AlertDialogHeader>
                    <AlertDialogTitle>Delete Layout Template</AlertDialogTitle>
                    <AlertDialogDescription>
                        Are you sure you want to delete &#34;{layoutName}&#34;? This action cannot be undone.
                    </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                    <AlertDialogAction
                        onClick={onConfirmDelete}
                        className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                    >
                        Delete
                    </AlertDialogAction>
                </AlertDialogFooter>
            </AlertDialogContent>
        </AlertDialog>
    );
}

import * as React from "react";
import {CheckCircle2} from "lucide-react";
import {cn} from "@/lib/utils";

type Step = {
    id: string;
    label: string;
}

interface ProgressStepsProps {
    steps: Step[];
    currentMode: string;
    onStepClick: (step: string) => void;
}

export function ProgressSteps({ steps, currentMode, onStepClick }: ProgressStepsProps) {
    return (
        <div className="mb-6">
            <div className="flex items-center justify-center">
                {steps.map((step, idx) => {
                    // Determine if this step is clickable (can't skip ahead, but can go back)
                    const currentStepIndex = steps.findIndex(s => s.id === currentMode);
                    const isClickable = idx <= currentStepIndex;

                    return (
                        <React.Fragment key={step.id}>
                            {/* Step indicator */}
                            <div
                                className={cn(
                                    "flex flex-col items-center",
                                    isClickable && "cursor-pointer"
                                )}
                                onClick={() => {
                                    if (!isClickable) return;
                                    onStepClick(step.id);
                                }}
                            >
                                <div
                                    className={cn(
                                        "flex items-center justify-center h-8 w-8 rounded-full border-2",
                                        currentMode === step.id
                                            ? "border-primary bg-primary text-primary-foreground"
                                            : idx < steps.findIndex(s => s.id === currentMode)
                                                ? "border-primary bg-primary/10 text-primary"
                                                : "border-muted-foreground text-muted-foreground"
                                    )}
                                >
                                    {idx < steps.findIndex(s => s.id === currentMode) ? (
                                        <CheckCircle2 className="h-5 w-5"/>
                                    ) : (
                                        <span>{idx + 1}</span>
                                    )}
                                </div>
                                <span className={cn(
                                    "text-xs mt-1",
                                    currentMode === step.id ? "text-primary font-medium" : "text-muted-foreground"
                                )}>
                                    {step.label}
                                </span>
                            </div>

                            {/* Connector line between steps */}
                            {idx < steps.length - 1 && (
                                <div
                                    className={cn(
                                        "w-12 h-[2px] mx-1",
                                        idx < steps.findIndex(s => s.id === currentMode)
                                            ? "bg-primary"
                                            : "bg-muted-foreground/30"
                                    )}
                                />
                            )}
                        </React.Fragment>
                    );
                })}
            </div>
        </div>
    );
}

'use client';

import * as React from 'react';
import {useEffect, useState} from 'react';
import {CoreDetailsStep} from "@/app/manage/organization/[organization_id]/event/_components/CoreDetailsStep";
import {Progress} from "@/components/ui/progress";
import {CreateEventRequest} from "@/types/event";
import {toast} from "sonner";
import {FormProvider, useForm} from "react-hook-form";
import {Button} from "@/components/ui/button";
import {TiersStep} from "@/app/manage/organization/[organization_id]/event/_components/TierStep";
import {zodResolver} from "@hookform/resolvers/zod";
import {coreDetailsSchema, tiersSchema} from "@/lib/validators/event";
import {WizardSidebar} from "../_components/WizardSideBar";
import {useSidebar} from "@/components/ui/sidebar";

// --- Main Wizard Parent Component ---
export default function CreateEventPage() {
    const [step, setStep] = useState(1);
    const [coverFiles, setCoverFiles] = useState<File[]>([]);
    const totalSteps = 5;
    const {setOpen} = useSidebar();

    const steps = [
        {number: 1, title: "Core Details", description: "Name, category, and location"},
        {number: 2, title: "Tiers & Pricing", description: "Set your ticket prices"},
        {number: 3, title: "Scheduling", description: "Add dates and times"},
        {number: 4, title: "Seating", description: "Configure seating maps"},
        {number: 5, title: "Review & Publish", description: "Final check before submission"},
    ];

    // Collapse the main sidebar when component mounts
    useEffect(() => {
        setOpen(false);
    }, [setOpen]);

    // ✅ The single form instance for the entire wizard is created here.
    const methods = useForm<CreateEventRequest>({
        defaultValues: {
            title: '',
            description: '',
            overview: '',
            organizationId: '', // Should be set from context/params
            categoryId: '',
            isOnline: false,
            tiers: [],
            sessions: [],
        },
        mode: 'onSubmit', // Only validate when explicitly triggered
    });

    // Update the validation schema based on the current step
    useEffect(() => {
        if (step === 1) {
            methods.clearErrors();
        } else if (step === 2) {
            methods.clearErrors();
        }
    }, [step, methods]);

    const validateCoreDetails = async () => {
        try {
            // Check for cover photos
            if (coverFiles.length === 0) {
                toast.error("Please upload at least one cover photo.");
                return false;
            }

            // Get form values for core details
            const values = methods.getValues();
            const coreData = {
                title: values.title,
                description: values.description || "",
                overview: values.overview || "",
                categoryId: values.categoryId || "",
                isOnline: values.isOnline || false,
                venueId: values.venueId,
                onlineLink: values.onlineLink,
                locationDescription: values.locationDescription,
            };

            // Validate with Zod schema
            const result = coreDetailsSchema.safeParse(coreData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.path.join('.')}: ${err.message}`);
                });

                // Also set form errors for field highlighting
                result.error.errors.forEach(err => {
                    const path = err.path.join('.') as keyof CreateEventRequest;
                    methods.setError(path, { 
                        type: 'manual', 
                        message: err.message 
                    });
                });

                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Validation error:", error);
            toast.error("Please check all required fields");
            return false;
        }
    };

    const validateTiers = async () => {
        try {
            // Get tiers data
            const values = methods.getValues();
            const tiersData = { tiers: values.tiers || [] };

            // Validate with Zod schema
            const result = tiersSchema.safeParse(tiersData);

            if (!result.success) {
                // Show validation errors
                result.error.errors.forEach(err => {
                    toast.error(`${err.message}`);
                });
                return false;
            }

            return true;
        } catch (error: any) {
            console.error("Tier validation error:", error);
            toast.error("Please add at least one tier with valid information");
            return false;
        }
    };

    const onNext = async () => {
        let isValid = false;

        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        } else {
            // For future steps that don't have validation yet
            isValid = true;
        }

        if (isValid) {
            setStep(s => Math.min(totalSteps, s + 1));
        }
    };

    const onPrev = () => setStep(s => Math.max(1, s - 1));

    const onSubmit = async (data: CreateEventRequest) => {
        // Perform final validation before submission
        let isValid = true;

        // Validate the current step first
        if (step === 1) {
            isValid = await validateCoreDetails();
        } else if (step === 2) {
            isValid = await validateTiers();
        }

        if (isValid) {
            console.log("Final Assembled Form Data:", data);
            console.log("Final Cover Files:", coverFiles);
            // Here you would call your createEvent API action
            toast.success("Event submitted for approval!");
        }
    };

    const renderStep = () => {
        switch (step) {
            case 1:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
            case 2:
                return <TiersStep/>;
            // Add cases for other steps here
            default:
                return <CoreDetailsStep coverFiles={coverFiles} setCoverFilesAction={setCoverFiles}/>;
        }
    };

    return (
        <div className="flex min-h-screen bg-muted/40">
            <WizardSidebar currentStep={step} steps={steps}/>
            <main className="flex-1 p-4 md:p-8 overflow-y-auto ml-72">
                <div className="max-w-4xl mx-auto">
                    <div className="mb-8">
                        <p className="text-sm text-muted-foreground">Step {step} of {totalSteps}</p>
                        <Progress value={(step / totalSteps) * 100} className="mt-2"/>
                    </div>

                    {/* ✅ The FormProvider broadcasts the form state to all children */}
                    <FormProvider {...methods}>
                        <form onSubmit={methods.handleSubmit(onSubmit)}>
                            {renderStep()}

                            <div className="flex justify-between mt-8">
                                <Button type="button" variant="outline" onClick={onPrev} disabled={step === 1}>
                                    Previous
                                </Button>
                                {step < totalSteps ? (
                                    <Button type="button" onClick={onNext}>
                                        Next
                                    </Button>
                                ) : (
                                    <Button type="submit">
                                        Submit for Approval
                                    </Button>
                                )}
                            </div>
                        </form>
                    </FormProvider>
                </div>
            </main>
        </div>
    );
}

import {CreateEventFormData} from "@/lib/validators/event";
import {apiFetch} from '@/lib/api';
import {EventDetailDTO, EventResponseDTO, EventStatus, EventSummaryDTO} from "@/types/event";

const API_BASE_PATH = '/event-seating/v1/events';

/**
 * Create a new event
 * @param eventData Form data for the new event
 * @param coverImages Cover images for the event
 */
export async function createEvent(eventData: CreateEventFormData, coverImages: File[]): Promise<EventResponseDTO> {
    try {
        const formData = new FormData();

        // Convert the event data to a JSON string and add it as a part
        formData.append('request', JSON.stringify(eventData));

        // Add each cover image as a separate part
        if (coverImages && coverImages.length > 0) {
            coverImages.forEach((file) => {
                formData.append('coverImages', file);
            });
        }

        // Use apiFetch instead of raw fetch
        return await apiFetch<EventResponseDTO>(`${API_BASE_PATH}`, {
            method: 'POST',
            body: formData,
            // No need to set headers, apiFetch handles that
        });
    } catch (error) {
        console.error('Error creating event:', error);
        throw error;
    }
}

/**
 * Fetch event details by ID
 * @param eventId The ID of the event to fetch
 */
export async function getEventById(eventId: string): Promise<EventDetailDTO> {
    try {
        return await apiFetch<EventDetailDTO>(`${API_BASE_PATH}/${eventId}`, {
            method: 'GET',
        });
    } catch (error) {
        console.error(`Error fetching event ${eventId}:`, error);
        throw error;
    }
}

/**
 * Fetch all events (admin only)
 * @param status Optional filter for event status
 * @param search Optional search term for filtering events
 * @param page Page number (0-based)
 * @param size Number of events per page
 */
export async function getAllEvents(
    status?: EventStatus,
    search?: string,
    page: number = 0,
    size: number = 10
): Promise<{ content: EventSummaryDTO[], totalPages: number, totalElements: number }> {
    try {
        let url = `${API_BASE_PATH}?page=${page}&size=${size}`;

        if (status) {
            url += `&status=${status}`;
        }

        if (search) {
            url += `&search=${encodeURIComponent(search)}`;
        }

        return await apiFetch<{ content: EventSummaryDTO[], totalPages: number, totalElements: number }>(url, {
            method: 'GET',
        });
    } catch (error) {
        console.error('Error fetching events:', error);
        throw error;
    }
}

/**
 * Approve an event (admin only)
 * @param eventId The ID of the event to approve
 */
export async function approveEvent(eventId: string): Promise<void> {
    try {
        await apiFetch<void>(`${API_BASE_PATH}/${eventId}/approve`, {
            method: 'POST',
        });
    } catch (error) {
        console.error(`Error approving event ${eventId}:`, error);
        throw error;
    }
}

/**
 * Reject an event (admin only)
 * @param eventId The ID of the event to reject
 * @param reason The reason for rejecting the event
 */
export async function rejectEvent(eventId: string, reason: string): Promise<void> {
    try {
        await apiFetch<void>(`${API_BASE_PATH}/${eventId}/reject`, {
            method: 'POST',
            body: JSON.stringify({reason}),
        });
    } catch (error) {
        console.error(`Error rejecting event ${eventId}:`, error);
        throw error;
    }
}

/**
 * Delete an event
 * @param eventId The ID of the event to delete
 */
export async function deleteEvent(eventId: string): Promise<void> {
    try {
        await apiFetch<void>(`${API_BASE_PATH}/${eventId}`, {
            method: 'DELETE',
        });
    } catch (error) {
        console.error(`Error deleting event ${eventId}:`, error);
        throw error;
    }
}

