import {SeatDTO, SeatingBlockDTO} from "@/types/event";
import {Button} from "@/components/ui/button";
import {Badge} from "@/components/ui/badge";
import {cn} from "@/lib/utils";
import {ReadModelSeatStatus} from "@/lib/validators/enums";
import {TooltipContent, TooltipTrigger, Tooltip} from "@/components/ui/tooltip";

export const SeatedGridBlock = ({block, selectedSeats, onSeatSelect}: {
    block: SeatingBlockDTO;
    selectedSeats?: string[];
    onSeatSelect?: (seat: SeatDTO, blockName: string) => void;
}) => (
    <>
        <div className="text-sm font-medium mb-2 text-center text-foreground">{block.name}</div>
        <div
            className="grid gap-1.5"
            style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, minmax(0, 1fr))`}}
        >
            {block.rows?.map(row =>
                row.seats.map(seat => {
                    const seatStatus = seat.status || ReadModelSeatStatus.AVAILABLE;
                    const isDisabled = seatStatus !== ReadModelSeatStatus.AVAILABLE;
                    const isSelected = !!selectedSeats?.some(s => s === seat.id);

                    return (
                        <Tooltip key={seat.id}>
                            <TooltipTrigger asChild>
                                <Button
                                    type="button"
                                    variant="ghost"
                                    className={cn(
                                        "h-6 w-6 p-0 rounded-full text-xs font-mono transition-transform duration-200 hover:scale-110",
                                        isDisabled ? "opacity-30 cursor-not-allowed" : "cursor-pointer hover:brightness-105",
                                        isSelected && "ring-2 ring-offset-2 ring-offset-background ring-primary"
                                    )}
                                    style={{backgroundColor: seat.tier ? `${seat.tier.color}80` : undefined}}
                                    disabled={isDisabled || !onSeatSelect}
                                    onClick={() => onSeatSelect?.(seat, block.name)}
                                >
                                    {seat.label}
                                </Button>
                            </TooltipTrigger>
                            <TooltipContent side="top"
                                            className="p-2 shadow-md border-border bg-popover">
                                <div className="flex items-center gap-2">
                                    <Badge
                                        variant={isDisabled ? 'destructive' : 'outline'}
                                        className="text-xs px-1"
                                    >
                                        {seatStatus.replace('_', ' ')}
                                    </Badge>
                                    {seat.tier && (
                                        <>
                                            <div className="flex items-center gap-1">
                                                <div className="h-2 w-2 rounded-full"
                                                     style={{backgroundColor: seat.tier.color}}/>
                                                <span className="text-xs">{seat.tier.name}</span>
                                            </div>
                                            <span
                                                className="text-xs font-medium">{new Intl.NumberFormat('en-LK', {
                                                style: 'currency',
                                                currency: 'LKR'
                                            }).format(seat.tier.price)}</span>
                                        </>
                                    )}
                                </div>
                            </TooltipContent>
                        </Tooltip>
                    );
                })
            )}
        </div>
    </>
);

import {SeatDTO, SeatingBlockDTO} from "@/types/event";
import {Button} from "@/components/ui/button";
import {Badge} from "@/components/ui/badge";
import {cn} from "@/lib/utils";
import {ReadModelSeatStatus} from "@/lib/validators/enums";
import {TooltipContent, TooltipTrigger, Tooltip} from "@/components/ui/tooltip";
import {Lock, X, Clock} from "lucide-react";

export const SeatedGridBlock = ({block, selectedSeats, onSeatSelect}: {
    block: SeatingBlockDTO;
    selectedSeats?: string[];
    onSeatSelect?: (seat: SeatDTO, blockName: string) => void;
}) => (
    <>
        <div className="text-sm font-medium mb-2 text-center text-foreground">{block.name}</div>
        <div
            className="grid gap-1.5"
            style={{gridTemplateColumns: `repeat(${block.rows?.[0]?.seats?.length || 1}, minmax(0, 1fr))`}}
        >
            {block.rows?.map(row =>
                row.seats.map(seat => {
                    const seatStatus = seat.status || ReadModelSeatStatus.AVAILABLE;
                    const isDisabled = seatStatus !== ReadModelSeatStatus.AVAILABLE;
                    const isSelected = !!selectedSeats?.some(s => s === seat.id);

                    // Get status-specific styling
                    const getStatusStyles = () => {
                        switch(seatStatus) {
                            case ReadModelSeatStatus.AVAILABLE:
                                return "cursor-pointer hover:brightness-105";
                            case ReadModelSeatStatus.LOCKED:
                                return "bg-amber-200 text-amber-900 cursor-not-allowed border-amber-500 border";
                            case ReadModelSeatStatus.RESERVED:
                                return "bg-red-200 text-red-900 cursor-not-allowed border-red-500 border";
                            case ReadModelSeatStatus.BOOKED:
                                return "bg-gray-300 text-gray-700 cursor-not-allowed";
                            default:
                                return "opacity-30 cursor-not-allowed";
                        }
                    };

                    // Get status icon
                    const StatusIcon = () => {
                        switch(seatStatus) {
                            case ReadModelSeatStatus.LOCKED:
                                return <Lock className="absolute top-0 right-0 h-2.5 w-2.5 text-amber-600" />;
                            case ReadModelSeatStatus.RESERVED:
                                return <X className="absolute top-0 right-0 h-2.5 w-2.5 text-red-600" />;
                            case ReadModelSeatStatus.BOOKED:
                                return <Clock className="absolute top-0 right-0 h-2.5 w-2.5 text-gray-600" />;
                            default:
                                return null;
                        }
                    };

                    return (
                        <Tooltip key={seat.id}>
                            <TooltipTrigger asChild>
                                <div className="relative">
                                    <Button
                                        type="button"
                                        variant="ghost"
                                        className={cn(
                                            "h-6 w-6 p-0 rounded-full text-xs font-mono transition-transform duration-200 hover:scale-110",
                                            getStatusStyles(),
                                            isSelected && "ring-2 ring-offset-2 ring-offset-background ring-primary"
                                        )}
                                        style={{
                                            backgroundColor: seatStatus === ReadModelSeatStatus.AVAILABLE && seat.tier 
                                                ? `${seat.tier.color}80` 
                                                : undefined
                                        }}
                                        disabled={isDisabled || !onSeatSelect}
                                        onClick={() => onSeatSelect?.(seat, block.name)}
                                    >
                                        {seat.label}
                                    </Button>
                                    <StatusIcon />
                                </div>
                            </TooltipTrigger>
                            <TooltipContent side="top"
                                            className="p-2 shadow-md border-border bg-popover">
                                <div className="flex items-center gap-2">
                                    <Badge
                                        variant={
                                            seatStatus === ReadModelSeatStatus.LOCKED 
                                                ? 'warning' 
                                                : seatStatus === ReadModelSeatStatus.RESERVED || seatStatus === ReadModelSeatStatus.BOOKED
                                                    ? 'destructive' 
                                                    : 'outline'
                                        }
                                        className="text-xs px-1"
                                    >
                                        {seatStatus === ReadModelSeatStatus.LOCKED 
                                            ? 'PAYMENT PROCESSING'
                                            : seatStatus.replace('_', ' ')}
                                    </Badge>
                                    {seat.tier && (
                                        <>
                                            <div className="flex items-center gap-1">
                                                <div className="h-2 w-2 rounded-full"
                                                     style={{backgroundColor: seat.tier.color}}/>
                                                <span className="text-xs">{seat.tier.name}</span>
                                            </div>
                                            <span
                                                className="text-xs font-medium">{new Intl.NumberFormat('en-LK', {
                                                style: 'currency',
                                                currency: 'LKR'
                                            }).format(seat.tier.price)}</span>
                                        </>
                                    )}
                                </div>
                            </TooltipContent>
                        </Tooltip>
                    );
                })
            )}
        </div>
    </>
);

import {SessionInfoBasicDTO} from '@/types/event';
import {Calendar, Clock, MapPin, ArrowLeft} from 'lucide-react';
import Link from 'next/link';
import {Button} from '@/components/ui/button';
import {SessionMap} from '@/app/(home-app)/events/[event_id]/_components/SessionMap';

export const SessionDetailsHeader = ({session, eventId}: { session: SessionInfoBasicDTO, eventId?: string }) => {

    const formatDate = (iso: string) => new Date(iso).toLocaleDateString("en-US", {
        weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
    });
    const formatTime = (iso: string) => new Date(iso).toLocaleTimeString("en-US", {
        hour: '2-digit', minute: '2-digit', hour12: true
    });

    return (
        <div>
            {eventId &&
                <div className="flex items-center mb-2">
                    <Link href={`/events/${eventId}`} passHref>
                        <Button variant="ghost" size="sm" className="gap-1 px-2 h-8">
                            <ArrowLeft className="h-4 w-4"/>
                            <span>All Sessions</span>
                        </Button>
                    </Link>
                </div>
            }
            <h1 className="text-2xl sm:text-3xl font-bold tracking-tight text-foreground">{'Session Details'}</h1>
            <div className="mt-2 flex flex-wrap items-center gap-x-6 gap-y-2 text-muted-foreground">
                <div className="flex items-center gap-2"><Calendar
                    className="h-4 w-4"/><span>{formatDate(session.startTime)}</span></div>
                <div className="flex items-center gap-2"><Clock
                    className="h-4 w-4"/><span>{formatTime(session.startTime)} - {formatTime(session.endTime)}</span>
                </div>
                {session.sessionType === 'PHYSICAL' &&
                    <div className="flex items-center gap-2"><MapPin
                        className="h-4 w-4"/><span>{session.venueDetails.name}</span></div>
                }
            </div>

            {/* Add map for physical events */}
            {session.sessionType === 'PHYSICAL' && session.venueDetails.location && (
                <div className="mt-4 h-64 rounded-md overflow-hidden border">
                    <SessionMap location={session.venueDetails.location} />
                </div>
            )}
        </div>
    );
};

'use client';

import * as React from 'react';
import Image from 'next/image';
import {Tag} from 'lucide-react';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Autoplay from 'embla-carousel-autoplay';
import {OrganizationResponse} from "@/types/oraganizations";
import {Avatar, AvatarFallback, AvatarImage} from "@/components/ui/avatar";
import {EventBasicInfoDTO} from "@/types/event";

export const ReviewEventHero: React.FC<EventBasicInfoDTO> = ({
                                                                    title,
                                                                    categoryName,
                                                                    organization,
                                                                    coverFiles
                                                                }) => {

    // Helper function to get image URL
    const getImageUrl = (file: File | string): string => {
        if (typeof file === 'string') {
            return file; // Assuming it's already a URL
        }
        return URL.createObjectURL(file); // Create a local URL for the file
    };

    return (
        <div className="space-y-6">
            {coverFiles.length > 0 && (
                <div className="w-full rounded-xl overflow-hidden">
                    <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                        <CarouselContent>
                            {coverFiles.map((file, index) => (
                                <CarouselItem key={index}>
                                    <div className="aspect-[21/9] w-full relative">
                                        <Image
                                            src={getImageUrl(file)}
                                            alt={`Cover photo ${index + 1}`}
                                            fill
                                            className="object-cover rounded-lg"
                                        />
                                    </div>
                                </CarouselItem>
                            ))}
                        </CarouselContent>
                        <CarouselPrevious className="absolute left-4"/>
                        <CarouselNext className="absolute right-4"/>
                    </Carousel>
                </div>
            )}

            <div className="space-y-4">
                <h1 className="text-4xl font-bold tracking-tight">{title}</h1>

                <div className="flex flex-wrap items-center gap-4 text-sm">

                    {organization?.name && (
                        <div className="flex items-center gap-1">
                            <Avatar className="h-6 w-6">
                                <AvatarImage src={organization.logoUrl} alt={organization.name}/>
                                <AvatarFallback> {organization.name.charAt(0).toUpperCase()} </AvatarFallback>
                            </Avatar>
                            <span>By {organization.name}</span>
                        </div>
                    )}

                    {categoryName && (
                        <div className="flex items-center gap-1">
                            <Tag className="h-4 w-4 text-muted-foreground"/>
                            <span>{categoryName}</span>
                        </div>
                    )}

                </div>
            </div>
        </div>
    );
};

'use client';

import * as React from 'react';
import Image from 'next/image';
import {Tag} from 'lucide-react';
import {Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious} from "@/components/ui/carousel";
import Autoplay from 'embla-carousel-autoplay';
import {Avatar, AvatarFallback, AvatarImage} from "@/components/ui/avatar";
import {EventBasicInfoDTO} from "@/types/event";

function getImageUrl(url: string) {
    // If the URL is already absolute, use it directly
    if (url.startsWith('http')) return url;
    // Otherwise, treat as relative to public
    return `/${url}`;
}

export const ReviewEventHero: React.FC<{ event: EventBasicInfoDTO }> = ({ event }) => {
    const { title, overview, coverPhotos, organization, category } = event;

    return (
        <div className="space-y-6">
            {coverPhotos && coverPhotos.length > 0 && (
                <div className="w-full rounded-xl overflow-hidden">
                    <Carousel plugins={[Autoplay({delay: 4000, stopOnInteraction: false})]}>
                        <CarouselContent>
                            {coverPhotos.map((url, index) => (
                                <CarouselItem key={index}>
                                    <div className="aspect-[21/9] w-full relative">
                                        <Image
                                            src={getImageUrl(url)}
                                            alt={`Cover photo ${index + 1}`}
                                            fill
                                            className="object-cover rounded-lg"
                                        />
                                    </div>
                                </CarouselItem>
                            ))}
                        </CarouselContent>
                        <CarouselPrevious className="absolute left-4"/>
                        <CarouselNext className="absolute right-4"/>
                    </Carousel>
                </div>
            )}
            <div className="flex items-center gap-4">
                {organization && (
                    <Avatar className="h-12 w-12 border flex-shrink-0">
                        <AvatarImage src={organization.logoUrl || ''} alt={organization.name}/>
                        <AvatarFallback>{organization.name?.charAt(0).toUpperCase()}</AvatarFallback>
                    </Avatar>
                )}
                <div className="flex flex-col gap-1 min-w-0">
                    <h1 className="text-2xl md:text-3xl font-bold truncate">{title}</h1>
                    {category && (
                        <div className="flex items-center gap-2 text-muted-foreground text-sm">
                            <Tag className="w-4 h-4"/>
                            <span>{category.name}</span>
                            {category.parentName && <span className="ml-2 text-xs">({category.parentName})</span>}
                        </div>
                    )}
                </div>
            </div>
            {overview && (
                <div className="prose max-w-2xl text-muted-foreground">
                    {overview}
                </div>
            )}
        </div>
    );
};

import {SeatDTO, SessionSeatingMapDTO} from "@/types/event";
import {SeatingBlock, SeatingBlockPreview} from "@/app/(home-app)/events/[event_id]/_components/SeatingBlock";
import {SelectedSeat} from "@/app/(home-app)/events/[event_id]/_components/SessionBookingPage";


export const SeatingLayoutPreview = ({seatingMap}: { seatingMap: SessionSeatingMapDTO }) => {
    return (
        <div className="border rounded-lg p-4 bg-card">
            <h3 className="font-semibold mb-4 text-foreground">Seating Layout: {seatingMap.name}</h3>
            <div
                className="relative min-h-[400px] p-4 rounded-lg overflow-auto"
                // Elegant dot-grid background
                style={{
                    backgroundImage: 'radial-gradient(circle at 1px 1px, hsl(var(--muted)) 1px, transparent 0)',
                    backgroundSize: '20px 20px'
                }}
            >
                {seatingMap.layout.blocks.map(block => (
                    <SeatingBlockPreview key={block.id} block={block}/>
                ))}
            </div>
        </div>
    );
};

export const SeatingLayout = ({
                                  seatingMap,
                                  selectedSeats,
                                  onSeatSelect,
                              }: {
    seatingMap: SessionSeatingMapDTO;
    selectedSeats?: SelectedSeat[]; // Make optional for reuse
    onSeatSelect?: (seat: SeatDTO, blockName: string, rowLabel: string) => void; // Make optional for reuse
}) => {
    return (
        <div className="border rounded-lg p-4 bg-background/50">
            <h3 className="font-semibold mb-4 text-foreground">Seating Layout: {seatingMap.name}</h3>
            <div
                className="relative flex p-4 rounded-lg"
                style={{
                    backgroundImage: 'radial-gradient(circle at 1px 1px, hsl(var(--muted)) 1px, transparent 0)',
                    backgroundSize: '20px 20px',
                }}
            >
                {seatingMap.layout.blocks.map(block => (
                    <SeatingBlock
                        key={block.id}
                        block={block}
                        selectedSeats={selectedSeats}
                        onSeatSelect={onSeatSelect}
                    />
                ))}
            </div>
        </div>
    );
};

import {SeatDTO, SessionSeatingMapDTO} from "@/types/event";
import {SeatingBlock, SeatingBlockPreview} from "@/app/(home-app)/events/[event_id]/_components/SeatingBlock";
import {SelectedSeat} from "@/app/(home-app)/events/[event_id]/_components/SessionBookingPage";


export const SeatingLayoutPreview = ({seatingMap}: { seatingMap: SessionSeatingMapDTO }) => {
    return (
        <div className="border rounded-lg p-4 bg-card">
            <h3 className="font-semibold mb-4 text-foreground">Seating Layout: {seatingMap.name}</h3>
            <div
                className="relative p-4 rounded-lg overflow-auto"
                // Elegant dot-grid background
                style={{
                    backgroundImage: 'radial-gradient(circle at 1px 1px, hsl(var(--muted)) 1px, transparent 0)',
                    backgroundSize: '20px 20px',
                    height: 'auto',
                    minHeight: '400px'
                }}
            >
                {seatingMap.layout.blocks.map(block => (
                    <SeatingBlockPreview key={block.id} block={block}/>
                ))}
            </div>
        </div>
    );
};

export const SeatingLayout = ({
                                  seatingMap,
                                  selectedSeats,
                                  onSeatSelect,
                              }: {
    seatingMap: SessionSeatingMapDTO;
    selectedSeats?: SelectedSeat[]; // Make optional for reuse
    onSeatSelect?: (seat: SeatDTO, blockName: string, rowLabel: string) => void; // Make optional for reuse
}) => {
    // Find the furthest y coordinate plus the height of the block to determine the container height
    const calculateRequiredHeight = () => {
        let maxBottom = 0;
        seatingMap.layout.blocks.forEach(block => {
            const bottom = block.position.y + (block.height || 0);
            if (bottom > maxBottom) {
                maxBottom = bottom;
            }
        });
        // Add padding to ensure blocks aren't cut off
        return maxBottom + 50;
    };

    const containerHeight = calculateRequiredHeight();

    return (
        <div className="border rounded-lg p-4 bg-background/50">
            <h3 className="font-semibold mb-4 text-foreground">Seating Layout: {seatingMap.name}</h3>
            <div
                className="relative p-4 rounded-lg overflow-x-auto"
                style={{
                    backgroundImage: 'radial-gradient(circle at 1px 1px, hsl(var(--muted)) 1px, transparent 0)',
                    backgroundSize: '20px 20px',
                    height: `${containerHeight}px`,
                    overflowY: 'hidden'
                }}
            >
                {seatingMap.layout.blocks.map(block => (
                    <SeatingBlock
                        key={block.id}
                        block={block}
                        selectedSeats={selectedSeats}
                        onSeatSelect={onSeatSelect}
                    />
                ))}
            </div>
        </div>
    );
};

'use client'

import React from 'react';
import {MapContainer, TileLayer, Marker, Popup} from "react-leaflet";
import {Skeleton} from "@/components/ui/skeleton";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import {useTheme} from "next-themes";

// Fix default marker icons (Leaflet requires this in React/Next.js setups)
const defaultIcon = new L.Icon({
    iconUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    iconRetinaUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    shadowUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
});
L.Marker.prototype.options.icon = defaultIcon;

export const SessionMap = ({location, scrollWheelZoom}: {
    location: { coordinates: [number, number] },
    scrollWheelZoom?: boolean
}) => {
    const [isMounted, setIsMounted] = React.useState(false);
    const {resolvedTheme} = useTheme();
    const isDarkMode = resolvedTheme === "dark";

    React.useEffect(() => {
        setIsMounted(true);
    }, []);

    if (!isMounted) {
        return <Skeleton className="h-64 w-full rounded-md"/>;
    }

    // Convert coordinates to Leaflet format [lat, lng]
    const position: [number, number] = [location.coordinates[1], location.coordinates[0]];

    // Tile URLs
    const lightTiles = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";
    const darkTiles = "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png";

    // Attribution
    const attribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/">CARTO</a>';

    return (
        <div className="h-full w-full rounded-md overflow-hidden z-0">
            <MapContainer
                center={position}
                zoom={13}
                scrollWheelZoom={scrollWheelZoom || false}
                style={{height: '100%', width: '100%'}}
            >
                <TileLayer
                    attribution={attribution}
                    url={isDarkMode ? darkTiles : lightTiles}
                />
                <Marker position={position}>
                    <Popup>Event Location</Popup>
                </Marker>
            </MapContainer>
        </div>
    );
};

'use client'

import React from 'react';
import {MapContainer, TileLayer, Marker, Popup} from "react-leaflet";
import {Skeleton} from "@/components/ui/skeleton";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import {useTheme} from "next-themes";

// Fix default marker icons (Leaflet requires this in React/Next.js setups)
const defaultIcon = new L.Icon({
    iconUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    iconRetinaUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    shadowUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
});
L.Marker.prototype.options.icon = defaultIcon;

export const SessionMap = ({location, scrollWheelZoom}: {
    location: { coordinates: [number, number] },
    scrollWheelZoom?: boolean
}) => {
    const [isMounted, setIsMounted] = React.useState(false);
    const {resolvedTheme} = useTheme();
    const isDarkMode = resolvedTheme === "dark";

    React.useEffect(() => {
        setIsMounted(true);
    }, []);

    if (!isMounted) {
        return <Skeleton className="h-64 w-full rounded-md"/>;
    }

    // Convert coordinates to Leaflet format [lat, lng]
    const position: [number, number] = [location.coordinates[1], location.coordinates[0]];

    // Tile URLs
    const lightTiles = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";
    const darkTiles = "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png";

    // Attribution
    const attribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/">CARTO</a>';

    return (
        <div className="h-full w-full rounded-md overflow-hidden" style={{zIndex: 10, position: 'relative'}}>
            <MapContainer
                center={position}
                zoom={13}
                scrollWheelZoom={scrollWheelZoom || false}
                style={{height: '100%', width: '100%', zIndex: 10, position: 'relative'}}
            >
                <TileLayer
                    attribution={attribution}
                    url={isDarkMode ? darkTiles : lightTiles}
                />
                <Marker position={position}>
                    <Popup>Event Location</Popup>
                </Marker>
            </MapContainer>
        </div>
    );
};

'use client';

import React, { useEffect, useState } from 'react';
import { SessionInfoBasicDTO } from "@/types/event";
import { getEventSessionsInRange } from '@/lib/actions/public/eventActions';
import { CalendarIcon } from "lucide-react";
import { SessionItem } from "@/app/(home-app)/events/[event_id]/_components/SessionItem";

const SessionsNoPagination = ({ eventId }: { eventId: string }) => {
    const [sessions, setSessions] = useState<SessionInfoBasicDTO[] | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchSessions = async () => {
            setIsLoading(true);

            // Get current date and a date one month from now
            const fromDate = new Date().toISOString();
            const toDate = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

            try {
                const data = await getEventSessionsInRange({ eventId, fromDate, toDate });
                setSessions(data);
            } catch (error) {
                console.error("Failed to fetch sessions", error);
            } finally {
                setIsLoading(false);
            }
        };

        fetchSessions();
    }, [eventId]);

    return (
        <div id="sessions-section" className="max-w-7xl mx-auto space-y-4 scroll-mt-16">
            <h2 className="text-2xl font-bold text-foreground flex items-center gap-2">
                <CalendarIcon className="w-6 h-6"/> Available Sessions
            </h2>
            {isLoading ? (
                <div>Loading sessions...</div>
            ) : sessions && sessions.length > 0 ? (
                <div className="space-y-2">
                    {sessions.map(session => (
                        <SessionItem key={session.id} session={session} />
                    ))}
                </div>
            ) : (
                <div>No upcoming sessions available.</div>
            )}
        </div>
    );
};

export default SessionsNoPagination;

'use client';

import {useRouter, usePathname, useSearchParams} from 'next/navigation';
import {useCallback} from 'react';
import {Input} from "@/components/ui/input";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Button} from "@/components/ui/button";
import {Label} from "@/components/ui/label";
import {Card} from "@/components/ui/card";
import {Separator} from "@/components/ui/separator";
import {CategoryResponseWithParentName} from '@/types/category';
import {sriLankaLocations} from '../../_utils/locations';
import {Search, MapPin, Tag, X} from 'lucide-react';

interface EventFiltersProps {
    categories: CategoryResponseWithParentName[];
    inHero?: boolean;
}

export function EventFilters({categories, inHero = false}: EventFiltersProps) {
    const router = useRouter();
    const pathname = usePathname();
    const searchParams = useSearchParams();

    const createQueryString = useCallback(
        (name: string, value: string) => {
            const params = new URLSearchParams(searchParams.toString());
            if (value) params.set(name, value);
            else params.delete(name);

            params.delete('page'); // reset pagination
            return params.toString();
        },
        [searchParams]
    );

    const handleSelectChange = (name: string) => (value: string) => {
        const finalValue = value === 'all' ? '' : value;
        router.push(pathname + '?' + createQueryString(name, finalValue));
    };

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        router.push(pathname + '?' + createQueryString(event.target.name, event.target.value));
    };

    const clearFilters = () => {
        router.push(pathname);
    };

    const hasActiveFilters = searchParams.size > 0;

    /* ---------------- Hero Layout ---------------- */
    if (inHero) {
        return (
            <Card className="bg-card/70 dark:bg-card/80 backdrop-blur-md border shadow-xl rounded-2xl p-6">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {/* Search */}
                    <div className="space-y-2">
                        <Label htmlFor="searchTerm">Search by name</Label>
                        <div className="relative">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-primary"/>
                            <Input
                                id="searchTerm"
                                placeholder="Event title or keyword..."
                                name="searchTerm"
                                defaultValue={searchParams.get('searchTerm') || ''}
                                onChange={handleInputChange}
                                className="pl-9"
                            />
                        </div>
                    </div>

                    {/* Category */}
                    <div className="space-y-2">
                        <Label>Category</Label>
                        <Select
                            value={searchParams.get('categoryId') || 'all'}
                            onValueChange={handleSelectChange('categoryId')}
                        >
                            <SelectTrigger className="w-full">
                                <Tag className="mr-2 h-4 w-4 text-primary"/>
                                <SelectValue placeholder="All Categories"/>
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="all">All Categories</SelectItem>
                                {categories.map(cat => (
                                    <SelectItem key={cat.id} value={cat.id}>{cat.name}</SelectItem>
                                ))}
                            </SelectContent>
                        </Select>
                    </div>

                    {/* Location */}
                    <div className="space-y-2">
                        <Label>Location</Label>
                        <Select
                            value={searchParams.get('location') || 'all'}
                            onValueChange={(value) => {
                                const params = new URLSearchParams(searchParams.toString());
                                if (value === 'all') {
                                    params.delete('location');
                                    params.delete('latitude');
                                    params.delete('longitude');
                                    params.delete('radiusKm');
                                } else {
                                    const location = sriLankaLocations.find(l => l.name === value);
                                    if (location) {
                                        params.set('location', location.name);
                                        params.set('latitude', location.latitude.toString());
                                        params.set('longitude', location.longitude.toString());
                                        params.set('radiusKm', '50');
                                    }
                                }
                                params.delete('page');
                                router.push(pathname + '?' + params.toString());
                            }}
                        >
                            <SelectTrigger className="w-full">
                                <MapPin className="mr-2 h-4 w-4 text-primary"/>
                                <SelectValue placeholder="Anywhere in Sri Lanka"/>
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="all">Anywhere in Sri Lanka</SelectItem>
                                {sriLankaLocations.map(loc => (
                                    <SelectItem key={loc.name} value={loc.name}>{loc.name}</SelectItem>
                                ))}
                            </SelectContent>
                        </Select>
                    </div>
                </div>

                {hasActiveFilters && (
                    <div className="mt-6 text-right">
                        <Button variant="outline" size="sm" onClick={clearFilters}>
                            <X className="mr-2 h-4 w-4"/>
                            Clear All
                        </Button>
                    </div>
                )}
            </Card>
        );
    }

    /* ---------------- Sidebar Layout ---------------- */
    return (
        <aside className="p-6 bg-muted/50 rounded-2xl space-y-6 sticky top-24">
            <h3 className="text-xl font-semibold">Filter Events</h3>

            {/* Search */}
            <div className="space-y-2">
                <Label htmlFor="searchTerm">Search by name</Label>
                <div className="relative">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-primary"/>
                    <Input
                        id="searchTerm"
                        placeholder="Event title or keyword..."
                        name="searchTerm"
                        defaultValue={searchParams.get('searchTerm') || ''}
                        onChange={handleInputChange}
                        className="pl-9"
                    />
                </div>
            </div>

            <Separator/>

            {/* Category */}
            <div className="space-y-2">
                <Label>Category</Label>
                <Select
                    value={searchParams.get('categoryId') || 'all'}
                    onValueChange={handleSelectChange('categoryId')}
                >
                    <SelectTrigger className="w-full">
                        <Tag className="mr-2 h-4 w-4 text-primary"/>
                        <SelectValue placeholder="All Categories"/>
                    </SelectTrigger>
                    <SelectContent>
                        <SelectItem value="all">All Categories</SelectItem>
                        {categories.map(cat => (
                            <SelectItem key={cat.id} value={cat.id}>{cat.name}</SelectItem>
                        ))}
                    </SelectContent>
                </Select>
            </div>

            <Separator/>

            {/* Location */}
            <div className="space-y-2">
                <Label>Location</Label>
                <Select
                    value={searchParams.get('location') || 'all'}
                    onValueChange={(value) => {
                        const params = new URLSearchParams(searchParams.toString());
                        if (value === 'all') {
                            params.delete('location');
                            params.delete('latitude');
                            params.delete('longitude');
                            params.delete('radiusKm');
                        } else {
                            const location = sriLankaLocations.find(l => l.name === value);
                            if (location) {
                                params.set('location', location.name);
                                params.set('latitude', location.latitude.toString());
                                params.set('longitude', location.longitude.toString());
                                params.set('radiusKm', '50');
                            }
                        }
                        params.delete('page');
                        router.push(pathname + '?' + params.toString());
                    }}
                >
                    <SelectTrigger className="w-full">
                        <MapPin className="mr-2 h-4 w-4 text-primary"/>
                        <SelectValue placeholder="Anywhere in Sri Lanka"/>
                    </SelectTrigger>
                    <SelectContent>
                        <SelectItem value="all">Anywhere in Sri Lanka</SelectItem>
                        {sriLankaLocations.map(loc => (
                            <SelectItem key={loc.name} value={loc.name}>{loc.name}</SelectItem>
                        ))}
                    </SelectContent>
                </Select>
            </div>

            {hasActiveFilters && (
                <div className="pt-2">
                    <Button variant="ghost" className="w-full" onClick={clearFilters}>
                        <X className="mr-2 h-4 w-4"/>
                        Clear All Filters
                    </Button>
                </div>
            )}
        </aside>
    );
}

'use client';

import {useRouter, usePathname, useSearchParams} from 'next/navigation';
import {useCallback} from 'react';
import {Input} from "@/components/ui/input";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Button} from "@/components/ui/button";
import {Label} from "@/components/ui/label";
import {Card} from "@/components/ui/card";
import {Separator} from "@/components/ui/separator";
import {CategoryResponseWithParentName} from '@/types/category';
import {sriLankaLocations} from '../../_utils/locations';
import {Search, MapPin, Tag, X} from 'lucide-react';

interface EventFiltersProps {
    categories: CategoryResponseWithParentName[];
    inHero?: boolean;
}

export function EventFilters({categories, inHero = false}: EventFiltersProps) {
    const router = useRouter();
    const pathname = usePathname();
    const searchParams = useSearchParams();

    const createQueryString = useCallback(
        (name: string, value: string) => {
            const params = new URLSearchParams(searchParams.toString());
            if (value) params.set(name, value);
            else params.delete(name);

            params.delete('page'); // reset pagination
            return params.toString();
        },
        [searchParams]
    );

    const handleSelectChange = (name: string) => (value: string) => {
        const finalValue = value === 'all' ? '' : value;
        router.push(pathname + '?' + createQueryString(name, finalValue));
    };

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        router.push(pathname + '?' + createQueryString(event.target.name, event.target.value));
    };

    const clearFilters = () => {
        router.push(pathname);
    };

    const hasActiveFilters = searchParams.size > 0;

    /* ---------------- Hero Layout ---------------- */
    if (inHero) {
        return (
            <Card className="bg-card/70 dark:bg-card/80 backdrop-blur-md border shadow-xl rounded-2xl p-6">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {/* Search */}
                    <div className="space-y-2">
                        <Label htmlFor="searchTerm">Search by name</Label>
                        <div className="relative">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-primary"/>
                            <Input
                                id="searchTerm"
                                placeholder="Event title or keyword..."
                                name="searchTerm"
                                defaultValue={searchParams.get('searchTerm') || ''}
                                onChange={handleInputChange}
                                className="pl-9"
                            />
                        </div>
                    </div>

                    {/* Category */}
                    <div className="space-y-2">
                        <Label>Category</Label>
                        <Select
                            value={searchParams.get('categoryId') || 'all'}
                            onValueChange={handleSelectChange('categoryId')}
                        >
                            <SelectTrigger className="w-full">
                                <Tag className="mr-2 h-4 w-4 text-primary"/>
                                <SelectValue placeholder="All Categories"/>
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="all">All Categories</SelectItem>
                                {categories.map(cat => (
                                    <SelectItem key={cat.id} value={cat.id}>{cat.name}</SelectItem>
                                ))}
                            </SelectContent>
                        </Select>
                    </div>

                    {/* Location with Clear All button */}
                    <div className="space-y-2">
                        <div className="flex justify-between items-center">
                            <Label>Location</Label>
                            {hasActiveFilters && (
                                <Button 
                                    variant="ghost" 
                                    size="sm" 
                                    onClick={clearFilters} 
                                    className="h-6 px-2 text-xs font-normal text-muted-foreground"
                                >
                                    <X className="mr-1 h-3 w-3"/>
                                    Clear All
                                </Button>
                            )}
                        </div>
                        <Select
                            value={searchParams.get('location') || 'all'}
                            onValueChange={(value) => {
                                const params = new URLSearchParams(searchParams.toString());
                                if (value === 'all') {
                                    params.delete('location');
                                    params.delete('latitude');
                                    params.delete('longitude');
                                    params.delete('radiusKm');
                                } else {
                                    const location = sriLankaLocations.find(l => l.name === value);
                                    if (location) {
                                        params.set('location', location.name);
                                        params.set('latitude', location.latitude.toString());
                                        params.set('longitude', location.longitude.toString());
                                        params.set('radiusKm', '50');
                                    }
                                }
                                params.delete('page');
                                router.push(pathname + '?' + params.toString());
                            }}
                        >
                            <SelectTrigger className="w-full">
                                <MapPin className="mr-2 h-4 w-4 text-primary"/>
                                <SelectValue placeholder="Anywhere in Sri Lanka"/>
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="all">Anywhere in Sri Lanka</SelectItem>
                                {sriLankaLocations.map(loc => (
                                    <SelectItem key={loc.name} value={loc.name}>{loc.name}</SelectItem>
                                ))}
                            </SelectContent>
                        </Select>
                    </div>
                </div>
            </Card>
        );
    }

    /* ---------------- Sidebar Layout ---------------- */
    return (
        <aside className="p-6 bg-muted/50 rounded-2xl space-y-6 sticky top-24">
            <h3 className="text-xl font-semibold">Filter Events</h3>

            {/* Search */}
            <div className="space-y-2">
                <Label htmlFor="searchTerm">Search by name</Label>
                <div className="relative">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-primary"/>
                    <Input
                        id="searchTerm"
                        placeholder="Event title or keyword..."
                        name="searchTerm"
                        defaultValue={searchParams.get('searchTerm') || ''}
                        onChange={handleInputChange}
                        className="pl-9"
                    />
                </div>
            </div>

            <Separator/>

            {/* Category */}
            <div className="space-y-2">
                <Label>Category</Label>
                <Select
                    value={searchParams.get('categoryId') || 'all'}
                    onValueChange={handleSelectChange('categoryId')}
                >
                    <SelectTrigger className="w-full">
                        <Tag className="mr-2 h-4 w-4 text-primary"/>
                        <SelectValue placeholder="All Categories"/>
                    </SelectTrigger>
                    <SelectContent>
                        <SelectItem value="all">All Categories</SelectItem>
                        {categories.map(cat => (
                            <SelectItem key={cat.id} value={cat.id}>{cat.name}</SelectItem>
                        ))}
                    </SelectContent>
                </Select>
            </div>

            <Separator/>

            {/* Location */}
            <div className="space-y-2">
                <Label>Location</Label>
                <Select
                    value={searchParams.get('location') || 'all'}
                    onValueChange={(value) => {
                        const params = new URLSearchParams(searchParams.toString());
                        if (value === 'all') {
                            params.delete('location');
                            params.delete('latitude');
                            params.delete('longitude');
                            params.delete('radiusKm');
                        } else {
                            const location = sriLankaLocations.find(l => l.name === value);
                            if (location) {
                                params.set('location', location.name);
                                params.set('latitude', location.latitude.toString());
                                params.set('longitude', location.longitude.toString());
                                params.set('radiusKm', '50');
                            }
                        }
                        params.delete('page');
                        router.push(pathname + '?' + params.toString());
                    }}
                >
                    <SelectTrigger className="w-full">
                        <MapPin className="mr-2 h-4 w-4 text-primary"/>
                        <SelectValue placeholder="Anywhere in Sri Lanka"/>
                    </SelectTrigger>
                    <SelectContent>
                        <SelectItem value="all">Anywhere in Sri Lanka</SelectItem>
                        {sriLankaLocations.map(loc => (
                            <SelectItem key={loc.name} value={loc.name}>{loc.name}</SelectItem>
                        ))}
                    </SelectContent>
                </Select>
            </div>

            {hasActiveFilters && (
                <div className="pt-2">
                    <Button variant="ghost" className="w-full" onClick={clearFilters}>
                        <X className="mr-2 h-4 w-4"/>
                        Clear All Filters
                    </Button>
                </div>
            )}
        </aside>
    );
}

