    return (
        <Dialog open={open} onOpenChange={onOpenChange}>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>Add Staff Member</DialogTitle>
                    <DialogDescription>
                        Invite a new staff member to your organization.
                    </DialogDescription>
                </DialogHeader>

                <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4 pt-2">
                        <FormField
                            control={form.control}
                            name="email"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Email</FormLabel>
                                    <FormControl>
                                        <Input placeholder="email@example.com" {...field} />
                                    </FormControl>
                                    <FormDescription>
                                        We&#39;ll send an invitation to this email address.
                                    </FormDescription>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="role"
                            render={({field}) => (
                                <FormItem>
                                    <FormLabel>Role</FormLabel>
                                    <Select
                                        onValueChange={field.onChange}
                                        defaultValue={field.value}
                                    >
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select a role"/>
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                            <SelectItem value={OrganizationRole.SCANNER}>
                                                Scanner
                                            </SelectItem>
                                            {/* Additional roles can be added here in the future */}
                                        </SelectContent>
                                    </Select>
                                    <FormDescription>
                                        The permission level for this staff member.
                                    </FormDescription>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />

                        <DialogFooter className="pt-4">
                            <Button type="button" variant="outline" onClick={() => onOpenChange(false)}>
                                Cancel
                            </Button>
                            <Button type="submit" disabled={isSubmitting}>
                                {isSubmitting ? 'Inviting...' : 'Invite Staff'}
                            </Button>
                        </DialogFooter>
                    </form>
                </Form>
            </DialogContent>
        </Dialog>
    );
};

"use client";

import React, {useState, useEffect} from 'react';
import {useParams} from 'next/navigation';
import {getOrganizationMembers, removeOrganizationMember} from '@/lib/actions/organizationActions';
import {OrganizationMemberResponse} from '@/types/oraganizations';
import {StaffTable} from './_components/StaffTable';
import {AddStaffDialog} from './_components/AddStaffDialog';
import {Button} from '@/components/ui/button';
import {PlusIcon} from 'lucide-react';

const StaffManagementPage = () => {
    const {organization_id} = useParams() as { organization_id: string };
    const [staffMembers, setStaffMembers] = useState<OrganizationMemberResponse[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [dialogOpen, setDialogOpen] = useState(false);

    useEffect(() => {
        const fetchStaffMembers = async () => {
            try {
                setIsLoading(true);
                const members = await getOrganizationMembers(organization_id);
                console.log('Fetched members:', members);
                setStaffMembers(members);
            } catch (error) {
                console.error('Failed to fetch staff members:', error);
            } finally {
                setIsLoading(false);
            }
        };

        if (organization_id) {
            fetchStaffMembers().then()
        }
    }, [organization_id]);

    const handleAddStaff = (newMember: OrganizationMemberResponse) => {
        setStaffMembers((prev) => [...prev, newMember]);
        setDialogOpen(false);
    };

    const handleRemoveStaff = async (userId: string) => {
        try {
            await removeOrganizationMember(organization_id, userId);
            setStaffMembers((prev) => prev.filter((member) => member.userId !== userId));
        } catch (error) {
            console.error('Failed to remove staff member:', error);
        }
    };

    return (
        <div className="p-4 md:p-8">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold tracking-tight">Staff Management</h1>
                <Button onClick={() => setDialogOpen(true)}>
                    <PlusIcon className="mr-2 h-4 w-4"/>
                    Add Staff
                </Button>
            </div>

            <StaffTable
                staffMembers={staffMembers}
                isLoading={isLoading}
                onRemove={handleRemoveStaff}
            />

            <AddStaffDialog
                open={dialogOpen}
                onOpenChange={setDialogOpen}
                organizationId={organization_id}
                onSuccess={handleAddStaff}
            />
        </div>
    );
};

export default StaffManagementPage;

"use client";

import React, {useState, useEffect} from 'react';
import {useParams} from 'next/navigation';
import {getOrganizationMembers, removeOrganizationMember} from '@/lib/actions/organizationActions';
import {OrganizationMemberResponse} from '@/types/oraganizations';
import {StaffTable} from './_components/StaffTable';
import {AddStaffDialog} from './_components/AddStaffDialog';
import {Button} from '@/components/ui/button';
import {PlusIcon} from 'lucide-react';
import { toast } from 'sonner';

const StaffManagementPage = () => {
    const {organization_id} = useParams() as { organization_id: string };
    const [staffMembers, setStaffMembers] = useState<OrganizationMemberResponse[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [dialogOpen, setDialogOpen] = useState(false);

    useEffect(() => {
        const fetchStaffMembers = async () => {
            try {
                setIsLoading(true);
                const members = await getOrganizationMembers(organization_id);
                console.log('Fetched members:', members);
                setStaffMembers(members);
            } catch (error) {
                console.error('Failed to fetch staff members:', error);
            } finally {
                setIsLoading(false);
            }
        };

        if (organization_id) {
            fetchStaffMembers().then()
        }
    }, [organization_id]);

    const handleAddStaff = (newMember: OrganizationMemberResponse) => {
        setStaffMembers((prev) => [...prev, newMember]);
        setDialogOpen(false);
    };

    const handleRemoveStaff = async (userId: string) => {
        // Show loading toast while deleting
        const loadingToast = toast.loading('Removing staff member...');
        try {
            await removeOrganizationMember(organization_id, userId);
            setStaffMembers((prev) => prev.filter((member) => member.userId !== userId));

            // Dismiss loading toast and show success
            toast.dismiss(loadingToast);
            toast.success('Staff member removed successfully');
        } catch (error) {
            // Dismiss loading toast and show error
            toast.dismiss(loadingToast);
            toast.error('Failed to remove staff member');
            console.error('Failed to remove staff member:', error);
        }
    };

    return (
        <div className="p-4 md:p-8">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold tracking-tight">Staff Management</h1>
                <Button onClick={() => setDialogOpen(true)}>
                    <PlusIcon className="mr-2 h-4 w-4"/>
                    Add Staff
                </Button>
            </div>

            <StaffTable
                staffMembers={staffMembers}
                isLoading={isLoading}
                onRemove={handleRemoveStaff}
            />

            <AddStaffDialog
                open={dialogOpen}
                onOpenChange={setDialogOpen}
                organizationId={organization_id}
                onSuccess={handleAddStaff}
            />
        </div>
    );
};

export default StaffManagementPage;

import { useEffect, useState } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { Loader2 } from 'lucide-react';

export default function OrderPaymentRedirect() {
  const params = useParams();
  const router = useRouter();
  const orderId = params.orderId as string;
  const [countdown, setCountdown] = useState(5);

  useEffect(() => {
    // Simulate redirect to payment gateway
    const timer = setTimeout(() => {
      // In a real implementation, this would redirect to the actual payment gateway
      // For now, we'll just simulate with a countdown
      window.location.href = `/payment-success?orderId=${orderId}`;
    }, 5000);

    // Countdown effect
    const interval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
        }
        return prev - 1;
      });
    }, 1000);

    return () => {
      clearTimeout(timer);
      clearInterval(interval);
    };
  }, [orderId]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="text-center max-w-md mx-auto">
        <div className="mb-6">
          <Loader2 className="h-12 w-12 animate-spin mx-auto text-primary" />
        </div>
        <h1 className="text-2xl font-bold mb-2">Redirecting to Payment Gateway</h1>
        <p className="text-muted-foreground mb-4">
          Please wait, you will be redirected to the payment gateway in {countdown} seconds...
        </p>
        <p className="text-sm text-muted-foreground">
          Order ID: <span className="font-mono">{orderId}</span>
        </p>
      </div>
    </div>
  );
}

'use client';

import React, {createContext, useContext, useState, useEffect, ReactNode, useMemo} from 'react';
import {useAuth} from '@/providers/AuthProvider';
import {getAppConfiguration} from '@/lib/actions/configActions';
import {AppConfig, TierLimitDetails, TierName} from '@/types/config';

// Define the shape of the context
interface LimitContextType {
    config: AppConfig | null;
    isLoading: boolean;
    error: string | null;
    currentUserTier: TierName;
    currentUserTierLimits: TierLimitDetails | null;
    getLimitsForTier: (tier: TierName) => TierLimitDetails | null;
}

const LimitContext = createContext<LimitContextType | undefined>(undefined);

export const LimitProvider = ({children}: { children: ReactNode }) => {
    const {keycloak} = useAuth();
    const [config, setConfig] = useState<AppConfig | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Fetch the entire configuration on initial application load
    useEffect(() => {
        const fetchConfig = async () => {
            try {
                const appConfig = await getAppConfiguration();
                setConfig(appConfig);
            } catch (err) {
                setError('Failed to load application configuration. Some features may be unavailable.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };
        fetchConfig();
    }, []);

    // A memoized helper to get the current user's highest tier from their JWT
    const currentUserTier = useMemo((): TierName => {
        if (!keycloak?.tokenParsed?.user_groups || !Array.isArray(keycloak.tokenParsed.user_groups)) return 'FREE';

        const userGroups: string[] = keycloak.tokenParsed.user_groups;
        let highestTier: TierName = 'FREE';
        let maxLevel = 0;

        const tierLevels: Record<TierName, number> = {FREE: 0, PRO: 1, ENTERPRISE: 2};

        userGroups.forEach(group => {
            if (group.startsWith('/Tiers/')) {
                const tierName = group.substring('/Tiers/'.length).toUpperCase() as TierName;
                if (tierLevels[tierName] !== undefined && tierLevels[tierName] > maxLevel) {
                    maxLevel = tierLevels[tierName];
                    highestTier = tierName;
                }
            }
        });
        return highestTier;
    }, [keycloak]);

    // A memoized value for the current user's specific limits, derived from the full config
    const currentUserTierLimits = useMemo(() => {
        if (!config) return null;
        return config.tierLimits[currentUserTier] || config.tierLimits.FREE;
    }, [config, currentUserTier]);

    // A function to get limits for any specified tier (useful for upgrade pages)
    const getLimitsForTier = (tier: TierName): TierLimitDetails | null => {
        if (!config) return null;
        return config.tierLimits[tier] || null;
    };

    const value = {config, isLoading, error, currentUserTier, currentUserTierLimits, getLimitsForTier};

    return (
        <LimitContext.Provider value={value}>
            {children}
        </LimitContext.Provider>
    );
};

// The custom hook to easily access the limits from any component
export const useLimits = (): LimitContextType => {
    const context = useContext(LimitContext);
    if (context === undefined) {
        throw new Error('useLimits must be used within a LimitProvider');
    }
    return context;
};

'use client';

import React, {createContext, useContext, useState, useEffect, ReactNode, useMemo} from 'react';
import {useAuth} from '@/providers/AuthProvider';
import {getAppConfiguration} from '@/lib/actions/configActions';
import {AppConfig, TierLimitDetails, TierName} from '@/types/config';

// Define the shape of the context
interface LimitContextType {
    config: AppConfig | null;
    isLoading: boolean;
    error: string | null;
    currentUserTier: TierName;
    currentUserTierLimits: TierLimitDetails | null;
    getLimitsForTier: (tier: TierName) => TierLimitDetails | null;
}

const LimitContext = createContext<LimitContextType | undefined>(undefined);

export const LimitProvider = ({children}: { children: ReactNode }) => {
    const {keycloak} = useAuth();
    const [config, setConfig] = useState<AppConfig | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Fetch the entire configuration on initial application load
